//生成业务数据
function wf_ndywsj(db,thisaorgid,thisaccid,ls_dwbh,ll_xh,ls_ym)
{
	var sql = &quot;SELECT C.DJH,
			 C.DWBH,
			 C.DWMC,
			 C.ZY,
			 C.PCH,
			 C.YY,
			 C.MM,
			 sum(c.je) ywje,
			 MAX(DECODE(C.LXBH, &apos;32&apos;, C.JE, 0)) TCJJ,
			 MAX(DECODE(C.LXBH, &apos;35&apos;, C.JE, 0)) GRZH,
			 MAX(DECODE(C.LXBH, &apos;3A&apos;, C.JE, 0)) GWY,
			 MAX(DECODE(C.LXBH, &apos;3B&apos;, C.JE, 0)) ZGWY
		FROM (SELECT A.DJH DJH,
				A.DWBH DWBH,
				A.DWMC DWMC,
				A.ZY ZY,
				A.PCH PCH,
				A.SCNY SCNY,
				SUBSTRB(A.SCNY, 1, 4) YY,
				SUBSTRB(A.SCNY, 5) MM,
				A.LXBH LXBH,
				SUM(NVL(A.JE1, 0) + NVL(A.JE2, 0) + NVL(A.JE3, 0) +
					 NVL(A.JE4, 0) + NVL(A.JE5, 0) + NVL(A.JE6, 0) +
					 NVL(A.JE7, 0) + NVL(A.JE8, 0) + NVL(A.JE9, 0) +
					 NVL(A.JE10, 0) + NVL(A.JE11, 0) + NVL(A.JE12, 0) +
					 NVL(A.JE13, 0) + NVL(A.JE14, 0) + NVL(A.JE15, 0)) JE
			FROM v_SI_DJB_TMP A, AD61 B
			WHERE A.DJH = B.BAD307
			  AND A.SCNY = B.AAE002
			  AND A.DWBH = B.AAZ010
			  AND A.ORG = &apos;[%ORGID]&apos;
			  AND A.ACC = &apos;[%ACCID]&apos;
			  AND B.AAE140 = &apos;31&apos;
			  AND B.BAD305 = &apos;3102&apos;
			  and a.dwbh = &apos;[%DWBH]&apos;
			  and b.aae002 = &apos;[%YM]&apos;
			GROUP BY A.DJH,
				A.DWBH,
				A.DWMC,
				A.ZY,
				A.PCH,
				A.SCNY,
				A.LXBH,
				SUBSTRB(A.SCNY, 1, 4),
				SUBSTRB(A.SCNY, 5)) C
		GROUP BY C.DJH, C.DWBH, C.DWMC, C.ZY, C.PCH, C.YY, C.MM
 			ORDER BY C.DWBH, C.YY, C.MM&quot;;

 	sql = pub.EAFunc.Replace(sql,&quot;[%ORGID]&quot;,thisorgid);
 	sql = pub.EAFunc.Replace(sql,&quot;[%ACCID]&quot;,thisaccid);
 	sql = pub.EAFunc.Replace(sql,&quot;[%DWBH]&quot;,ls_dwbh);
 	sql = pub.EAFunc.Replace(sql,&quot;[%YM]&quot;,ls_ym);

 	var ds = db.QuerySQL(sql);
 	//20170301 LYH MDIFY 
 	if(ds.getRowCount() &lt;= 0){
 		db.Rollback();
 		return -1;//&quot;查询业务数据出错，查询无此数据，单位编号&quot;+ls_dwbh;
 	}

 	var ret = 0;
 	for (var i=0;i&lt;ds.getRowCount();i++) {
 		var ls_djh  = ds.getStringAt(i,&quot;DJH&quot;);
 		var li_yy  = ds.getStringAt(i,&quot;YY&quot;);
 		var li_mm  = ds.getStringAt(i,&quot;MM&quot;);
 		var ldb_tcjj  = pub.EAFunc.Round( ds.getStringAt(i,&quot;TCJJ&quot;) ,2);
 		var ldb_grzh  = pub.EAFunc.Round( ds.getStringAt(i,&quot;GRZH&quot;) ,2);
 		var ldb_gwy   = pub.EAFunc.Round( ds.getStringAt(i,&quot;GWY&quot;)  ,2);
 		var ldb_zgwy  = pub.EAFunc.Round( ds.getStringAt(i,&quot;ZGWY&quot;) ,2);
 		var ldb_ywje  = pub.EAFunc.Round( ds.getStringAt(i,&quot;YWJE&quot;) ,2);
 		//更新每月业务数据
 		sql = &quot;UPDATE cw_zekzmxb SET ywje_tcjj=&quot;+ldb_tcjj+&quot;,
 					ywje_grzh=&quot;+ldb_grzh+&quot;,
 					ywje_gwy = &quot;+ldb_gwy+&quot;,
 					ywje_zgwy=&quot;+ldb_zgwy+&quot;,
 					ywje= &quot;+ldb_ywje+&quot;,
 					djh = &apos;&quot;+ls_djh+&quot;&apos;
			WHERE org = &apos;&quot;+thisorgid+&quot;&apos; AND acc = &apos;&quot;+thisaccid+&quot;&apos; AND xh = &quot;+ll_xh+&quot; AND yy = &quot;+li_yy+&quot; AND to_number(mm) = to_number(&apos;&quot;+li_mm+&quot;&apos;) and BFBZ = &apos;0&apos;&quot;;
		db.ExcecutSQL(sql);

		sql = &quot;update cw_zekzb a set ywzje = (select sum(nvl(ywje,0)) from cw_zekzmxb b where a.org=b.org and a.acc=b.acc and a.sbh=b.sbh and a.zth=b.zth and a.xh=b.xh and b.BFBZ = &apos;0&apos;) 
			where org = &apos;&quot;+thisorgid+&quot;&apos; AND acc = &apos;&quot;+thisaccid+&quot;&apos; AND xh = &quot;+ll_xh;
		ret += db.ExcecutSQL(sql);		
 	}
 	return ret;
}

//获取(准)公务员数据
function getZgwyData()
{
	var db = null;
	var sql = &quot;&quot;;
	try {
		db = new pub.EADatabase();

		var ls_dwbh = &quot;&quot;;
		var ls_ym = &quot;&quot;;
		var ldb_zje = 0;
//		if (getType == 1) {
//			ls_dwbh = dwbh;
//			ls_ym = yyyy+mm;
//		}

		ls_ym = yyyy+&quot;&quot;+mm;

		var ds = new pub.EAXmlDS(kzbXml);
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var sbh = ds.getStringAt(i,&quot;SBH&quot;);
			var zth = ds.getStringAt(i,&quot;ZTH&quot;);
			var yy = ds.getStringAt(i,&quot;YY&quot;);
			var lsh = ds.getStringAt(i,&quot;XH&quot;);
			var dwbh = ds.getStringAt(i,&quot;DWBH&quot;);
			var dwmc = ds.getStringAt(i,&quot;DWMC&quot;);

			var ret = wf_gwysj(db,thisorgid,thisaccid,dwbh,lsh,yy,zth);
			if (ret &lt;= 0 ) {
				db.Rollback();
				return &quot;生成业务数据失败！没有生成数据！&quot;+ret;
			}
		}

		db.Commit();

		return &quot;生成业务数据成功!&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}
}

//更新(准)公务员每月指标数 
function wf_gwysj(db,thisaorgid,thisaccid,ai_dwbh,ai_xh,ai_yy,ai_zth)
{

//	var ls_ym1 = string(ai_yy) + &quot;01&quot;;
//	var ls_ym2 = string(ai_yy) + &quot;11&quot;;
//	20170301 lyh modify 未生成拨款单及日记账的数据，获取去年11个月公务员平均数据
	var yy = ai_yy*1 - 1;
	var ls_ym1 = yy +&quot;&quot;+&quot;01&quot;;
	var ls_ym2 = yy +&quot;&quot;+&quot;11&quot;;

	var sql = &quot;SELECT B.DWBH DWBH,
		       max(B.DWMC) DWMC,
		       COUNT(DISTINCT B.DJH) YS,
		       SUM(DECODE(B.LXBH, &apos;3A&apos;, B.JE, 0)) GWY,
		       SUM(DECODE(B.LXBH, &apos;3B&apos;, B.JE, 0)) ZGWY
		  FROM (SELECT A.DJH DJH,
		               A.DWBH DWBH,
		               A.DWMC DWMC,
		               A.LXBH LXBH,
		               SUM(NVL(A.JE1, 0) + NVL(A.JE2, 0) + NVL(A.JE3, 0) +
		                   NVL(A.JE4, 0) + NVL(A.JE5, 0) + NVL(A.JE6, 0) +
		                   NVL(A.JE7, 0) + NVL(A.JE8, 0) + NVL(A.JE9, 0) +
		                   NVL(A.JE10, 0) + NVL(A.JE11, 0) + NVL(A.JE12, 0) +
		                   NVL(A.JE13, 0) + NVL(A.JE14, 0) + NVL(A.JE15, 0)) JE
		          FROM SI_DJB_TMP A
		         WHERE A.DWBH = &apos;&quot;+ai_dwbh+&quot;&apos;
		           AND A.YM1 &gt;= &quot;+ls_ym1+&quot;
		           AND A.YM1 &lt;= &quot;+ls_ym2+&quot;
		           AND A.LXBH IN (&apos;3A&apos;, &apos;3B&apos;)
		           and a.ywlx = &apos;D130&apos;
		           and a.sbh = &apos;&quot;+thisorgid+&quot;&apos;
		           and a.zth = &apos;&quot;+ai_zth+&quot;&apos;
		         GROUP BY A.DJH, A.DWBH, A.DWMC, A.LXBH) B
		 GROUP BY B.DWBH
		 ORDER BY B.DWBH&quot;;
	var ls_dwbh = &quot;&quot;;
	var ls_dwmc = &quot;&quot;;
	var ll_ys = &quot;&quot;;
	var ldb_gwy_je = 0.00;
	var ldb_zgwy_je = 0.00;
	var tmpds = db.QuerySQL(sql);
	if(tmpds.getRowCount() &lt;= 0){
		db.Rollback();
		return -2;//&quot;查询无此数据&quot;+sql;
	}

	if (tmpds.getRowCount() &gt; 0) {
		ls_dwbh = tmpds.getStringAt(0,&quot;DWBH&quot;);
		ls_dwmc = tmpds.getStringAt(0,&quot;DWMC&quot;);
		ll_ys   = 1 * tmpds.getStringAt(0,&quot;YS&quot;);
		ldb_gwy_je  = tmpds.getStringAt(0,&quot;GWY&quot;);
		ldb_zgwy_je = tmpds.getStringAt(0,&quot;ZGWY&quot;);
	}	 
//	20170301 lyh 
//	if (ai_yy != 2012 && ll_ys != 11) {
	if(ll_ys != 11){
		db.Rollback();
		return -1;//&quot;查询上年度[&quot;+ls_dwmc+&quot;]该单位业务数据不为11个月!&quot;;
	}

	var ldb_je1 = pub.EAFunc.Round(ldb_gwy_je / ll_ys ,2);
	var ldb_je2 = pub.EAFunc.Round(ldb_zgwy_je / ll_ys ,2);

	var ret = 0;

//	20170301 lyh modify 无2013年的数据了
//	if (ai_yy == 2013) { //暂时允许重复取，因为之前未取过数据
//		sql = &quot;update cw_zekzmxb set gwy_je = &quot;+ldb_je1+&quot;,zgwy_je = &quot;+ldb_je2+&quot; where org = &apos;&quot;+thisorgid+&quot;&apos; and acc = &apos;&quot;+thisaccid+&quot;&apos; and xh = &quot;+al_xh+&quot; and BFBZ = &apos;0&apos;&quot;;
//		ret += db.ExcecutSQL(sql);
//	}
//	else {
		sql = &quot;update cw_zekzmxb set gwy_je = &quot;+ldb_je1+&quot;,zgwy_je = &quot;+ldb_je2+&quot; where org = &apos;&quot;+thisorgid+&quot;&apos; and acc = &apos;&quot;+thisaccid+&quot;&apos; and xh = &quot;+al_xh+&quot; 
			and BFBZ = &apos;0&apos; and nvl(bkbz,&apos;0&apos;) = &apos;0&apos; and bkdid is null and rjzlsh is null&quot;;
		ret += db.ExcecutSQL(sql);
//	}

	return ret;
}


//生成医疗数据
function GenYLData()
{
	var db = null;
	var sql = &quot;&quot;;
	try {
		db = new pub.EADatabase();

		var yy = yymm.split(&quot;-&quot;)[0];
		var zth = thisaccid.substring(thisaccid.indexOf(thisorgid)+thisorgid.length());

//		20170214 lyh modify 不在查询AEF3 改为查询cw_bkdyhb
//		var ls_xtbh = &quot;00119&quot;;
//		var ls_xzlx = &quot;31&quot;;		
//		if (zth == &quot;19&quot;) {
//			ls_xtbh = &quot;00119&quot;;
//			ls_xzlx = &quot;31&quot;;
//		}
//		else {
//			ls_xtbh = &quot;00118&quot;;
//			ls_xzlx = &quot;11&quot;;
//		}
//		
//		sql = &quot;SELECT A.AAB001 dwbh, A.AAB025 dwmc
//			 FROM AEF3 A
//			 WHERE  A.ORG = &apos;&quot;+thisorgid+&quot;&apos; and A.ACC = &apos;&quot;+thisaccid+&quot;&apos; 
//			 	AND A.BAE007 = &apos;&quot;+ls_xtbh+&quot;&apos; 
//				AND A.AAE140 = &apos;&quot;+ls_xzlx+&quot;&apos; 
//				AND A.BAE541 = 0
//				AND A.BAE511 = 0
//				AND A.AAE338 = &apos;-1&apos;
//				AND A.BAE201 = &apos;-1&apos;
//				AND A.BAE202 = &apos;-1&apos;
//				AND A.AAE002 = &apos;2&apos;
//				AND NOT EXISTS (SELECT 1
//						 FROM CW_ZEKZB B
//						WHERE B.ORG = &apos;&quot;+thisorgid+&quot;&apos; 
//						  AND B.ACC = &apos;&quot;+thisaccid+&quot;&apos; 
//						  AND B.YY = &quot;+yy+&quot; 
//						  AND A.AAB001 = B.DWBH)
//			 ORDER BY AAB001&quot;;

		sql  = &quot;SELECT A.JGBH DWBH, JGMC DWMC
			  FROM CW_BKDYHB A
			 WHERE A.SBH = &apos;&quot;+thisorgid+&quot;&apos;
			   AND A.ZTH = &apos;&quot;+zth+&quot;&apos;
			   AND A.BZ = &apos;21&apos;
			   AND A.XZLX = &apos;31&apos;
			   AND NOT EXISTS (SELECT 1
			          FROM CW_ZEKZB B
			         WHERE B.SBH = &apos;&quot;+thisorgid+&quot;&apos;
			           AND B.ZTH = &apos;&quot;+zth+&quot;&apos;
			           AND B.YY = &quot;+yy+&quot;
			           AND A.SBH = B.SBH
			           AND A.ZTH = B.ZTH
			           AND A.JGBH = B.DWBH)
			   order by a.jgbbh&quot;;
		var ds = db.QuerySQL(sql);
		if (ds.getRowCount() == 0) {
			return &quot;查询CW_BKDYHB没有单位信息，或 &quot; + yy + &quot; 年的数据已经生成过，不允许重新生成!&quot;;
		}
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var llxh = db.GetSQL(&quot;SELECT SEQ_CW_ZEKZB_XH.NEXTVAL FROM DUAL&quot;);
			var dwbh = ds.getStringAt(i,&quot;DWBH&quot;);
			var dwmc = ds.getStringAt(i,&quot;DWMC&quot;);
			//
			sql = &quot;INSERT INTO CW_ZEKZB(ORG,ACC,SBH,ZTH,XH,YY,DWBH,DWMC,NZQSBZ) VALUES (&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,0)&quot;
				.format([thisorgid,thisaccid,thisorgid,zth,llxh,yy,dwbh,dwmc]);
			db.ExcecutSQL(sql);
		}

		db.Commit();

		return &quot;生成医疗数据成功!&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}
}

//删除医疗数据
function DelYLData()
{
	var db = null;
	var sql = &quot;&quot;;
	try {
		db = new pub.EADatabase();
		var ret = 0;
		var kzbds = new pub.EAXmlDS(kzbXml);		
		for (var i=0;i&lt;kzbds.getRowCount();i++) {
			var sbh = kzbds.getStringAt(i,&quot;SBH&quot;);
			var zth = kzbds.getStringAt(i,&quot;ZTH&quot;);
			var dwbh = kzbds.getStringAt(i,&quot;DWBH&quot;);
			var dwmc = kzbds.getStringAt(i,&quot;DWMC&quot;);
			var yy = kzbds.getStringAt(i,&quot;YY&quot;);
			var lsh = kzbds.getStringAt(i,&quot;XH&quot;);
			var zje = 1.0 * nvl(kzbds.getStringAt(i,&quot;ZJE&quot;),&quot;0&quot;);
       			var gz_zje = 1.0 * nvl(kzbds.getStringAt(i,&quot;GZ_ZJE&quot;),&quot;0&quot;);
			var gwy_zje = 1.0 * nvl(kzbds.getStringAt(i,&quot;GWY_ZJE&quot;),&quot;0&quot;);
			var zgwy_zje = 1.0 * nvl(kzbds.getStringAt(i,&quot;ZGWY_ZJE&quot;),&quot;0&quot;);	

			//20170214 lyh modify 将&&修改为||
			if (zje != 0 || gz_zje != 0) {
				return &quot;年初指标已经分配，不允许进行删除操作!&quot;;
			}	

			var cnt = db.GetSQL(&quot;select count(1) cnt from cw_zekzmxb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and xh=&quot;+lsh+&quot; and bfbz &lt;&gt; &apos;0&apos;&quot;);
			if (cnt &gt; 0) {
				return &quot;单位[&quot;+dwmc+&quot;]存在其他指标，不允许操作!&quot;;
			}

			if (deleType == 1) {
				sql = &quot;delete from cw_zekzmxb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and xh=&quot;+lsh+&quot; and bfbz = &apos;0&apos;&quot;;
				ret += db.ExcecutSQL(sql);

				sql = &quot;delete from cw_zekzb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and xh=&quot;+lsh;
				ret += db.ExcecutSQL(sql);
			}
			//20170214 lyh modify 不分配年初数，不会有月指标数据
//			else if ( deleType == 2) {
//				sql = &quot;delete from cw_zekzmxb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and xh=&quot;+lsh+&quot; and bfbz = &apos;0&apos;&quot;;
//				ret += db.ExcecutSQL(sql);
//			}
		}

		db.Commit();

		if (ret == 0) {
			return &quot;操作记录数为零，没有数据被删除！&quot;;
		}
		return &quot;删除医疗数据成功!&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}
}

//存入日记帐
function SaveToRjz()
{
	var db = null;
	var sql = &quot;&quot;;
	try {
		db = new pub.EADatabase();
		var ds = new pub.EAXmlDS(rjzXml);
		var bfyy = &quot;&quot;;
		var bfmm = &quot;&quot;;
		var sblsh = &quot;&quot;;	 //20170220 lyh add 社保流水号
		var service = new SBCW_sbcwService();
		var yhrjzid = &quot;&quot;;

		// 20170220  lyh ADD 获取统筹区编码		
		sql = &quot;select tcqbm from cw_sbsb where sbh=&apos;&quot;+thisorgid+&quot;&apos;&quot;;
		var tcqbm = db.GetSQL(sql);

		if( bfny.indexOf(&quot;-&quot;) &gt; -1 ){
			//bfny.split(&quot;-&quot;)[1];
			bfyy = bfny.split(&quot;-&quot;)[0];
			bfmm = bfny.split(&quot;-&quot;)[1];
		}else{
			bfyy = bfny.substring(0,4);
			bfmm = bfny.substring(4,6);
		}
		var bfyymm = db.GetSQL(&quot;SELECT &apos;&quot;+bfyy+&quot;&apos;||TRIM(TO_CHAR(&apos;&quot;+bfmm+&quot;&apos;,&apos;00&apos;)) FROM DUAL&quot;);

		for (var i=0;i&lt;ds.getRowCount();i++) {
			var lsh = ds.getStringAt(i,&quot;XH&quot;);

			//获取社保流水号			
			sblsh = service.genSiSeq(tcqbm);					
			yhrjzid = db.GetSQL(&quot;select seq_yhrjzb_id.nextval from dual&quot;);

			//获取单位信息财务业务金额
			sql = &quot;SELECT a.zth,A.DWBH,
					 A.DWMC,
					 NVL(B.JE, 0) JE,
					 NVL(B.YWJE, 0) YWJE,
					 NVL(B.YWJE_GRZH, 0) YWJE_GRZH,
					 NVL(B.YWJE_GWY, 0) YWJE_GWY,
					 NVL(B.YWJE_ZGWY, 0) YWJE_ZGWY,
					 NVL(B.YWJE_TCJJ, 0) YWJE_TCJJ,
					 a.xh ,
					 NVL(B.gz_JE, 0) gz_JE,
					 nvl(b.gwy_je,0) gwy_je,
					 nvl(b.zgwy_je,0) zgwy_je,
		   		    	 nvl(b.bk_je,0) bk_je
				 FROM CW_ZEKZB A, CW_ZEKZMXB B
				 WHERE A.SBH = B.SBH
					AND A.XH = B.XH
					AND A.ZTH = B.ZTH
					AND A.YY = B.YY
					AND A.ORG = &apos;&quot;+thisorgid+&quot;&apos;
					AND A.ACC = &apos;&quot;+thisaccid+&quot;&apos;
					AND A.XH = &apos;&quot;+lsh+&quot;&apos;
					AND A.YY = &apos;&quot;+bfyy+&quot;&apos;
					and to_number(b.mm) = to_number(&apos;&quot;+bfmm+&quot;&apos;)
					and b.rjzlsh is null
					and b.bkdid is not null
				ORDER BY a.dwbh&quot;;
			var tmpds = db.QuerySQL(sql);
			if(tmpds.getRowCount() &lt;= 0){
				db.Rollback();
				return &quot;获取单位信息财务业务金额出错，查无此数据,是否未打印拨款单&quot;+sql;
			}
			if (tmpds.getRowCount() &gt; 0) {
				var zth = tmpds.getStringAt(0,&quot;ZTH&quot;);
				var ls_dwbh= tmpds.getStringAt(0,&quot;DWBH&quot;);
				var ls_dwmc = tmpds.getStringAt(0,&quot;DWMC&quot;);
				//摘要日期及所属期
				var ls_rq = rjzyy + rjzmm;
				var ls_zy = &quot;支&quot; + ls_dwmc + bfyymm + &quot;医疗保险费&quot;;
				var ll_lsh = db.GetSQL(&quot;SELECT seq_rjz_lsh.NEXTVAL FROM dual&quot;);
				var ldb_je = tmpds.getStringAt(0,&quot;JE&quot;);
				var ldb_ywje = tmpds.getStringAt(0,&quot;YWJE&quot;);
				var ldb_ywje_gwy = tmpds.getStringAt(0,&quot;YWJE_GWY&quot;);
				var ldb_ywje_grzh = tmpds.getStringAt(0,&quot;YWJE_GRZH&quot;);
				var ldb_ywje_zgwy = tmpds.getStringAt(0,&quot;YWJE_ZGWY&quot;);
				var ldb_ywje_tcjj = tmpds.getStringAt(0,&quot;YWJE_TCJJ&quot;);
				var ldb_gz_je = tmpds.getStringAt(0,&quot;GZ_JE&quot;);
				var ldb_cwgwy_je = tmpds.getStringAt(0,&quot;GWY_JE&quot;);
				var ldb_cwzgwy_je = tmpds.getStringAt(0,&quot;ZGWY_JE&quot;);
				var ldb_bk_je = tmpds.getStringAt(0,&quot;BK_JE&quot;);

				//日记账总金额
				//20170221 lyh modify 				
				var ldb_sum_je = (1.0 * ldb_je) + (1.0 * ldb_gz_je) + (1.0 * ldb_cwgwy_je) + (1.0 * ldb_cwzgwy_je); 
				var ls_lxbh = &quot;32353A3B&quot;;

				if(ldb_ywje_gwy == 0 && ldb_cwgwy_je != 0){
					ldb_ywje_gwy = ldb_cwgwy_je;
				}

				if(ldb_ywje_zgwy == 0 && ldb_cwzgwy_je != 0){
					ldb_ywje_zgwy = ldb_cwzgwy_je;
				}

				var ldb_sum_tcjj = ldb_sum_je - (1.0 * ldb_ywje_gwy) - (1.0 * ldb_ywje_zgwy) - (1.0 * ldb_ywje_grzh);

				if(ldb_sum_je != ldb_bk_je){
					db.Rollback();
					return &quot;拨款金额与日记账金额不等，程序出错，请联系系统开发商&quot;;
				}

				//获取科目最大顺序号
				var ll_max_sxh = db.GetSQL(&quot;SELECT nvl(MAX(sxh),0)+1 FROM cw_rjzb WHERE org=&apos;&quot;+thisorgid+&quot;&apos; AND acc=&apos;&quot;+thisaccid+&quot;&apos; AND yy=&quot;+rjzyy+&quot; AND mm=&quot;+(1*rjzmm)+&quot; AND kmbh=&apos;&quot;+rjzkmbh+&quot;&apos;&quot;);
				var ll_ii = 1;

				var yhrjz_max_sxh = db.GetSQL(&quot;SELECT nvl(MAX(sxh),0)+1 FROM cw_yhrjzb WHERE sbh=&apos;&quot;+thisorgid+&quot;&apos; AND zth=&apos;&quot;+zth+&quot;&apos; AND yy=&quot;+rjzyy+&quot; AND mm=&quot;+(1*rjzmm)+&quot; AND kmbh=&apos;&quot;+rjzkmbh+&quot;&apos;&quot;);

				//
				sql = &quot;INSERT INTO cw_rjzb (org,acc,sbh,zth,lsh,yy,mm,dd,sxh,czyxm,zy,kmbh,lxbh,djh,dwbh,je,jefx,qcbz,dwmc,mkjmbz,siseqno)
					values(&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;)&quot;
					.format([thisorgid,thisaccid,thisorgid,zth,ll_lsh,rjzyy,rjzmm,rjzdd,ll_max_sxh,czyxm,ls_zy,rjzkmbh,ls_lxbh,&quot;&quot;,ls_dwbh,&quot;&quot;+ldb_sum_je,&quot;贷&quot;,&quot;0&quot;,ls_dwmc,&quot;5&quot;,sblsh]);
				db.ExcecutSQL(sql);

				//判断业务金额，构建类型编号
				if (ldb_gz_je != &quot;0&quot;) { //个人账户
					ls_lxbh = &quot;35&quot;;
					sql = &quot;INSERT INTO cw_rjzmxb (org,acc,sbh,zth,lsh,mxxh,lxbh,lxxh,ym1,ym2,je,jefx)VALUES (&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;1&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;贷&apos;)&quot;
						.format([thisorgid,thisaccid,thisorgid,zth,ll_lsh,&quot;&quot;+ll_ii,ls_lxbh,bfyy+&quot;&quot;+bfmm,bfyy+&quot;&quot;+bfmm,&quot;&quot;+ldb_gz_je]);
					db.ExcecutSQL(sql);
					ll_ii ++;
				}
				if (ldb_ywje_gwy != &quot;0&quot;) { //公务员
					ls_lxbh = &quot;3A&quot;;
					sql = &quot;INSERT INTO cw_rjzmxb (org,acc,sbh,zth,lsh,mxxh,lxbh,lxxh,ym1,ym2,je,jefx)VALUES (&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;1&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;贷&apos;)&quot;
						.format([thisorgid,thisaccid,thisorgid,zth,ll_lsh,&quot;&quot;+ll_ii,ls_lxbh,bfyy+&quot;&quot;+bfmm,bfyy+&quot;&quot;+bfmm,&quot;&quot;+ldb_ywje_gwy]);
					db.ExcecutSQL(sql);
					ll_ii ++;
				}
				if (ldb_ywje_zgwy != &quot;0&quot;) { //准公务员
					ls_lxbh = &quot;3B&quot;;
					sql = &quot;INSERT INTO cw_rjzmxb (org,acc,sbh,zth,lsh,mxxh,lxbh,lxxh,ym1,ym2,je,jefx)VALUES (&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;1&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;贷&apos;)&quot;
						.format([thisorgid,thisaccid,thisorgid,zth,ll_lsh,&quot;&quot;+ll_ii,ls_lxbh,bfyy+&quot;&quot;+bfmm,bfyy+&quot;&quot;+bfmm,&quot;&quot;+ldb_ywje_zgwy]);
					db.ExcecutSQL(sql);
					ll_ii ++;
				}
				if (ldb_je != &quot;0&quot;) { //统筹基金
					ls_lxbh = &quot;32&quot;;
					sql = &quot;INSERT INTO cw_rjzmxb (org,acc,sbh,zth,lsh,mxxh,lxbh,lxxh,ym1,ym2,je,jefx)VALUES (&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;1&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;贷&apos;)&quot;
						.format([thisorgid,thisaccid,thisorgid,zth,ll_lsh,&quot;&quot;+ll_ii,ls_lxbh,bfyy+&quot;&quot;+bfmm,bfyy+&quot;&quot;+bfmm,&quot;&quot;+ldb_sum_tcjj]);
					db.ExcecutSQL(sql);
					ll_ii ++;
				}

				//20170220 lyh add
				sql = &quot;insert into cw_yhrjzb(id,sbh,zth,YY,MM,DD,CWPCH,PZH,ZY,JE,JEFX,YEFX,YE,SISEQNO,DJH,YWPCH,ZTLX,ZTBH,ZTMC,KMBH,CZYXM,CZYSJ,JSFS,yspz_fjzs,yspz_fjpch,yspz_lrrq,DYYWJK_LX,sxh)
					values(&apos;&quot;+yhrjzid+&quot;&apos;,&apos;&quot;+thisorgid+&quot;&apos;,&apos;&quot;+zth+&quot;&apos;,&apos;&quot;+rjzyy+&quot;&apos;,&apos;&quot;+rjzmm+&quot;&apos;,&apos;&quot;+rjzdd+&quot;&apos;,&apos;&apos;,&apos;&apos;,&apos;&quot;+ls_zy+&quot;&apos;,&apos;&quot;+ldb_sum_je+&quot;&apos;,&apos;贷&apos;,&apos;借&apos;,0,&apos;&quot;+sblsh+&quot;&apos;,&apos;&apos;,&apos;&apos;,&apos;3&apos;,
						&apos;3&apos;,&apos;医疗机构(医院药店)&apos;,&apos;&quot;+rjzkmbh+&quot;&apos;,&apos;&quot;+czyxm+&quot;&apos;,sysdate,&apos;转账&apos;,&apos;&apos;,&apos;&apos;,sysdate,&apos;&apos;,&apos;&quot;+yhrjz_max_sxh+&quot;&apos;)&quot;;
				db.ExcecutSQL(sql);

				//更新月结算总额控制表
				sql = &quot;update cw_zekzmxb set rjzlsh=&apos;&quot;+sblsh+&quot;&apos;,rjz_czyxm=&apos;&quot;+czyxm+&quot;&apos;,rjz_czsj=sysdate 
					where sbh = &apos;&quot;+thisorgid+&quot;&apos; AND zth = &apos;&quot;+zth+&quot;&apos; AND yy = &apos;&quot;+bfyy+&quot;&apos; AND mm = &apos;&quot;+bfmm+&quot;&apos; and xh = &apos;&quot;+lsh+&quot;&apos;&quot;;
				db.ExcecutSQL(sql);

			}
		}

		db.Commit();

		return &quot;存入日记帐成功！&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}

}

//年终清算
function SaveNzqs(){
	var db = null;
	var sql = &quot;&quot;;
	try {
		db = new pub.EADatabase();

		//判断当年实付存在清算数据
		sql = &quot;select count(1) from cw_zekzmxb where sbh = &apos;&quot;+thisorgid+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and yy = substrb(&apos;&quot;+bfny+&quot;&apos;,1,4) and bfbz = &apos;6&apos;&quot;;
		var cou = db.GetSQL(sql);

		if(cou &lt;= 0){
			sql = &quot;insert into cw_zekzmxb(sbh,zth,xh,yy,mm,bfbz,org,acc) 
				select sbh,zth,xh,yy,mm,&apos;6&apos;,org,acc from cw_zekzmxb where sbh = &apos;&quot;+thisorgid+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and yy = substrb(&apos;&quot;+bfny+&quot;&apos;,1,4) and mm = 12 &quot;;
			db.ExcecutSQL(sql);
		}else{
			sql = &quot;insert into cw_zekzmxb(sbh,zth,xh,yy,mm,bfbz,org,acc) 
				select a.sbh,a.zth,a.xh,a.yy,a.mm,&apos;6&apos;,a.org,a.acc from cw_zekzmxb a where a.sbh = &apos;&quot;+thisorgid+&quot;&apos; and a.zth = &apos;&quot;+zth+&quot;&apos; and a.yy = substrb(&apos;&quot;+bfny+&quot;&apos;,1,4) and a.mm = 12 
				and not exists (select 1 from cw_zekzmxb b where a.sbh = b.sbh and a.zth = b.zth and a.yy = b.yy and a.mm = b.mm and a.xh = b.xh and b.bfbz = &apos;6&apos;)&quot;;
			db.ExcecutSQL(sql);
		}

		db.Commit();
		return 1;
	}catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}finally {
		if (db != null) db.Close();
	}

}
