	_this.SetToComboBox(&quot;&quot;,0,1,8,xzlxlList);
	_this.SetCellText(0,1,8,&quot;%&quot;);

//	//核定方式
//	_this.SetToComboBox(&quot;&quot;,0,2,8,allList);
//	_this.SetCellText(0,2,8,&quot;%&quot;);

	//所属行业
	hyList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_SSHY&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	_this.SetToComboBox(&quot;&quot;,0,3,2,hyList);
	_this.SetCellText(0,3,2,&quot;%&quot;);

	//发放批次
//	var pcList = _this.CreateListValue();
//	for (var i=1;i&lt;=15;i++) {
//		_this.SetListValue(pcList,i,i+&quot;-发&quot;);
//	}
//	_this.SetListValue(pcList,&quot;%&quot;,&quot;全部&quot;);
//	_this.SetToComboBox(&quot;&quot;,0,3,6,pcList);
