//加载数据
function loadMainData()
{	
	_this.SetToolbarString(&quot;正在加载数据...&quot;);

	_this.LoadZXGFile(ZXGFILE2,-1,2);
	_this.SetFixedRow(2,1);
	_this.SetFixedRow(2,1);

	_this.SetMainCellRange(2,1,1,_this.GetRowCount(2)-2,_this.GetColCount(2)-1);
	_this.SetAttribe(&quot;SHEET_2&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );

	var zth = G_ACCID.replace(G_ORGID,&quot;&quot;);
	var szbz = &quot;2&quot;;
	var ywlx = _this.GetCellText(0,3,6);
	var kpbz = _this.GetCellText(0,2,2);
	if (kpbz == 2) kpbz = &quot;&quot;;
	else if (kpbz == 0) kpbz = &quot; and a.bae421 is null&quot;;
	else if (kpbz == 1) kpbz = &quot; and a.bae421 is not null&quot;;
	var zfbz = _this.GetCellText(0,2,4);
	var sshy = _this.GetCellText(0,3,2); //所属行业
	var ssyh = _this.GetCellText(0,2,6); //所属银行
	var hpbz = _this.GetCellText(0,2,8);
	var xzlx = _this.GetCellText(0,1,8); //险种类型
	var ffpc = _this.GetCellText(0,1,4);
	var scny = (_this.GetCellText(0,1,2)).replace(&quot;-&quot;,&quot;&quot;); 
	var param = &quot;YWLX=&quot;+ywlx+&quot;&ZTH=&quot;+zth+&quot;&SZBZ=&quot;+szbz+&quot;&ZFBZ=&quot;+escape(zfbz)+&quot;&SSHY=&quot;+escape(sshy)+&quot;&FFPC=&quot;+escape(ffpc)+&quot;&SCNY=&quot;+scny+&quot;&SSYH=&quot;+escape(ssyh)+&quot;&XZLX=&quot;+escape(xzlx)+&quot;&KPBZ=&quot;+kpbz+&quot;&HPBZ=&quot;+escape(hpbz);
	var xml = _sql.query(&quot;MAIN&quot;,param);
	_this.SetXmlDS(2,1,1,_this.GetRowCount(2)-2,_this.GetColCount(2)-1,xml);
	_this.AutoFixColWidth(2,600);

	_this.SetToBoolBox(2,0,0);
	for (var r=_this.GetMainCellRangeRow1(2);r&lt;=_this.GetMainCellRangeRow2(2);r++) {
		if (_this.GetCellText(2,r,6) == &quot;&quot;) break;
		_this.SetToBoolBox(2,r,0);
		var cwbz = _this.GetCellText(2,r,1);
		if (cwbz == &quot;1&quot;) {
			_this.SetCellShowText(2,r,1,&quot;通过&quot;);
			_this.SetCellColor(2,r,1,0,255,0);
		}
		else if (cwbz == &quot;2&quot;) {
			_this.SetCellShowText(2,r,1,&quot;未通过&quot;);
			_this.SetCellColor(2,r,1,255,0,0);
		}
		else {
			_this.SetCellShowText(2,r,1,&quot;未审核&quot;);
			_this.SetCellColor(2,r,1,255,0,0);
		}
		var kpbz = _this.GetCellText(2,r,2);
		if (kpbz == &quot;1&quot;) {
			_this.SetCellShowText(2,r,2,&quot;已拷&quot;);
			_this.SetCellColor(2,r,2,0,0,255);
		}
		else if (kpbz == &quot;0&quot;) {
			_this.SetCellShowText(2,r,2,&quot;未拷&quot;);
			_this.SetCellColor(2,r,2,255,0,0);
		}
		var hpbz = _this.GetCellText(2,r,3);
		if (hpbz == &quot;1&quot;) {
			_this.SetCellShowText(2,r,3,&quot;已回&quot;);
			_this.SetCellColor(2,r,3,0,0,255);
		}
		else if (hpbz == &quot;0&quot;) {
			_this.SetCellShowText(2,r,3,&quot;未回&quot;);
			_this.SetCellColor(2,r,3,255,0,0);
		}
		_this.SetToComboBox(&quot;&quot;,2,r,11,xzlxlList);
		_this.SetToComboBox(&quot;&quot;,2,r,10,hyList);
	}
	_this.RefreshFormula();
	_this.SetToolbarString(&quot;&quot;);

}

//加载明细数据
function loadMainDetail(sheet,row,pageno)
{
	curr_pageno = pageno;
	curr_pageno_fail = pageno;
	var pagerows = 1000; //分页每页总行数

	_this.LoadZXGFile(ZXGFILE3,-1,3);
	_this.LoadZXGFile(ZXGFILE4,-1,4);
//	_this.SetFixedRow(3,2);
//	_this.SetFixedCol(3,1);

	_this.SetMainCellRange(3,2,0,_this.GetRowCount(3)-2,_this.GetColCount(3)-1);
	_this.SetAttribe(&quot;SHEET_3&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );

	_this.SetMainCellRange(4,2,0,_this.GetRowCount(4)-2,_this.GetColCount(4)-1);
	_this.SetAttribe(&quot;SHEET_4&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );

	var sfny = _this.GetCellText(2,row,6);
	var djh = _this.GetCellText(2,row,9);
	var xzlx = _this.GetCellText(2,row,11);
	var thm = _this.GetCellText(2,row,19);
	var dfyh = _this.GetCellText(2,row,21);
	var lsh = _this.GetCellText(2,row,22);
	if (lsh != &quot;&quot;) lsh = &quot; and aae076=&quot;+lsh;
	else lsh = &quot; and aae076 is null&quot;;
	var param = &quot;SFNY=&quot;+sfny+&quot;&DJH=&quot;+djh+&quot;&XZLX=&quot;+xzlx+&quot;&DFYHBM=&quot;+dfyh+&quot;&FILTERSTR=&quot;;//+lsh+&quot;&THM=&quot;+thm;
	//var xml = _sql.query(&quot;DETAIL&quot;,param);
	param += &quot;&thissytid=&quot;+G_SYTID+&quot;&thisgrdid=&quot;+G_GRDID+&quot;&dsid=DETAIL&pageno=&quot;+pageno+&quot;&pagerows=&quot;+pagerows;

	//获取总记录数
	//添加区分成功和失败数据 20160908 dxb
	var suminfo = invokeOSFunc(&quot;queryRowCount&quot;,param+&quot;&cgbz=1&quot;); 
	var totalcnt = suminfo.split(&quot;~&quot;)[0];
	var summny = suminfo.split(&quot;~&quot;)[1];

	//计算最大分页数
	curr_maxpageno = Math.ceil(totalcnt / pagerows);
	_this.SetCellText(3,0,0,&quot;共 &quot;+totalcnt+&quot; 行  第 &quot; + pageno + &quot;/&quot; + curr_maxpageno + &quot;页 每页 &quot; + pagerows + &quot; 行&quot;);
	_this.SetCellText(3,0,8,summny);

	var xml = invokeOSFunc(&quot;queryXml&quot;,param+&quot;&cgbz=1&quot;);
	_this.SetXmlDS(3,2,0,_this.GetRowCount(3)-2,_this.GetColCount(3)-1,xml);
	//_this.AutoFixColWidth(3,300);


	//添加区分成功和失败数据 20160908 dxb
	var suminfo = invokeOSFunc(&quot;queryRowCount&quot;,param+&quot;&cgbz=0&quot;); 
	var totalcnt = suminfo.split(&quot;~&quot;)[0];
	var summny = suminfo.split(&quot;~&quot;)[1];

	//计算最大分页数
	curr_maxpageno_fail = Math.ceil(totalcnt / pagerows);
	_this.SetCellText(4,0,0,&quot;共 &quot;+totalcnt+&quot; 行  第 &quot; + pageno + &quot;/&quot; + curr_maxpageno_fail + &quot;页 每页 &quot; + pagerows + &quot; 行&quot;);
	_this.SetCellText(4,0,8,summny);	

	var xml = invokeOSFunc(&quot;queryXml&quot;,param+&quot;&cgbz=0&quot;);
	_this.SetXmlDS(4,2,0,_this.GetRowCount(4)-2,_this.GetColCount(4)-1,xml);	

	_this.RefreshFormula();

	_this.SetRowFixWidthMode(3,1);
	_this.SetCellColWidth(3,0,0,160);
	_this.SetCellColWidth(3,0,3,60);
	_this.SetCellColWidth(3,0,4,60);
	_this.SetCellColWidth(3,0,5,60);
	_this.SetCellColWidth(3,0,6,60);
	_this.SetCellColWidth(3,0,15,60);

	_this.SetRowFixWidthMode(4,1);
	_this.SetCellColWidth(4,0,0,160);
	_this.SetCellColWidth(4,0,3,60);
	_this.SetCellColWidth(4,0,4,60);
	_this.SetCellColWidth(4,0,5,60);
	_this.SetCellColWidth(4,0,6,60);	
//	_this.SetMergeCellTextMode(3,1);


	_this.SetRedraw(1);
}



//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if (id == &quot;查询&quot;) {
		loadMainData();
	}
	else if (id == &quot;上一页&quot;) {
		var pageno = curr_pageno - 1;
		if (pageno &lt;= 0) return;
		loadMainDetail(sheet,curr_row,pageno);
	}
	else if (id == &quot;下一页&quot;) {
		var pageno = curr_pageno + 1;
		if (pageno &gt; curr_maxpageno) return;
		loadMainDetail(sheet,curr_row,pageno);
	}
	else if (id == &quot;首页&quot;) {
		loadMainDetail(sheet,curr_row,1);
	}
	else if (id == &quot;尾页&quot;) {
		loadMainDetail(sheet,curr_row,curr_maxpageno);
	}
	else if(id == &quot;点击查询&quot;) {
		loadGRDetail();
	}
}

//鼠标左键点击
function _thisOnMouseLClick(sheet,row,col)
{
	if (sheet == 2) {
		if (row &gt;= _this.GetMainCellRangeRow1(sheet) && row &lt;= _this.GetMainCellRangeRow2(sheet) && col != 0) {
			curr_row = row;
			loadMainDetail(sheet,row,1);
		}
	}
	else if (sheet == 0) {
//		if (row == 3 && col == 9) {
//			showKM();
//		}
	}
}

function showKM()
{
	var obj = new Object();
        obj.filter = &quot;xjyhbz &lt;&gt; 0&quot;;
        obj.mjbz = &quot;1&quot;;
        obj.bz = &quot;&quot;;
        obj.jb = 0;
        obj.kmbh = &quot;&quot;;
	var retVal = window.showModalDialog(&quot;show.sp?grdid=CW_KMSEL&quot;,obj,&quot;dialogWidth=600px;dialogHeight=500px&quot;);
	cur_kmbh = retVal;
	if (cur_kmbh != &quot;&quot; && cur_kmbh != &quot;undefined&quot; && cur_kmbh != undefined) {
		_this.SetCellText(0,3,6,cur_kmbh);
		_thisOnCellModify(0,3,6,&quot;&quot;,cur_kmbh);
	}

}

function getSelectXml(sheet)
{
	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;&lt;ROWSET&gt;\n&quot;;
	var num = 0;
	for (var r=_this.GetMainCellRangeRow1(sheet);r&lt;=_this.GetMainCellRangeRow2(sheet);r++) {
		var flag = _this.GetCellText(sheet,r,0);
		var sfny = _this.GetCellText(sheet,r,6);
		var lsh = _this.GetCellText(2,r,22);
		if (flag == &quot;1&quot; && sfny != &quot;&quot;) {
			num ++;
			xml += &quot;&lt;ROW num=\&quot;&quot;+num+&quot;\&quot;&gt;\n&quot;;
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,1)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,1) +&quot;&lt;/&quot;+_this.GetColName(sheet,1)+&quot;&gt;\n&quot;;		//作废标志
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,6)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,6) +&quot;&lt;/&quot;+_this.GetColName(sheet,6)+&quot;&gt;\n&quot;;		//实付年月
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,9)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,9) +&quot;&lt;/&quot;+_this.GetColName(sheet,9)+&quot;&gt;\n&quot;;		//业务单据号
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,11)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,11) +&quot;&lt;/&quot;+_this.GetColName(sheet,11)+&quot;&gt;\n&quot;;	//险种类型
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,21)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,21) +&quot;&lt;/&quot;+_this.GetColName(sheet,21)+&quot;&gt;\n&quot;;	//代发银行
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,13)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,13) +&quot;&lt;/&quot;+_this.GetColName(sheet,13)+&quot;&gt;\n&quot;;	//代发银行
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,18)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,18) +&quot;&lt;/&quot;+_this.GetColName(sheet,18)+&quot;&gt;\n&quot;;	//发放批次
			xml += &quot;&lt;/ROW&gt;\n&quot;;		
		}
	}
	xml += &quot;&lt;/ROWSET&gt;&quot;;
	return xml;
}

//生成文件
function GenFile()
{
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}
	var kpdat = _this.GetCellText(0,1,6);

	if (!confirm(&quot;生成&quot;+kpdat+&quot;的拷盘文件，是否继续？&quot;)) return;
	var mypch = _this.GetCellText(0,1,4);
	if(mypch == &quot;&quot; || mypch == &quot;%&quot;) {
		alert(&quot;必须输入批次号才能生成拷盘文件&quot;);
		return false;
	}
	var cnt = 0;
	var arr = new Array();
	for (var r=_this.GetMainCellRangeRow1(2);r&lt;=_this.GetMainCellRangeRow2(2);r++) {
		var flag = _this.GetCellText(2,r,0);
		var cwbz = _this.GetCellText(2,r,1);
		var kpbz = _this.GetCellText(2,r,2);
		var sfny = _this.GetCellText(2,r,6);
		var djh = _this.GetCellText(2,r,9);
		var xzlx = _this.GetCellShowText(2,r,11);
		var dfyh = _this.GetCellText(2,r,13);
		var mny = _this.GetCellShowText(2,r,15);
		var pch = _this.GetCellText(2,r,18);
		var lsh = _this.GetCellText(2,r,22);
		if (flag == 1 && sfny != &quot;&quot;) {
			cnt ++;
			arr.push(djh);
		}
		if (flag == 1 && cwbz != &quot;1&quot;) {
			alert(&quot;该笔财务实付审核[&quot;+_this.GetCellShowText(2,r,1)+&quot;]，不能生成拷盘！\n银行：&quot;+dfyh+&quot; \n险种：&quot;+xzlx+&quot; \n单据号：&quot;+djh+&quot; \n批次号：&quot;+pch+&quot; \n金额：&quot;+mny);
			_this.SetFocusCell(2,r,6);
			return;
		}
		if (flag == 1 && kpbz == 1) {
			alert(&quot;该笔记录已生成拷盘，不能重复拷盘！\n银行：&quot;+dfyh+&quot; \n险种：&quot;+xzlx+&quot; \n单据号：&quot;+djh+&quot; \n批次号：&quot;+pch+&quot; \n金额：&quot;+mny);
			_this.SetFocusCell(2,r,6);
			return;
		}
	}
	if (cnt == 0) {
		alert(&quot;没有选中记录！&quot;);
		return;
	}
	var param = new Object();
	param.kpdat = kpdat;
	param.xml = getSelectXml(2);
	param.thisorgid = G_ORGID;
	param.thisaccid = G_ACCID;
	param.sbh = G_ORGID;
	param.zth = G_ACCID.replace(G_ORGID,&quot;&quot;);
	param.usrid = G_USRID;
	param.czyxm = G_USRNAM;

	try {
		var ret = invokeOSFuncExt(&quot;GenKPFile&quot;,param);
		if (ret.substring(0,1) == &quot;1&quot;) {
			alert(&quot;生成[&quot;+kpdat+&quot;]拷盘文件成功！&quot;+ret.substring(2));
			loadMainData();
//			for (var r=_this.GetMainCellRangeRow1(2);r&lt;=_this.GetMainCellRangeRow2(2);r++) {
//				var flag = _this.GetCellText(2,r,0);
//				var kpbz = _this.GetCellText(2,r,2);
//				if (flag == 1 && kpbz == 0) {
//					_this.SetCellText(2,r,2,&quot;1&quot;);
//					_this.SetCellText(2,r,5,kpdat.replace(/\-/g,&quot;&quot;));
//					_this.SetCellShowText(2,r,2,&quot;已拷&quot;);
//					_this.SetCellColor(2,r,2,0,0,255);
//				}
//			}				
		}
		else {
			alert(&quot;生成拷盘文件不成功：&quot;+ret);
			return;
		}
		//生成拷盘文件后自动下载文件 20160621 丁晓斌
		try{	
			var tips = &quot;&quot;;
			var cnt = 0;
			var dat = _this.GetCellText(0,1,6);
			var mmdir = dat.substring(0,4) + dat.substring(5,7);
			var fso = new ActiveXObject(&quot;Scripting.FileSystemObject&quot;);
			for (var i=0;i&lt;arr.length;i++) {
				var mydjh = arr[i];
				_this.XMLDS_Reset(); 
				var sbh = G_ORGID;
				var zth = G_ACCID.replace(sbh,&quot;&quot;);
				_sql.querytods(&quot;QueryAC95&quot;,&quot;mydjh=&quot;+mydjh+&quot;&sbh=&quot;+sbh+&quot;&zth=&quot;+zth);
				var kpbz = _this.XMLDS_GetString(&quot;0&quot;,&quot;kpbz&quot;);
				var kprq = _this.XMLDS_GetString(&quot;0&quot;,&quot;kprq&quot;);
				var yhbm = _this.XMLDS_GetString(&quot;0&quot;,&quot;dfyhbm&quot;);
				var kpwjm = _this.XMLDS_GetString(&quot;0&quot;,&quot;kpwjm&quot;);
				var kpwjmSum = kpwjm.substring(0,kpwjm.lastIndexOf(&quot;.&quot;))+&quot;_SUM&quot;+kpwjm.substring(kpwjm.lastIndexOf(&quot;.&quot;));
				if(kpbz == &quot;1&quot;) {
					cnt ++;
					_sql.querytods(&quot;YHFTPINFO&quot;,&quot;YHBM=&quot;+yhbm);
					var wjml = _this.XMLDS_GetString(0,&quot;WJML&quot;);
					wjml += &quot;\\&quot; + mmdir;
					if (!autoCreateFolder(wjml)) continue; //20160608 by flm	
					var txt = invokeOSFunc(&quot;downLoadFile&quot;,&quot;filename=&quot;+kpwjm+&quot;&mmdir=&quot;+mmdir+&quot;&thisorgid=&quot;+G_ORGID+&quot;&thisaccid=&quot;+G_ACCID);
					var filename = wjml+&quot;\\&quot;+kpwjm;
					if (fso.FileExists(filename)) {
						if (!confirm(&quot;拷盘文件&quot;+filename+&quot;已存在，是否覆盖？&quot;)) continue;
					}			
					tips += _this.SaveTextFile(filename,txt,&quot;*.txt&quot;) + &quot;\n&quot; ;		
					txt = invokeOSFunc(&quot;downLoadFile&quot;,&quot;filename=&quot;+kpwjmSum+&quot;&mmdir=&quot;+mmdir+&quot;&thisorgid=&quot;+G_ORGID+&quot;&thisaccid=&quot;+G_ACCID);
					tips += _this.SaveTextFile(wjml+&quot;\\&quot;+kpwjmSum,txt,&quot;*.txt&quot;) + &quot;\n&quot; ;	
				}		
			}						
			if (tips != &quot;&quot; && cnt &gt; 0) {
				alert(&quot;下载成功，不需要再手动下载文件！\n&quot;+tips);
			}
			else {
				alert(&quot;1-自动下载拷盘文件出错，请手动下载!&quot;);
			}		
		}catch(e) {
			alert(&quot;2-自动下载拷盘文件出错，请手动下载!!&quot;);
		}				
	}
	catch (e) {
		alert(e);
	}
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}

	if (sheet == 2) {
		if (row == 0 && col == 0) {
			for (var r=_this.GetMainCellRangeRow1(sheet);r&lt;=_this.GetMainCellRangeRow2(sheet);r++) {
				if (_this.GetCellText(sheet,r,6) != &quot;&quot;) {
					_this.SetCellText(sheet,r,0,newvalue);
				}
			}
		}	
		else if (col == 1) {

			if (newvalue == 1) {
				if (!confirm(&quot;作废该笔记录，是否继续？&quot;)) return;
			}
			else if (newvalue == 0) {
				if (!confirm(&quot;取消作废该笔记录，是否继续？&quot;)) return;
			}
			var param = new Object();
			param.zfbz = newvalue;
			param.thisorgid = G_ORGID;
			param.thisaccid = G_ACCID;
			param.czyxm = G_USRNAM;
			param.djh = _this.GetCellText(sheet,row,9);
			param.xzlx = _this.GetCellText(sheet,row,11);
			param.pch = _this.GetCellText(sheet,row,18);
			param.yhbm = _this.GetCellText(sheet,row,21);
			param.lsh = _this.GetCellText(sheet,row,22);
			var ret = invokeOSFuncExt(&quot;zfSave&quot;,param);
			alert(ret);
		}	
	}
	else if (sheet == 0) {
		if (row == 1 && col == 2) {
			var ym1 = _this.GetCellText(sheet,1,2);
			if (ym1.indexOf(&quot;-&quot;) == -1) {
				var ym11 = ym1.substring(0,4)+&quot;-&quot;+ym1.substring(4,6);
				_this.SetCellText(sheet,1,2,ym11);
			}
			ym1 = ym1.replace(&quot;-&quot;,&quot;&quot;);
			if (ym1.length &lt; 6) {
				alert(&quot;输入的期限不正确，请检查！格式：YYYYMM/YYYY-MM 201501/2015-01&quot;);
				_this.SetCellText(sheet,row,col,oldvalue);
			}

		}	
		loadMainData();
	}
}

function selectOneRow(sheet,row)
{
	var flg = _this.GetCellText(sheet,row,0);
	if (flg == 1) {
		_this.SetCellText(sheet,row,0,0);
		setOneRowBkColor(sheet,row,255,255,255);		
	}
	else {
		_this.SetCellText(sheet,row,0,1);
		setOneRowBkColor(sheet,row,255,236,139);
	}
}

function setOneRowBkColor(sheet,row,r,g,b)
{
	for (var c=0;c&lt;_this.GetColCount(sheet);c++) {
		_this.SetCellBkColor(sheet,row,c,r,g,b);
	}
	_this.SetCellFocus(sheet,row,0);
	_this.Redraw();
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if (sheet == 2 && row &gt;= _this.GetMainCellRangeRow1(2) && row &lt;= _this.GetMainCellRangeRow2(2)) {
		selectOneRow(sheet,row);
	}
}

//下载拷盘文件
function downKP()
{
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}

	var ret = &quot;&quot;;
	var cnt = 0;
	var dat = _this.GetCellText(0,1,6);
	var mmdir = dat.substring(0,4) + dat.substring(5,7);
	var fso = new ActiveXObject(&quot;Scripting.FileSystemObject&quot;);
	for (var r=_this.GetMainCellRangeRow1(2);r&lt;=_this.GetMainCellRangeRow2(2);r++) {
		var flag = _this.GetCellText(2,r,0);
		var kpbz = _this.GetCellText(2,r,2);
		var kprq = _this.GetCellText(2,r,5);
		var yhbm = _this.GetCellText(2,r,21);
		var kpwjm = _this.GetCellText(2,r,23);
		var kpwjmSum = kpwjm.substring(0,kpwjm.lastIndexOf(&quot;.&quot;))+&quot;_SUM&quot;+kpwjm.substring(kpwjm.lastIndexOf(&quot;.&quot;));
		if (flag == 1 && kpbz == &quot;1&quot;) {
			//2016-12-24 add by flm
			var yszlbz = invokeOSFunc(&quot;checkYSZL&quot;,&quot;sbh=&quot;+jz_sbh+&quot;&zth=&quot;+jz_zth+&quot;&yhbm=&quot;+yhbm);
			if (yszlbz != &quot;ok&quot;) {
				alert(&quot;不允许下载拷盘文件!\n&quot;+yszlbz);
				return;
			}

			cnt ++;
			_sql.querytods(&quot;YHFTPINFO&quot;,&quot;YHBM=&quot;+yhbm);
			var wjml = _this.XMLDS_GetString(0,&quot;WJML&quot;);
			wjml += &quot;\\&quot; + mmdir;

			if (!autoCreateFolder(wjml)) continue; //20160608 by flm

			//服务器端的mmdir修改为拷盘日期的年月：kprq.substring(0,6)
			var txt = invokeOSFunc(&quot;downLoadFile&quot;,&quot;filename=&quot;+kpwjm+&quot;&mmdir=&quot;+kprq.substring(0,6)+&quot;&thisorgid=&quot;+G_ORGID+&quot;&thisaccid=&quot;+G_ACCID);
			var filename = wjml+&quot;\\&quot;+kpwjm;
			if (fso.FileExists(filename)) {
				if (!confirm(&quot;拷盘文件&quot;+filename+&quot;已存在，是否覆盖？&quot;)) continue;
			}			
			ret += _this.SaveTextFile(filename,txt,&quot;*.txt&quot;) + &quot;\n&quot; ;		
			txt = invokeOSFunc(&quot;downLoadFile&quot;,&quot;filename=&quot;+kpwjmSum+&quot;&mmdir=&quot;+kprq.substring(0,6)+&quot;&thisorgid=&quot;+G_ORGID+&quot;&thisaccid=&quot;+G_ACCID);
			ret += _this.SaveTextFile(wjml+&quot;\\&quot;+kpwjmSum,txt,&quot;*.txt&quot;) + &quot;\n&quot; ;		
		}
	}

	if (cnt == 0) {
		alert(&quot;没有拷盘数据！&quot;);
	}

	if (ret != &quot;&quot; && cnt &gt; 0) alert(&quot;下载成功！\n&quot;+ret);


}

//点击菜单项
function _thisOnMenuItemSelect(sheet,row,col,menuitemid)
{
	if (menuitemid == &quot;typ_ftp&quot; || menuitemid == &quot;typ_seltxt&quot;) {
		//检查是否已经结账
		var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
		var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
		var jz_sbh = G_ORGID;
		var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

		var param = new Object();
		param.yy = jz_yy;
		param.mm = jz_mm;
		param.sbh = jz_sbh;
		param.zth = jz_zth;
		var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
		var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
		var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
		if(jz_ret != 1 ){
			alert(jz_retVal);
			return false;
		}

		var hpbank = _this.GetCellText(0,2,6); //回盘银行
		var hpdat = _this.GetCellText(0,1,6); //回盘日期
		var zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

		//2016-12-27 add by flm 加入回盘时必须选择银行
		if (hpbank == &quot;&quot; || hpbank == &quot;%&quot;) {
			alert(&quot;请先选择回盘银行！（提示：在查询参数里选择银行种类）&quot;);
			return;
		}
		if (hpdat == &quot;&quot;) {
			alert(&quot;请选择回盘日期！（提示：在查询参数里选择回盘到账日期）&quot;);
			return;
		}

		if (!confirm(&quot;请确认：当前回盘日期（记帐日期）为 &quot;+hpdat+&quot; ，是否继续？&quot;)) return;

		var param = new Object();
		param.hpbank = hpbank;  //传入回盘银行 
		var txt = &quot;&quot;;
		//FTP方式导入
		if (menuitemid == &quot;typ_ftp&quot;) {
			var retVal = window.showModalDialog(&quot;show.sp?grdid=FTPUTIL&szbz=2&bank=3&quot;,null,&quot;dialogWidth=1000px;dialogHeight=500px&quot;);
			if (retVal != &quot;&quot; && retVal != &quot;undefined&quot; && retVal != undefined) {
				if (retVal.split(&quot;,&quot;).length != 5) return;

				param.typ = 1;
				param.ftpinfo = retVal;
				param.hpfilename = &quot;&quot;;
				param.text = &quot;&quot;;
			}
			else {
				return;
			}
		}
		//本地文本文件导入
		else  if (menuitemid == &quot;typ_seltxt&quot;) {
			var inputObj = document.createElement(&apos;input&apos;)
		        inputObj.setAttribute(&apos;id&apos;,&apos;_ef&apos;);
		        inputObj.setAttribute(&apos;type&apos;,&apos;file&apos;);
		        inputObj.setAttribute(&quot;style&quot;,&apos;visibility:hidden&apos;);
		        document.body.appendChild(inputObj);
		        inputObj.click();
		        var filename = inputObj.value ;
		        if (filename == &quot;&quot; || filename == undefined) {
		        	return;
		        }

		        txt = _this.LoadTextFile(filename);
		        param.typ = 2;
		        param.hpfilename = filename;
		        param.text = txt;
			param.ftpinfo = &quot;&quot;;
	        }

		param.zth = zth;
		param.hpdat = hpdat;
		param.thisorgid = G_ORGID;
		param.thisaccid = G_ACCID;
		param.usrid = G_USRID;
		param.czyxm = G_USRNAM;

		if (jobid == &quot;&quot;) {
			jobid = invokeOSFunc(&quot;getSysDate&quot;,&quot;thisorgid=&quot;+G_ORGID+&quot;&zth=&quot;+zth);
			alert(jobid);
		}
		var jobseqid = &quot;FFHP&quot;+jobid;
		param.jobid = jobid;
		param.jobnam = &quot;银行发放回盘导入进程[&quot;+jobseqid+&quot;]&quot;;

		_sql.querytods(&quot;QueryRunOSTimer&quot;,&quot;jobseqid=&quot;+jobseqid);
		if( 1*_this.XMLDS_GetStringAt(0,0)&gt;0 ){
			//有一个作业正在运行，打开进度框
			//window.showModalDialog(&quot;show.sp?grdid=RunOSTimer&jobseqid=&quot;+jobseqid,&quot;&quot;,&quot;dialogwidth:600pt;dialogheight:500pt;help:yes;resizable:yes;&quot; );
		}
		else {
			_sql.querytods(&quot;QueryRunOSTimerExist&quot;,&quot;jobseqid=&quot;+jobseqid);
			if ( 1*_this.XMLDS_GetStringAt(0,0) &gt; 0) { //是否运行结束
				//运行结束打开预览保存窗口
				clearInterval(intervalProcess);
				var hpret = window.showModalDialog(&quot;show.sp?grdid=RJZ_PAYRETURN&JOBID=&quot;+jobid+&quot;&HPRQ=&quot;+hpdat+&quot;&HPBANK=&quot;+hpbank,&quot;&quot;,&quot;dialogWidth=1200px;dialogHeight=800px&quot;);
				if (hpret == &quot;success&quot;) {
					loadMainData();
				}
				jobid = &quot;&quot;;
			}
			else {
				intervalProcess = setInterval(&quot;refresh(&apos;&quot;+jobseqid+&quot;&apos;)&quot;,500);
				var ret = invokeOSFuncExt(&quot;genbatch&quot;,param) ;   
				if ( ret == jobseqid ) {
					//window.showModalDialog(&quot;show.sp?grdid=RunOSTimer&rungrdid=RJZ_PAYRETURN&jobseqid=&quot;+ret ,&quot;&quot;,&quot;dialogwidth:600pt;dialogheight:500pt;help:yes;resizable:yes;&quot; );
				}			
			}
		}


	}

}


//自动创建文件目录
function autoCreateFolder(path)
{
	try {
		var fso = new ActiveXObject(&quot;Scripting.FileSystemObject&quot;);
		var a;

		var p = path.split(&quot;\\&quot;);
		var root = p[0];
		var subdir = &quot;&quot;;
		for (var i=1;i&lt;p.length;i++) {
			subdir += &quot;\\&quot; + p[i]
			var dir = root + subdir; 
			if (!fso.FolderExists(dir)) {
				a = fso.CreateFolder(dir); //创建文件夹目录
			}
		}

		if (a == &quot;undefied&quot; || a == &quot;&quot;) {
			alert(&quot;自动创建文件目录（&quot;+path+&quot;）失败！&quot;);
			return false;
		}
		return true;
	}
	catch (e) {
		alert(&quot;创建文件目录失败!&quot;+path);
	}
}

//显示日记帐
function ShowRjz()
{
	var currow = _this.GetCurrentRow(2);
	if (currow &lt; _this.GetMainCellRangeRow1(2)) return;
	var djh = _this.GetCellText(2,currow,9);
	var lsh = _this.GetCellText(2,currow,22);
	if (lsh != &quot;&quot;) {
		var url = &quot;show.sp?grdid=CW_SHOWRJZMX&lsh=&quot;+lsh+&quot;&djh=&quot;+djh;
		window.showModalDialog(url,null,&quot;dialogWidth=1024px;dialogHeight=620px&quot;);
	}
	else {
		alert(&quot;没有日记帐记录,可能是尚未入帐!&quot;);
	}
}
//自动创建文件目录
function autoCreateFolder(path)
{
	try {
		var fso = new ActiveXObject(&quot;Scripting.FileSystemObject&quot;);
		var a;

		var p = path.split(&quot;\\&quot;);
		var root = p[0];
		var subdir = &quot;&quot;;
		for (var i=1;i&lt;p.length;i++) {
			subdir += &quot;\\&quot; + p[i]
			var dir = root + subdir; 
			if (!fso.FolderExists(dir)) {
				a = fso.CreateFolder(dir); //创建文件夹目录
			}
		}

		if (a == &quot;undefied&quot; || a == &quot;&quot;) {
			alert(&quot;自动创建文件目录（&quot;+path+&quot;）失败！&quot;);
			return false;
		}
		return true;
	}
	catch (e) {
		alert(&quot;创建文件目录失败!&quot;+path+&quot;，错误信息:&quot;+e.toString());
	}
}

//查询个人发放失败历史
function loadGRDetail()
{
	var grbh = _this.GetCellText(5,0,2);
	if(grbh == &quot;&quot;) {
		alert(&quot;请先输入个人编号&quot;);
		return false;
	}	
	_this.LoadZXGFile(ZXGFILE5,-1,5);
	_this.SetMainCellRange(5,2,0,_this.GetRowCount(5)-2,_this.GetColCount(5)-1);
	_this.SetAttribe(&quot;SHEET_5&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );	
	_this.XMLDS_Reset();
	var xml = _sql.query(&quot;GRDetail&quot;,&quot;grbh=&quot;+grbh);
     	_this.XMLDS_Parse(xml);
     	if(_this.XMLDS_GetRowCount() == 0) {
     		alert(&quot;查询无数据&quot;);
     	}	
	_this.SetXmlDS(5,2,0,_this.GetRowCount(5)-2,_this.GetColCount(5)-1,xml);
	_this.RefreshFormula();
}


function refresh(jobseqid)
{
	timecnt ++;

	_sql.querytods(&quot;TIPSDS&quot;,&quot;jobseqid=&quot;+jobseqid);
	var percent = _this.XMLDS_GetString(0,&quot;PERCENT&quot;);
	var tips = _this.XMLDS_GetString(0,&quot;TIPS&quot;);
	var impbz = 0;
	_this.SetToolbarString(tips);
	if (tips.indexOf(&quot;回盘文件导入成功&quot;) &gt; -1) {
		impbz = 1;
		_this.SetToolbarString(&quot;&quot;);
	}

	if (percent == 100) {
		clearInterval(intervalProcess);
		if (impbz == 1) {
			var hpdat = _this.GetCellText(0,1,6); //回盘日期
			var hpbank = _this.GetCellText(0,2,6); //回盘银行
			var hpret = window.showModalDialog(&quot;show.sp?grdid=RJZ_PAYRETURN&JOBID=&quot;+jobid+&quot;&HPRQ=&quot;+hpdat+&quot;&HPBANK=&quot;+hpbank,&quot;&quot;,&quot;dialogWidth=1200px;dialogHeight=800px&quot;);
			if (hpret == &quot;success&quot;) {
				loadMainData();
			}
		}
		else {
			alert(tips+&quot; 进程ID:&quot;+jobseqid);
		}
		jobid = &quot;&quot;;
	}
}

