//创建临时表
function createTempTable(db)
{
	var tempTable = db.GetSQL(&quot;select &apos;TMP_YHKP&apos;||to_char(sysdate,&apos;yymmddhh24miss&apos;) from dual&quot;);
	var sql = &quot;create table &quot;+tempTable+&quot; (zfbz varchar(20),sfny varchar(20),djh varchar(20),xzlx varchar(20),dfyhbm varchar(20),dfyh varchar(50),ffpc varchar(20))&quot;;
	db.ExcecutSQL(sql);	
	return tempTable;
}

//drop临时表
function dropTempTable(db,tempTable)
{
	var sql = &quot;drop table &quot;+tempTable;
	db.ExcecutSQL(sql);
}

//生成拷盘文件
function GenKPFile()
{
	//return xml;
	var db = null;
	var sql = &quot;&quot;;
	var tempTable = &quot;&quot;;
	var tips = &quot;&quot;;
	try {
		storeRoot = pub.EAFunc.NVL(pub.EAOption.get(&quot;filestore&quot;),&quot;/filestore&quot;);

		db = new pub.EADatabase();
		var ds = new pub.EAXmlDS(xml);
		tempTable = createTempTable(db);
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var zfbz = &quot;0&quot;;//ds.getStringAt(i,&quot;ZFBZ&quot;); //作废标志
			var sfny = ds.getStringAt(i,&quot;SFNY&quot;);
			var djh = ds.getStringAt(i,&quot;DJH&quot;);
			var xzlx = ds.getStringAt(i,&quot;XZLX&quot;);
			var dfyhbm = ds.getStringAt(i,&quot;DFYHBM&quot;);
			var dfyh = ds.getStringAt(i,&quot;DFYHMC&quot;);
			var ffpc = ds.getStringAt(i,&quot;FFPCH&quot;);
			sql = &quot;insert into &quot;+tempTable+&quot;(zfbz,sfny,djh,xzlx,dfyhbm,dfyh,ffpc) values (&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;)&quot;.format([zfbz,sfny,djh,xzlx,dfyhbm,dfyh,ffpc]);
			db.ExcecutSQL(sql); //写入临时表进行分组
		}

		//按银行分组，同一银行不同单据号的合并成为一个文件
//		sql = &quot;select sfny,dfyh,to_char(wm_concat(distinct djh)) djh,xzlx,dfyhbm,ffpc  
//			from &quot;+tempTable+&quot; where zfbz=&apos;0&apos;
//			group by sfny,dfyhbm,dfyh,xzlx,ffpc&quot;;
		//20161229处理单据号转换长度可能超4000报错问题
		sql = &quot;select sfny,dfyh,xzlx,dfyhbm,ffpc from &quot;+tempTable+&quot; where zfbz=&apos;0&apos; group by sfny,dfyhbm,dfyh,xzlx,ffpc&quot;;
		ds = db.QuerySQL(sql);

		//是否启用银社直连判断
		var sipub = new SBCW_PUBFUNC();
		for (var i=0;i&lt;ds.getRowCount();i++) {	  
			var yhbm = ds.getStringAt(i,&quot;DFYHBM&quot;);
			var chkret = sipub.checkYSZLStatusByYH(db,sbh,zth,yhbm);
			if (chkret != &quot;0&quot;) { //已启用
				if (chkret != &quot;&quot;) {
					var rst = 1*db.GetSQL(&quot;select to_date(&apos;&quot;+kpdat+&quot;&apos;,&apos;yyyy-mm-dd&apos;) - to_date(&apos;&quot;+chkret+&quot;&apos;,&apos;yyyy-mm-dd&apos;) from dual&quot;); //2017-01-18 add
					if (rst &gt;= 0) {
						var yhmc = db.GetSQL(&quot;select fullname from V_CW_YHZL_ZF where sbh=&apos;&quot;+sbh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and id=&apos;&quot;+yhbm+&quot;&apos;&quot;);
						return &quot;【&quot;+yhmc+&quot;】社银直连已启用，请到社银直连模块操作！&quot;; 
					} 				
				}
			}
		}

		for (var i=0;i&lt;ds.getRowCount();i++) {			
			var sfny = ds.getStringAt(i,&quot;SFNY&quot;);
			//var djh = ds.getStringAt(i,&quot;DJH&quot;); 
			var xzlx = ds.getStringAt(i,&quot;XZLX&quot;);
			var dfyhbm = ds.getStringAt(i,&quot;DFYHBM&quot;);
			var dfyh = ds.getStringAt(i,&quot;DFYH&quot;);
			var ffpc = ds.getStringAt(i,&quot;FFPC&quot;);

			//20161024 lyh modify 同一个人会出现两笔记录，要求和
			var datasql = &quot;select rownum rn,a.* from (
				select AAC001, /*个人编号*/
					replace(replace(replace(AAE009,chr(9),&apos;&apos;),chr(10),&apos;&apos;),chr(13),&apos;&apos;) AAC003, /*姓名*/
					AAB001, /*单位编号*/
					AAE135, /*身份证*/
					LPAD(TRIM(TO_CHAR(SUM(AIC263), &apos;999999999990.99&apos;)), 16) AIC263, /*代发金额  20161223 lyh modify 生成文件金额 .1  改为 0.1显示 LPAD(TRIM(TO_CHAR(SUM(AIC263), &apos;999999999999.99&apos;)), 16) AIC263,*/
					replace(replace(replace(aae010,chr(9),&apos;&apos;),chr(10),&apos;&apos;),chr(13),&apos;&apos;) AAE010, /*代发账号*/
					AAE007, /*邮政编码*/
					/*BAE074||&apos;||&apos;||BAE415||&apos;||&apos;||BIC006 BIC006 //单据号||批次号||通信地址*/
					BAE074||&apos;__&apos;||BAE415||&apos;__&apos;||BAZ610 BIC006 /*单据号__批次号__通信地址 20161209改为下划线 20170117 lyh modify 不需要地址，改为要AC95.BAZ610*/
				from ac95
				where AAE208=&apos;&quot;+sfny+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and aaa027=(select tcqbm from cw_sbsb where org=&apos;&quot;+sbh+&quot;&apos;)
				  and BAE074 in (select djh from &quot;+tempTable+&quot; where zfbz=&apos;0&apos; and sfny=&apos;&quot;+sfny+&quot;&apos; and xzlx=&apos;&quot;+xzlx+&quot;&apos; and dfyhbm=&apos;&quot;+dfyhbm+&quot;&apos; and ffpc=&apos;&quot;+ffpc+&quot;&apos;)
				  and aae140=&apos;&quot;+xzlx+&quot;&apos;
				  and BAE419=&apos;&quot;+dfyhbm+&quot;&apos;
				  and BAE415=&apos;&quot;+ffpc+&quot;&apos;
				 GROUP BY AAC001,
			          AAE009,
			          AAB001,
			          AAE135,
			          AAE010,
			          AAE007,
			          BAE074,BAE415,BIC006,
			          BAE415,
			          BAE419,BAZ610
				order by BAE415,BAE419,AAB001,AAC001
				) a&quot;;	

			//为避免溢出做分页处理
			var pagerows = 1000;
			var totalrows = db.GetSQLRowCount(datasql); //总记录数
			var pagecount = db.GetSQL(&quot;select ceil(&quot;+totalrows+&quot;/&quot;+pagerows+&quot;) from dual&quot;); //总页数

			var sumHS = totalrows; 	//合计户数
			var sumMny = &quot;&quot;;	//合计金额
			var sumsql = &quot;select to_char(round(sum(AIC263),2)) mny from (&quot;+datasql+&quot;)&quot;; //转成字符串避免产生科学计数
			sumMny = db.GetSQL(sumsql); //合计金额

			//return fileStr;

			//获取社保所统筹区代码
			//服务器保存本地文件目录改为：/u/dsdf/统筹区代码_帐套号/yyyymm/文件名
			var sicode = db.GetSQL(&quot;select tcqbm from cw_sbsb where sbh=&apos;&quot;+thisorgid+&quot;&apos;&quot;);

			sql = &quot;select * from CW_DFDSB where sbh=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and yhbm=&apos;&quot;+dfyhbm+&quot;&apos; and szbz = &apos;2&apos;&quot;;
			var ftpds = db.QuerySQL(sql); //ftp的配置信息
			if (ftpds.getRowCount() &gt; 0) {
				var wjm = ftpds.getStringAt(0,&quot;WJM&quot;);
				var ftpip = ftpds.getStringAt(0,&quot;FTP_IP&quot;);
				var ftpport = ftpds.getStringAt(0,&quot;FTP_PORT&quot;);
				var ftpuser = ftpds.getStringAt(0,&quot;FTP_GXSI_LOGIN_NAME&quot;);
				var ftppasswd = ftpds.getStringAt(0,&quot;FTP_GXSI_LOGIN_PASSWD&quot;);		
				var ftploginpath = ftpds.getStringAt(0,&quot;FTP_GXSI_LOGIN_PATH&quot;);			
				var ftpuppath = ftpds.getStringAt(0,&quot;FTP_UP_PATH&quot;);								
				var upwjm = pub.EAFunc.Replace(kpdat,&quot;-&quot;,&quot;&quot;); //20151217								

				var kpdir = upwjm.substring(0,6);
				ftpuppath += &quot;/&quot; + kpdir;
				upwjm = wjm.substring(0,wjm.indexOf(&quot;.&quot;))+upwjm.substring(2,8)+wjm.substring(wjm.indexOf(&quot;.&quot;)); //NY151217.txt

				upwjm = getKPFileName(xzlx,upwjm); //sy_NY20151217.txt

				//var fileName = &quot;/u/dsdf/&quot;+kpdir+&quot;/&quot;+upwjm; ///u/dsdf/sy_NY20151217.txt	
				var fileName = &quot;/u/dsdf/&quot;+sicode+&quot;_&quot;+thisaccid+&quot;/&quot;+kpdir+&quot;/&quot;+upwjm; ///u/dsdf/459900_131/201604/sy_NY20151217.txt	
				var tmpfile = new java.io.File(fileName);
	      			if (tmpfile.exists()) {
	      				//upwjm = upwjm + &quot;&quot;;
	      					//fileName = &quot;/u/dsdf/&quot;+kpdir+&quot;/&quot;+upwjm;
	      					return &quot;拷盘文件【&quot;+fileName+&quot;】已存在，不能覆盖！&quot;;
	      			}
	      			tips += fileName+&quot;\n&quot;;

				//生成拷盘明细文件	
				//按分页追加的方式生成明细文件，避免文件过大出现溢出错误 //20161208 by flm
				for (var pageno=1;pageno&lt;=pagecount;pageno++) {		
					var pagesql = &quot;select * from ( &quot; + datasql + &quot; ) where rn&gt;(&quot;+pageno+&quot;-1)*&quot;+pagerows+&quot; and rn&lt;=&quot;+pageno+&quot;*&quot;+pagerows; //分页查询
					var tmpds = db.QuerySQL(pagesql);
					var fileStr = &quot;&quot;;
					for (var k=0;k&lt;tmpds.getRowCount();k++) {
						var AAC001 = formatStr(tmpds.getStringAt(k,&quot;AAC001&quot;),16,0);
						var AAC003 = formatStr(tmpds.getStringAt(k,&quot;AAC003&quot;),50,0);
						var AAB001 = formatStr(tmpds.getStringAt(k,&quot;AAB001&quot;),16,0);
						var AAE135 = formatStr(tmpds.getStringAt(k,&quot;AAE135&quot;),20,0);
						var AIC263 = tmpds.getStringAt(k,&quot;AIC263&quot;);//,16,1);
						var AAE010 = formatStr(tmpds.getStringAt(k,&quot;AAE010&quot;),40,0);
						var AAE007 = formatStr(tmpds.getStringAt(k,&quot;AAE007&quot;),6,0);
						var BIC006 = formatStr(tmpds.getStringAt(k,&quot;BIC006&quot;),100,0);
						var line = AAC001+&quot;&quot;+AAC003+&quot;&quot;+AAB001+&quot;&quot;+AAE135+&quot;&quot;+AIC263+&quot;&quot;+AAE010+&quot;&quot;+AAE007+&quot;&quot;+BIC006+&quot;\r\n&quot;;
						//java.lang.System.out.println(k+&quot;&quot;+line);
						fileStr += line;
					}					
					pub.EAFunc.WriteToFileEx(fileName,fileStr,true);  //生成本地文件,追加方式 

				}

				//pub.EAFunc.WriteToFileEx(fileName,fileStr,false);  //生成本地文件,覆盖方式

				//生成合计文件				
				var upwjmSum = upwjm.substring(0,upwjm.indexOf(&quot;.&quot;))+&quot;_SUM&quot;+upwjm.substring(upwjm.indexOf(&quot;.&quot;));   //临时保存到服务器本地的英文文件名
				var upwjmSum2 = upwjm.substring(0,upwjm.indexOf(&quot;.&quot;))+&quot;_合计&quot;+upwjm.substring(upwjm.indexOf(&quot;.&quot;)); //上传到FTP时的带中文文件名
				var fileStrSum = &quot;格式:文件名    分行名称    户数(人数)    金额    备注(原核对数)\r\n&quot;;
				fileStrSum += upwjm + &quot;    &quot; + sumHS + &quot;    &quot; + sumMny + &quot;    \r\n&quot;;
				fileStrSum += &quot;    合计    &quot;+ sumHS + &quot;    &quot; + sumMny + &quot;    \r\n&quot;;
				//var fileName2 = &quot;/u/dsdf/&quot;+kpdir+&quot;/&quot;+upwjmSum; 
				var fileName2 = &quot;/u/dsdf/&quot;+sicode+&quot;_&quot;+thisaccid+&quot;/&quot;+kpdir+&quot;/&quot;+upwjmSum; 

				pub.EAFunc.WriteToFileEx(fileName2,fileStrSum,false);  //生成本地文件,覆盖方式

				var lognote = &quot;生成拷盘文件：&quot;+fileName2;
				sql = &quot;insert into oplog(grdid,type,action,opdat,opusr,opusrnam,note,acc,syt,org)
					values(&apos;RJZ_YWKP&apos;,&apos;发放拷盘&apos;,&apos;生成&apos;,sysdate,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;)&quot;.format([usrid,czyxm,lognote,thisaccid,&quot;SBCW&quot;,thisorgid]);
				db.ExcecutSQL(sql);

				//获取拷盘流水号				
				var kplsh = db.GetSQL(&quot;select seq_kplsh.nextval from dual&quot;);
				//更新拷盘标志信息
				sql = &quot;update ac95 set BAE421=&apos;&quot;+kplsh+&quot;&apos;,BAC001=&apos;&quot;+upwjm+&quot;&apos;,BAC002=to_date(&apos;&quot;+kpdat+&quot;&apos;,&apos;yyyy-mm-dd&apos;),AAE036=sysdate,AAE011=&apos;&quot;+czyxm+&quot;&apos; 
					where AAE208=&apos;&quot;+sfny+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and aaa027=(select tcqbm from cw_sbsb where org=&apos;&quot;+sbh+&quot;&apos;)
					  and BAE074 in (select djh from &quot;+tempTable+&quot; where zfbz=&apos;0&apos; and sfny=&apos;&quot;+sfny+&quot;&apos; and xzlx=&apos;&quot;+xzlx+&quot;&apos; and dfyhbm=&apos;&quot;+dfyhbm+&quot;&apos; and ffpc=&apos;&quot;+ffpc+&quot;&apos;)
					  and aae140=&apos;&quot;+xzlx+&quot;&apos;
					  and BAE419=&apos;&quot;+dfyhbm+&quot;&apos;
					  and BAE415=&apos;&quot;+ffpc+&quot;&apos;&quot;;
				db.ExcecutSQL(sql);	

//				//暂时不上传FTP
				//上传FTP
//				var eaftp = new pub.EAFtpClient2();
//				eaftp.ftpClient = new ftppack.FTPClient();
//				eaftp.ftpClient.setControlEncoding(&quot;UTF-8&quot;); //处理文件名是中文乱码问题
//				eaftp.ftpClient.connect(ftpip);
//				eaftp.ftpClient.login(ftpuser, ftppasswd);
				//eaftp.connectServer(ftpip,ftpuser,ftppasswd);  //登录FTP//不用，采用上面几行代码登录代替

//				var localFile = new java.io.File(fileName);
//				eaftp.ftpClient.changeWorkingDirectory(ftploginpath + &quot;/&quot; + ftpuppath);
//				var inputStream = new java.io.FileInputStream(localFile);
//				var b = eaftp.ftpClient.storeFile(upwjm,inputStream);
//				if (b == true) { //上传成功


					//上传合计文件
//					var localFile2 = new java.io.File(fileName2);
//					var inputStream2 = new java.io.FileInputStream(localFile2);
//					eaftp.ftpClient.storeFile(upwjmSum2,inputStream2);
					//删除本地生成的临时文件
					//var f = new java.io.File(fileName2);
					//if(f.exists()) f.delete();
//				}
//				eaftp.closeConnect();

				//删除本地生成的临时文件
				//var f = new java.io.File(fileName);
				//if(f.exists()) f.delete();	
			}
			else {
				db.Rollback();
				return &quot;查询cw_dfdsb出错！！&quot;;
			}

		}

		dropTempTable(db,tempTable); //删除临时表

		db.Commit();
		//db.Rollback();

		return &quot;1~&quot;+tips; //&quot;生成拷盘文件成功！&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return &quot;生成拷盘出错:&quot;+e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

//补全长度空格
function formatStr(str,len,bef)
{
	str = &quot;&quot;+str;
	var slen = getWordCount(str);//str.length();
	if (slen &gt; len) {
		//return str.substring(0,len); //2017-01-16修正
		var retstr = pub.EADbTool.GetSQL(&quot;select substrb(&apos;&quot;+str+&quot;&apos;,0,&quot;+len+&quot;) from dual&quot;);
		//throw new Exception(&quot;str=&quot;+str+&quot;,slen=&quot;+slen+&quot;,len=&quot;+len+&quot;,retstr=&quot;+retstr);
		return retstr;
	}
	var trim = &quot;&quot;;
	for (var i=0;i&lt;(len-slen);i++) {
		trim += &quot; &quot;;
	}
	if (bef == 1) return trim + str;
	else return str + trim;
}

function getWordCount(s)
{
        var length = 0;
        for(var i = 0; i &lt; s.length(); i++) {
		var ascii = java.lang.Character.codePointAt(s, i);
		if(ascii &gt;= 0 && ascii &lt;=255)
			length++;
		else
			length += 2;
	}
        return length;
}

//文件名
function getKPFileName(xzlx,filename)
{
	var retFileName = &quot;&quot;;
	if (xzlx == &quot;21&quot; && filename.indexOf(&quot;sy_&quot;) == -1) retFileName = &quot;sy_&quot; + filename;
	else if (xzlx == &quot;12&quot; && filename.indexOf(&quot;JB_&quot;) == -1) retFileName = &quot;JB_&quot; + filename;
	else if (xzlx == &quot;13&quot; && filename.indexOf(&quot;JB_&quot;) == -1) retFileName = &quot;JB_&quot; + filename;
	else if (xzlx == &quot;41&quot; && filename.indexOf(&quot;gs_&quot;) == -1) retFileName = &quot;gs_&quot; + filename;
	else if (xzlx == &quot;51&quot; && filename.indexOf(&quot;syu_&quot;) == -1) retFileName = &quot;syu_&quot; + filename;
	else if (xzlx == &quot;991&quot; && filename.indexOf(&quot;js_&quot;) == -1) retFileName = &quot;js_&quot; + filename;
	else if (xzlx == &quot;992&quot; && filename.indexOf(&quot;jzg_&quot;) == -1) retFileName = &quot;jzg_&quot; + filename;
	else if (xzlx == &quot;993&quot; && filename.indexOf(&quot;jsc_&quot;) == -1) retFileName = &quot;jsc_&quot; + filename;
	else if (xzlx == &quot;994&quot; && filename.indexOf(&quot;lxjt_&quot;) == -1) retFileName = &quot;lxjt_&quot; + filename;
	else if (xzlx == &quot;995&quot; && filename.indexOf(&quot;lgmf_&quot;) == -1) retFileName = &quot;lgmf_&quot; + filename;
	else if (xzlx == &quot;996&quot; && filename.indexOf(&quot;jlg_&quot;) == -1) retFileName = &quot;jlg_&quot; + filename;
	else if (xzlx == &quot;997&quot; && filename.indexOf(&quot;hn_&quot;) == -1) retFileName = &quot;hn_&quot; + filename;
	else if (xzlx == &quot;998&quot; && filename.indexOf(&quot;zydg_&quot;) == -1) retFileName = &quot;zydg_&quot; + filename;
	else if (xzlx == &quot;11&quot;) retFileName = filename;

	return retFileName;
}


//银行回盘处理
function ImportYHFile()
{
	var ftpcfg = &quot;&quot;;
	var filename = &quot;&quot;;
	var host = &quot;&quot;;
	var user = &quot;&quot;;
	var userpwd = &quot;&quot;;
	var path = &quot;&quot;;

	var db = null;
	var sql = &quot;&quot;;
	var line = &quot;&quot;;
	var jkret = &quot;&quot;;
	var rowcnt = 0;
	var failecnt = 0;
	var process = 0;
	var sbh = thisorgid;
	var yszlCheck = 0;

	try {
		db = new pub.EADatabase();

		notify(jobseqid,0,&quot;准备导入银行回盘文件...&quot;,&quot;run&quot;);//名称为空表示只要更新进度

		var txt = &quot;&quot;;

		if (typ == 1) {
			ftpcfg = ftpinfo.split(&quot;,&quot;);
			filename = ftpcfg[0];
			host = ftpcfg[1];
			user = ftpcfg[2];
			userpwd = ftpcfg[3];
			path = ftpcfg[4];

			var eaftp = new pub.EAFtpClient2();
			eaftp.connectServer(host,user,userpwd);  //登录FTP
			eaftp.ftpClient.changeWorkingDirectory(path);

			//保存回盘文件的目录
			var sicode = pub.EADbTool.GetSQL(&quot;select tcqbm from cw_sbsb where sbh=&apos;&quot;+thisorgid+&quot;&apos;&quot;);		
			var destFile = &quot;/u/dsdf/&quot;+sicode+&quot;_&quot;+thisaccid+&quot;/return/&quot;+filename;  //回盘文件保存的目录	
			var tmpfile = new java.io.File(&quot;/u/dsdf/&quot;+sicode+&quot;_&quot;+thisaccid+&quot;/return/&quot;);
	      		if (!tmpfile.exists()) {
				tmpfile.mkdirs(); //目录不存在则创建
			}

			var b = eaftp.download(path+&quot;/&quot;+filename,destFile);
			eaftp.closeConnect();
			if (b == true) {
				txt = pub.EAFunc.readFile(destFile);
			}
		}
		else if (typ == 2) {
			txt = text;
			filename = hpfilename;
			filename = db.GetSQL(&quot;select substr(&apos;&quot;+hpfilename+&quot;&apos;,instr(&apos;&quot;+hpfilename+&quot;&apos;,&apos;\\&apos;,-1)+1) from dual&quot;);
		}
		if (txt == &quot;&quot;) {
			if (notify(jobseqid,100,&quot;回盘失败！导入文件为空！&quot;,&quot;end&quot;)==&quot;ERROR&quot;) throw new Exception ( &quot;进程已经中断&quot; );
			return &quot;导入文件为空！&quot;;
		}

		//判断回盘文件名	
		sql = &quot;SELECT INSTRB(lower(&apos;&quot;+filename +&quot;&apos;), &apos;succ_&apos;) + INSTRB(lower(&apos;&quot;+filename +&quot;&apos;), &apos;fail_&apos;) + INSTRB(lower(&apos;&quot;+filename +&quot;&apos;), &apos;return_&apos;) fileret FROM DUAL&quot;;	
		var fileRet = db.GetSQL(sql);
		if( fileRet == 0){
			db.Rollback();
			notify(jobseqid,100,&quot;回盘失败！回盘导入文件[&quot;+filename+&quot;]不正确,必须包含前缀：succ_ / fail_ / return_&quot;,&quot;end&quot;);
			return &quot;回盘导入文件名不正确,文件名:&quot;+filename+&quot;;必须包含前缀：succ_ / fail_ / return_&quot;;
		}		
		var sr = new java.io.StringReader(txt);
		var br = new java.io.BufferedReader(sr);
		var sr2 = new java.io.StringReader(txt);
		var br2 = new java.io.BufferedReader(sr2);

		var fileDataCount = 0;
		while( br2.readLine() != null) { //计算文件总行数
			fileDataCount ++;
		}
		br2.close();

		sql = &quot;select tcqbm from cw_sbsb where sbh=&apos;&quot;+sbh+&quot;&apos;&quot;;
		var tcqbm = db.GetSQL(sql);

		//获取社保流水号
		var service = new SBCW_sbcwService();
		var sblsh = service.genSiSeq(tcqbm);		
		var kmbh = &quot;&quot;;

		while( (line=br.readLine()) != null) { //读取文本行记录
			if (line == &quot;&quot;) continue;
			rowcnt ++;
			var linelength = 1*db.GetSQL(&quot;select lengthb(&apos;&quot;+line+&quot;&apos;) from dual&quot;);               /*每行长度*/
			if(linelength != 435) {
				db.Rollback();
				notify(jobseqid,100,&quot;回盘文件导入失败！文件[&quot;+filename+&quot;]第&quot;+rowcnt+&quot;行长度不正确，正确长度435，实际长度&quot;+linelength,&quot;run&quot;);//名称为空表示只要更新进度
				return &quot;文件第&quot;+rowcnt+&quot;行长度不正确，正确长度435，实际长度&quot;+linelength;				
			}			
			var grbh = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,0,16),&quot; &quot;,&quot;&quot;);		/*个人编号 16*/
			var xm = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,16,50),&quot; &quot;,&quot;&quot;);		/*姓名 50*/
			var dwbh = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,66,16),&quot; &quot;,&quot;&quot;);	/*单位编号 16*/
			var sfzh = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,82,20),&quot; &quot;,&quot;&quot;);	/*身份证 20*/
			var dfje = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,102,16),&quot; &quot;,&quot;&quot;);	/*代发金额 16*/
			var dfzh = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,118,40),&quot; &quot;,&quot;&quot;);	/*代发账号 40*/
			var zcode = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,158,6),&quot; &quot;,&quot;&quot;);	/*邮政编码 6*/
			var addr = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,164,100),&quot; &quot;,&quot;&quot;);	/*通信地址 单据号||批次号 100*/
//			原方法截取不正确，换成oracle 方法截取
			sql = &quot;SELECT SUBSTRB(&apos;&quot;+addr+&quot;&apos;, 1, INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;||&apos;) - 1) DJH,SUBSTRB(&apos;&quot;+addr+&quot;&apos;,INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;||&apos;, 1) + 2,
				INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;||&apos;, 1, 2) - INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;||&apos;, 1, 1) - 2) PCH FROM DUAL&quot;;
			//20161209改为下划线处理
			//20170117 lyh modify 增加序号baz610截取数据  修改的文件只有__没有||
			var djh = &quot;&quot;;
			var pch = &quot;&quot;;
			var baz610 = &quot;&quot;;
			//2017-01-20 modify by flm兼容新旧格式
			if (addr.indexOf(&quot;||&quot;) &gt; -1) addr = pub.EAFunc.Replace(addr,&quot;||&quot;,&quot;__&quot;);  //将旧格式的||替换为__
			if (addr.indexOf(&quot;__&quot;) &gt; -1) {
//				sql = &quot;SELECT SUBSTRB(&apos;&quot;+addr+&quot;&apos;, 1, INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;) - 1) DJH,
//				              SUBSTRB(&apos;&quot;+addr+&quot;&apos;,INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;, 1) + 2,INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;, 1, 2) - INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;, 1, 1) - 2) PCH,
//				              SUBSTRB(&apos;&quot;+addr+&quot;&apos;,INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;, 1,2) + 2,INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;, 1, 3) - INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;, 1, 2) - 2) BAZ610
//				         FROM DUAL&quot;;
				sql = &quot;SELECT SUBSTRB(&apos;&quot;+addr+&quot;&apos;, 1, INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;) - 1) DJH,
					              SUBSTRB(&apos;&quot;+addr+&quot;&apos;,INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;, 1) + 2,INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;, 1, 2) - INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;, 1, 1) - 2) PCH,
					              SUBSTRB(&apos;&quot;+addr+&quot;&apos;,INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;, 1,2) + 2 ) BAZ610
					         FROM DUAL&quot;;
				var dj_ds = db.QuerySQL(sql);
				djh = dj_ds.getStringAt(0,&quot;DJH&quot;).trim();
				pch = dj_ds.getStringAt(0,&quot;PCH&quot;).trim();
				baz610 = dj_ds.getStringAt(0,&quot;BAZ610&quot;).trim();
			}

			//20161223 lyh add 不存在第二个&apos;__&apos;或&apos;||&apos;则会出现pch为空的可能性 故处理从第一个&apos;__&apos;或&apos;||&apos; 截取到最后位置
			//20170117 lyh modify 增加序号baz610截取数据  修改的文件只有__没有||
			if(pch == &quot;&quot; || baz610 == &quot;&quot;){
				sql = &quot;SELECT SUBSTRB(&apos;&quot;+addr+&quot;&apos;, 1, INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;||&apos;) - 1) DJH,
					      SUBSTRB(&apos;&quot;+addr+&quot;&apos;,INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;||&apos;, 1) + 2) PCH 
					 FROM DUAL&quot;;
				if (addr.indexOf(&quot;__&quot;) &gt; -1) {
					sql = &quot;SELECT SUBSTRB(&apos;&quot;+addr+&quot;&apos;, 1, INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;) - 1) DJH,
					              SUBSTRB(&apos;&quot;+addr+&quot;&apos;,INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;, 1) + 2,INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;, 1, 2) - INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;, 1, 1) - 2) PCH,
					              SUBSTRB(&apos;&quot;+addr+&quot;&apos;,INSTRB(&apos;&quot;+addr+&quot;&apos;, &apos;__&apos;, 1,2) + 2 ) BAZ610
					         FROM DUAL&quot;;
					var dj_ds = db.QuerySQL(sql);
					djh = dj_ds.getStringAt(0,&quot;DJH&quot;).trim();
					pch = dj_ds.getStringAt(0,&quot;PCH&quot;).trim();
					baz610 = dj_ds.getStringAt(0,&quot;BAZ610&quot;).trim();
				}

			}

//			var djh = pub.EAFunc.Replace(pub.EAFunc.bSubstring(addr,0,addr.indexOf(&quot;||&quot;)),&quot; &quot;,&quot;&quot;);
//			var pch = pub.EAFunc.Replace(pub.EAFunc.bSubstring(addr,addr.indexOf(&quot;||&quot;)+2,addr.length()),&quot; &quot;,&quot;&quot;);
			var cgbz = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,264,1),&quot; &quot;,&quot;&quot;);	/*成功发放标志 1*/
			var sbbz = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,265,80),&quot; &quot;,&quot;&quot;);	/*不成功发放备注 80*/
			var zqzh = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,345,40),&quot; &quot;,&quot;&quot;);	/*返回正确帐号 40*/
			var zqyhm = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,385,50),&quot; &quot;,&quot;&quot;);	/*返回正确户名 50*/

			process = db.GetSQL(&quot;select round(&quot;+rowcnt+&quot; / &quot;+fileDataCount+&quot; * 100,0)-1 from dual&quot;);

			if (rowcnt % 500 == 0) {
				notify(jobseqid,process,&quot;正在读取回盘文件，行号：&quot;+rowcnt+&quot;...&quot;,&quot;run&quot;);//名称为空表示只要更新进度				
			}

			if(cgbz == &quot;&quot; || cgbz == null) {
				db.Rollback();
				notify(jobseqid,100,&quot;回盘文件导入失败！发放标志不能为空！出错行号:&quot;+rowcnt+&quot;.个人编号：&quot;+grbh+&quot;,姓名：&quot;+xm+&quot;,金额：&quot;+dfje+&quot;.&quot;,&quot;end&quot;);//名称为空表示只要更新进度
				return &quot;回盘文件导入失败！发放标志不能为空！出错行号:&quot;+rowcnt+&quot;.个人编号：&quot;+grbh+&quot;,姓名：&quot;+xm+&quot;,金额：&quot;+dfje+&quot;.&quot;;				
			}

			//是否启用银社直连判断
			if (yszlCheck == 0) {
				yszlCheck = 1;
				sql = &quot;select distinct BAE419 from ac95 where zth=&apos;&quot;+zth+&quot;&apos; and aaa027=&apos;&quot;+tcqbm+&quot;&apos; and AAB001=&apos;&quot;+dwbh+&quot;&apos; and BAE074=&apos;&quot;+djh+&quot;&apos; and BAE415=&apos;&quot;+pch+&quot;&apos; and (aae008=&apos;&quot;+hpbank+&quot;&apos; or BAE419=&apos;&quot;+hpbank+&quot;&apos;)&quot;;
				var ac95ds = db.QuerySQL(sql);
				if (ac95ds.getRowCount() &gt; 0) {
					var bae419 = ac95ds.getStringAt(0,&quot;BAE419&quot;); //单位银行编码
					var sipub = new SBCW_PUBFUNC();
					var chkret = sipub.checkYSZLStatusByYH(db,sbh,zth,bae419);
					if (chkret != &quot;0&quot;) { //已启用
						if (chkret != &quot;&quot;) {
							var rst = 1*db.GetSQL(&quot;select to_date(&apos;&quot;+hpdat+&quot;&apos;,&apos;yyyy-mm-dd&apos;) - to_date(&apos;&quot;+chkret+&quot;&apos;,&apos;yyyy-mm-dd&apos;) from dual&quot;); 
							if (rst &gt;= 0) {
								var yhmc = db.GetSQL(&quot;select fullname from V_CW_YHZL_ZF where sbh=&apos;&quot;+sbh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and id=&apos;&quot;+bae419+&quot;&apos;&quot;);
								notify(jobseqid,100,&quot;导入回盘失败！【&quot;+yhmc+&quot;】社银直连已启用，请到社银直连模块操作！出错行号:&quot;+rowcnt+&quot;.&quot;,&quot;end&quot;);
								return &quot;导入回盘失败！【&quot;+yhmc+&quot;】社银直连已启用，请到社银直连模块操作！&quot;;								
							}										
						}
					}

				}
			}

			sql = &quot;select * from ac95 where zth=&apos;&quot;+zth+&quot;&apos; and aaa027=&apos;&quot;+tcqbm+&quot;&apos; and AAC001=&apos;&quot;+grbh+&quot;&apos; and AAB001=&apos;&quot;+dwbh+&quot;&apos; and BAE074=&apos;&quot;+djh+&quot;&apos; and BAE415=&apos;&quot;+pch+&quot;&apos; 
				and (aae008=&apos;&quot;+hpbank+&quot;&apos; or BAE419=&apos;&quot;+hpbank+&quot;&apos;) and BAZ901 is not null and BAE036 is not null&quot;;
			if (sfzh != &quot;&quot;) sql += &quot; and AAE135=&apos;&quot;+sfzh+&quot;&apos;&quot;;
			var hpds = db.QuerySQL(sql);
			if (hpds.getRowCount() &gt; 0) { //已回过盘不允许重复
				db.Rollback();
				notify(jobseqid,100,&quot;已经回盘，不允许重复回盘！出错行号:&quot;+rowcnt+&quot;.&quot;,&quot;end&quot;);//名称为空表示只要更新进度
				return &quot;已经回盘，不允许重复回盘！出错行号:&quot;+rowcnt+&quot;.&quot;;
			}

			//写入导入中间表
			//20170117 lyh modify 增加序号baz610截取数据  BAZ610写入SI_YHRETURN_TEMP
			//旧文件格式有冲突，暂时不写baz610 20170119
			//2017-03-01修改写入BAZ610 by flm
			sql = &quot;insert into SI_YHRETURN_TEMP(XH,GRBH,XM,DWBH,SFZH,DFJE,DFZH,ZCODE,ADDR,DJH,PCH,CGBZ,SBBZ,ZQZH,ZQYHM,JOBID,FILENAME,baz610)
				values(&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;)&quot;
				.format([&quot;&quot;+rowcnt,grbh,xm,dwbh,sfzh,dfje,dfzh,zcode,addr,djh,pch,cgbz,sbbz,zqzh,zqyhm,jobid,filename,baz610]);			
			db.ExcecutSQL(sql);


		} //end while

		if (notify(jobseqid,100,&quot;回盘文件导入成功！数据总笔数：&quot;+rowcnt,&quot;end&quot;)==&quot;ERROR&quot;) throw new Exception ( &quot;进程已经中断&quot; );
		br.close();
		db.Commit();
		//return &quot;银行回盘导入成功！\n回盘文件：&quot;+filename+&quot;\n总笔数：&quot;+rowcnt+&quot;\n失败笔数：&quot;+failecnt+&quot;。&quot;;
		return jobid;

	}
	catch (e) {
		notify(jobseqid,100,e.toString()+&quot;  出错行=&quot; + rowcnt,&quot;error&quot;);
		if (db != null) db.Rollback();
		return e.toString() + &quot;  出错行=&quot; + rowcnt;
	}
	finally {
		if (db != null) db.Close();
	}
}

//是否启用银社直连判断
function checkYSZL()
{	
	var db = null;
	try {
		db = new pub.EADatabase();

		var sipub = new SBCW_PUBFUNC();
		var chkret = sipub.checkYSZLStatusByYH(db,sbh,zth,yhbm);
		if (chkret != &quot;0&quot;) { //已启用
			if (chkret != &quot;&quot;) {
				var yhmc = db.GetSQL(&quot;select fullname from V_CW_YHZL_ZF where sbh=&apos;&quot;+sbh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and id=&apos;&quot;+yhbm+&quot;&apos;&quot;);
				return &quot;【&quot;+yhmc+&quot;】社银直连已启用，请到社银直连模块操作！&quot;;				
			}
		}
		return &quot;ok&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

//下载拷盘文件保存到本地P目录
function downLoadFile()
{
	//获取社保所统筹区代码
	//服务器保存本地文件目录改为：/u/dsdf/统筹区代码_帐套号/yyyymm/文件名
	var sicode = pub.EADbTool.GetSQL(&quot;select tcqbm from cw_sbsb where sbh=&apos;&quot;+thisorgid+&quot;&apos;&quot;);
	var path = &quot;/u/dsdf/&quot;+sicode+&quot;_&quot;+thisaccid+&quot;/&quot;+mmdir+&quot;/&quot;;
	var txt = pub.EAFunc.readFile(path+filename);
	return txt;
}
//作为.sp服务时的入口
//预定义变量：request,response
function Response()
{
	var itemclass = request.getParameter(&quot;filename&quot;);
  	response.setHeader(&quot;File&quot;,filename);
        response.setHeader(&quot;Content-Disposition&quot;, &quot;attachment;filename=&quot;+filename);
	var path = &quot;/u/dsdf/&quot;+mmdir;
	var txt = pub.EAFunc.readFile(path+filename);
	pub.EAZip.compressStringToStream(txt,response.getOutputStream());
}

//后台操作序号jobid
function getSysDate()
{
	var sql = &quot;select tcqbm from cw_sbsb where sbh=&apos;&quot;+thisorgid+&quot;&apos;&quot;;
	var tcqbm = pub.EADbTool.GetSQL(sql);	
	var dt = pub.EADbTool.GetSQL(&quot;select to_char(sysdate,&apos;yyyymmddhh24miss&apos;) from dual&quot;);
	var seqno = tcqbm+&quot;_&quot;+zth+&quot;_&quot;+dt;
	return seqno;
}


//回盘加入进度条提示实现 
function genbatch()
{
	var db = null;
	var jobseqid  = &quot;FFHP&quot;+jobid;
	try {
		//db = new pub.EADatabase();	// 如果连接到其他数据库, new pubpack.EADatabase(“dbname”)

		var tim = new timepack.EARunOSTimer(); 
		tim.init(jobseqid,jobnam,&quot;SBCW&quot;,&quot;RJZ_YWKP2&quot;,&quot;ImportYHFile&quot;,&quot;jobid=&quot;+jobid+&quot;&thisorgid=&quot;+thisorgid+&quot;&jobseqid=&quot;+jobseqid 
			+&quot;&thisaccid=&quot;+thisaccid+&quot;&typ=&quot;+typ+&quot;&ftpinfo=&quot;+ftpinfo+&quot;&hpdat=&quot;+hpdat+&quot;&hpbank=&quot;+hpbank+&quot;&zth=&quot;+zth
			+&quot;&usrid=&quot;+usrid+&quot;&czyxm=&quot;+czyxm+&quot;&hpfilename=&quot;+hpfilename+&quot;&text=&quot;+text);
	}
	catch ( ee ) {
		throw new pub.EAException ( ee.toString() );
	}
	finally {
		if (db!=null) db.Close();
	}
	return jobseqid  ;// 返回后台分配的作业编号

}

// 通知外部当前的运行情况
function notify(jobseqid,percent,note,stat)
{
	var db = null;
	if ( percent &lt; 0 ) return &quot;&quot;;
	try{
		db = new pub.EADatabase();
		note = pub.EAFunc.Replace( note, &quot;&apos;&quot;,&quot;‘&quot; );
		if(note==&quot;&quot;){
			db.ExcecutSQL(&quot;update RunOSTimer set percent=&quot;+(percent) +&quot;,stat=&apos;&quot;+stat+&quot;&apos; where id=&apos;&quot;+jobseqid+&quot;&apos;&quot;);
		}
		else {
			db.ExcecutSQL(&quot;update RunOSTimer set percent=&quot;+(percent) +&quot;,percentnote=&apos;&quot;+note+&quot;&apos;,stat=&apos;&quot;+stat+&quot;&apos; where id=&apos;&quot;+jobseqid+&quot;&apos;&quot;);
			db.ExcecutSQL(&quot;insert into RunOSTimerDTL(id,name) values(&apos;&quot;+jobseqid+&quot;&apos;,&apos;&quot;+note+&quot;&apos;)&quot; );
		}
		db.Commit();
	}
	catch ( e ) {
		//pubpack.EAFunc.Log( e.toString() );
		db.Rollback();
		return &quot;ERROR&quot; ;
	}
	finally {
		if (db!=null) db.Close();
	}
	return &quot;OK&quot;;
}

//分页查询
function queryXml()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		var mwsql = new servletpack.MWSQL();
		var sql = mwsql.GetQuerySQL(request,db,thissytid ,thisgrdid ,dsid);
		//param = &quot;SFNY=&quot;+sfny+&quot;&DJH=&quot;+djh+&quot;&XZLX=&quot;+xzlx+&quot;&DFYHBM=&quot;+dfyh+&quot;&FILTERSTR=&quot;;
		if(cgbz == &quot;0&quot;) {
			sql = &quot;select rownum rn,a.* from (
					select AAC001,AAC003,AAB001,AAE135,AIC263,AAE009,AAE010,AAE007,BIC006,MINAAE002,MAXAAE002,months,
					       AAE208,BAE074,AAE076,siseqno,kmbh,rq,zy,BAZ901,BAZ902,BAC003
					from (
					select   AAC001,nvl(AAE009,AAC003) AAC003,AAB001,AAE135,AIC263,AAE009,AAE010,AAE007,BIC006,
					  min(substr(AAE002,0,6)) MINAAE002,max(substr(AAE002,8,13)) MAXAAE002,
					  nvl(max(substr(AAE002,8,13)),max(substr(AAE002,0,6)))-nvl(min(substr(AAE002,0,6)),0)+1 months,
					  AAE208,BAE074,AAE076,BAZ901,
					  nvl(a.BAZ902,(select aaa103 from aa10 b where a.BAZ901=b.aaa102 and b.aaa100=&apos;BAZ901&apos; )) BAZ902,
					  BAC003,BAE415,BAE419,c.siseqno,b.rq,c.zy,c.kmbh
					from ac95 a,v_si_djb_tmp b,cw_yhrjzb c
					where a.AAE208=&apos;[%SFNY]&apos;
					  and a.BAE074=&apos;[%DJH]&apos;
					  and a.aae140=&apos;[%XZLX]&apos;
					  and a.BAE419=&apos;[%DFYHBM]&apos; 
					  [%FILTERSTR] [%BZSTR]
					  and a.zth = b.zth(+)
					  and a.prc_out_xh = b.xh(+)
					  and c.sbh(+) = b.sbh and c.zth(+) = b.zth
					  and c.siseqno(+) = b.lsh
					group by AAC001,nvl(AAE009,AAC003),AAB001,AAE135,AIC263,AAE009,AAE010,AAE007,BIC006,
					  AAE208,BAE074,AAE076,BAZ901,BAZ902,BAC003,BAE415,BAE419,prc_out_xh,prc_out_code,a.zth,
					  b.zth,c.zth,b.sbh,c.sbh,b.xh,c.siseqno,b.lsh,b.rq,c.kmbh,c.zy
					) 
					order by BAE415,BAE419,AAB001,AAC001
					) a&quot;;		
		}
		sql = pub.EAFunc.Replace(sql,&quot;[%SFNY]&quot;,SFNY);
		sql = pub.EAFunc.Replace(sql,&quot;[%DJH]&quot;,DJH);
		sql = pub.EAFunc.Replace(sql,&quot;[%XZLX]&quot;,XZLX);
		sql = pub.EAFunc.Replace(sql,&quot;[%DFYHBM]&quot;,DFYHBM);
		sql = pub.EAFunc.Replace(sql,&quot;[%FILTERSTR]&quot;,FILTERSTR);

		if(cgbz == &quot;0&quot;) {//查询失败的
			sql = pub.EAFunc.Replace(sql,&quot;[%BZSTR]&quot;,&quot; and baz901 not in (&apos;0&apos;,&apos;0000&apos;) and baz901 is not null&quot;);
		}	
		else {
			sql = pub.EAFunc.Replace(sql,&quot;[%BZSTR]&quot;,&quot;&quot;);
		}	

		sql = &quot;select * from ( &quot; + sql + &quot; ) where rn&gt;(&quot;+pageno+&quot;-1)*&quot;+pagerows+&quot; and rn&lt;=&quot;+pageno+&quot;*&quot;+pagerows;

		var ds = db.QuerySQL(sql);
		return ds.GetXml();

	}
	catch(e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}


//分页取总行数~总金额
function queryRowCount()
{	
	var db = null;
	try {
		db = new pub.EADatabase();
		var mwsql = new servletpack.MWSQL();
		var sql = mwsql.GetQuerySQL(request,db,thissytid ,thisgrdid ,dsid);
		//param = &quot;SFNY=&quot;+sfny+&quot;&DJH=&quot;+djh+&quot;&XZLX=&quot;+xzlx+&quot;&DFYHBM=&quot;+dfyh+&quot;&FILTERSTR=&quot;;
		sql = pub.EAFunc.Replace(sql,&quot;[%SFNY]&quot;,SFNY);
		sql = pub.EAFunc.Replace(sql,&quot;[%DJH]&quot;,DJH);
		sql = pub.EAFunc.Replace(sql,&quot;[%XZLX]&quot;,XZLX);
		sql = pub.EAFunc.Replace(sql,&quot;[%DFYHBM]&quot;,DFYHBM);
		sql = pub.EAFunc.Replace(sql,&quot;[%FILTERSTR]&quot;,FILTERSTR);
		if(cgbz == &quot;0&quot;) {//查询失败的
			sql = pub.EAFunc.Replace(sql,&quot;[%BZSTR]&quot;,&quot; and baz901 not in (&apos;0&apos;,&apos;0000&apos;) and baz901 is not null&quot;);
		}	
		else {
			sql = pub.EAFunc.Replace(sql,&quot;[%BZSTR]&quot;,&quot;&quot;);
		}		
		//var cnt = db.GetSQLRowCount(sql);

		sql = &quot;select count(*) cnt,sum(aic263) summny from ( &quot; + sql + &quot; )&quot;;
		var ds = db.QuerySQL(sql);
		var cnt = ds.getStringAt(0,&quot;CNT&quot;);
		var summny = ds.getStringAt(0,&quot;SUMMNY&quot;);
		return cnt+&quot;~&quot;+summny;
	}
	catch(e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}
