//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (row &gt;= _this.GetMainCellRangeRow1(sheet) && row &lt;= _this.GetMainCellRangeRow2(sheet)) {
		if (xzlx == &quot;&quot;) {
			alert(&quot;请先选择转出银行账户信息!&quot;);
			_this.SetCellText(sheet,row,col,oldvalue);
			return;
		}
		var zje = _this.GetCellText(sheet,6,13);
		_this.SetCellText(0,6,5,changeMoneyToChinese(zje));
		_this.SetCellShowText(0,6,13,&quot;￥&quot;+fmoney(zje,2));

		if (col == 6) { //拨款期限
			var bfqx = newvalue.split(&quot;-&quot;);
			if (bfqx.length == 1) {
				_this.SetCellText(sheet,row,16,bfqx[0]);
				_this.SetCellText(sheet,row,17,bfqx[0]);
			}
			else {
				_this.SetCellText(sheet,row,16,bfqx[0]);
				_this.SetCellText(sheet,row,17,bfqx[1]);
			}
		}
		if(col == 9) {
	            	   if (!LogicalValue(newvalue,&apos;integer&apos;)) {
	            	     	 alert(&quot;输入人数格式不正确&quot;);
	            	     	 _this.SetCellText(sheet,row,col,oldvalue);
	            	     	 return;
	            	   }    			
		}
            if(col == 11) {
            	   if (!LogicalValue(newvalue,&apos;number&apos;)) {
            	     	 alert(&quot;输入金额格式不正确&quot;);
            	     	 _this.SetCellText(sheet,row,col,oldvalue);
            	     	 return;
            	   }  	              		
            }	   		
	}
	else if (row == 2 && col == 4) {
		var rq = _this.GetCellText(sheet,2,4);
		var jbrq = rq.replace(/-/g,&quot;&quot;);
		_this.SetCellText(sheet,7,27,jbrq);
	}

}

