//鼠标左键点击
function _thisOnMouseDClick(sheet,row,col)
{
	//选择款项
	if (sheet == 0 && col == 1 && row &gt;= _this.GetMainCellRangeRow1(0) && row &lt;= _this.GetMainCellRangeRow2(0)) {
		if (xzlx == &quot;&quot;) {
			alert(&quot;请先选择转出银行账户信息!&quot;);
			return;
		}

		var retVal = window.showModalDialog(&quot;show.sp?grdid=SEL_RJZKX&quot;,&quot;&quot;,&quot;dialogWidth=800px;dialogHeight=600px&quot;);
		if (retVal != &quot;&quot; && retVal != &quot;undefined&quot; && retVal != undefined) {
			var kxinfo = retVal.split(&quot;~&quot;);
			_this.SetCellText(0,row,1,kxinfo[2]);
			_this.SetCellText(0,row,2,kxinfo[1]);
			_this.SetCellText(0,row,15,kxinfo[0]);

			_this.SetCellText(sheet,row,14,xzlx);
			_this.SetCellText(0,row,18,row - 7);
			_this.SetCellText(sheet,row,19,&quot;1&quot;);
			_this.SetCellText(sheet,row,20,AAE400);

		}
	}
}

