ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

//单据保存前
function fos_beforesave(eaContext)
//var eaContext=new pub.EAContext();
{
	var db = eaContext.getEADatabase();
	var thisorgid = eaContext.getOrgid();
	var thisaccid = eaContext.getAccid();
	var zth = thisaccid.substring(thisaccid.indexOf(thisorgid)+1);
	var hdrxml = eaContext.getXmlhdr();
	var hdrds = new pub.EAXmlDS(hdrxml);
	var zy = hdrds.getStringAt(0,&quot;JBRNOTE&quot;);
	if(zy == &quot;&quot;) {
		throw new Exception(&quot;保存失败，摘要不能为空&quot;);		
	}
	//更新AC95表的AAE400拨款单号
	var dtlxml = eaContext.getXmldtl();
	var dtlds = new pub.EAXmlDS(dtlxml);
	var num = 0;
	//检查明细数据
	for(var row = 0;row&lt;dtlds.getRowCount();row++){
		var dymc = dtlds.getStringAt(row,&quot;DYMC&quot;);
		var ssqx = dtlds.getStringAt(row,&quot;SSQX&quot;);
		var je = dtlds.getStringAt(row,&quot;AAE408&quot;);
		var rs = dtlds.getStringAt(row,&quot;AAE407&quot;);
		if(dymc == &quot;&quot; && ssqx == &quot;&quot; && je == &quot;&quot; && rs == &quot;&quot;) {
			num++;
		}
		else {
			if(dymc == &quot;&quot; || ssqx == &quot;&quot; || je == &quot;&quot;) {
				throw new Exception(&quot;保存失败，明细数据不完整&quot;);
			}
		}
	}  
	if(num == dtlds.getRowCount()) {
		throw new Exception(&quot;保存失败，明细数据不能为空&quot;);
	}
}

