ROW.GRDJSDS_VALUE:
var objParam;
var _stat = &quot;&quot;;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{

	_this.AutoFixScreenWidth();

	var statList = _this.CreateListValue();
	_this.SetListValue(statList,&quot;1&quot;,&quot;通过&quot;);
	_this.SetListValue(statList,&quot;0&quot;,&quot;不通过&quot;);

	_this.SetToComboBox(&quot;&quot;,sheet,10,2,statList);


	objParam = window.dialogArguments;
	_this.SetCellText(sheet,3,2,objParam.zfdws);
	_this.SetCellText(sheet,4,2,objParam.zfrs);
	_this.SetCellText(sheet,5,2,objParam.zfje);
	var dxje = convertCurrency(objParam.zfje);
	dxje = dxje.replace(&quot;人民币&quot;,&quot; &quot;);
	_this.SetCellText(sheet,6,2,dxje);

	var stat = objParam.stat;
	_sql.querytods(&quot;AEF3INFO&quot;,&quot;GUID=&quot;+objParam.guid);
	//_stat = _this.XMLDS_GetString(0,&quot;STAT&quot;);
	_stat = stat;
	if (stat == &quot;21&quot;) { //财务实付审核
		_this.SetCellText(0,1,1,&quot;社银直连批量支付――财务实付审核&quot;);
		_this.SetCellText(0,10,2,_this.XMLDS_GetString(0,&quot;CWSHSTAT1&quot;));
		_this.SetCellText(0,12,2,_this.XMLDS_GetString(0,&quot;CWSHNOTE1&quot;));
		_this.SetCellText(0,16,2,_this.XMLDS_GetString(0,&quot;CWSHTIME1&quot;));
	}
	else if (stat == &quot;31&quot;) { //财务主管审核
		_this.SetCellText(0,1,1,&quot;社银直连批量支付--财务主管审核&quot;);
		_this.SetCellText(0,10,2,_this.XMLDS_GetString(0,&quot;CWSHSTAT2&quot;));
		_this.SetCellText(0,12,2,_this.XMLDS_GetString(0,&quot;CWSHNOTE2&quot;));
		_this.SetCellText(0,16,2,_this.XMLDS_GetString(0,&quot;CWSHTIME2&quot;));
	}
}

//审核确认
function checkSubmit()
{

	//增加权限控制
	var author = 0;
	if (_stat == 21) { //财务实付审核
		author = invokeOSFunc(&quot;CheckZFSH1&quot;);
	}
	else if (_stat == 31) { //财务主管审核
		author = invokeOSFunc(&quot;CheckZFSH2&quot;);
	}
//	else if (_stat == 31) { //财务主管复核
//		author = invokeOSFunc(&quot;CheckZFSH3&quot;);
//	}
	if (author != 1) {
		alert(&quot;对不起,你没有该权限!&quot;);
		return;
	}

	if (!confirm(&quot;审核确认，是否继续？&quot;)) return;
	var sbh = G_ORGID;
	var zth = G_ACCID.replace(sbh,&quot;&quot;);
	var bkdjh = _this.GetCellText(0,4,2);
	var stat = objParam.stat;
	var chkstat = _this.GetCellText(0,10,2);
	var chknote = _this.GetCellText(0,12,2);
	var param = &quot;CHKSTAT=&quot;+chkstat+&quot;&CWSHNOTE=&quot;+chknote+&quot;&CHKMAN=&quot;+G_USRNAM+&quot;&GUID=&quot;+objParam.guid+&quot;&STAT=&quot;+stat+&quot;&sbh=&quot;+sbh+&quot;&zth=&quot;+zth+&quot;&bkdjh=&quot;+bkdjh;
	var ret = _this.invokeOSFunc(&quot;ZFSH&quot;,param);
	alert(ret);
	window.returnValue = &quot;1&quot;;
	window.close();

}

