//财务主管审核
function CheckZFSH3()
{
	return &quot;1&quot;;
}


//支付审核
function ZFSH()
{
	var db = null;
	var sql = &quot;&quot;;
	try{
		db = new pub.EADatabase();
		var mystat = STAT;
		var ywlx = db.GetSQL(&quot;select ywlx from aef3 where guid=&apos;&quot;+GUID+&quot;&apos;&quot;);

		if (STAT == &quot;21&quot;) { //财务实付审核
			if(CHKSTAT == &quot;0&quot;) {//不通过
				mystat = &quot;20&quot;;
			}
			sql = &quot;update aef3 set STAT=&apos;&quot;+mystat+&quot;&apos;,
					CWSHSTAT1=&apos;&quot;+CHKSTAT+&quot;&apos;,
					CWSHMAN1=&apos;&quot;+CHKMAN+&quot;&apos;,
					CWSHTIME1=sysdate,
					CWSHNOTE1=&apos;&quot;+CWSHNOTE+&quot;&apos;
					WHERE GUID = &apos;&quot;+GUID+&quot;&apos;&quot;;
			var cnt = db.ExcecutSQL(sql);
			if(cnt == 0) {
				db.Rollback();
				return &quot;财务实付审核失败，更新aef3表失败&quot;;
			}
			sql = &quot;update aef4 set  STAT=&apos;&quot;+CHKSTAT+&quot;&apos; where formguid=&apos;&quot;+GUID+&quot;&apos;&quot;;
			var cnt = db.ExcecutSQL(sql);
			if(cnt == 0) {
				db.Rollback();
				return &quot;财务实付审核失败，更新aef4表失败&quot;;
			}
			//判断不调用业务接口：YWXT_YHDM is null and YWXT_DRDM is null
			var sql = &quot;select count(*) from aa10 where aaa100=&apos;YWLX&apos; and aaa102 = &apos;&quot;+ywlx+&quot;&apos; and YWXT_YHDM is null and YWXT_DRDM is null&quot;;
			var ywbz = db.GetSQL(sql);			
			//手工录入的审批单不需要更新中间表
			if (ywbz == 0) {
				sql = &quot;update ac95 set bae116=&apos;&quot;+CHKSTAT+&quot;&apos;,BAE012=&apos;&quot;+CHKMAN+&quot;&apos;,bae034=sysdate WHERE AAE400 = &apos;&quot;+bkdjh+&quot;&apos;&quot;;	
				var cnt = db.ExcecutSQL(sql);
				if(cnt == 0) {
					db.Rollback();
					return &quot;财务实付审核失败，更新ac95表失败&quot;;
				}
				sql = &quot;update v_si_djb_tmp set bae116=&apos;&quot;+CHKSTAT+&quot;&apos;,BAE012=&apos;&quot;+CHKMAN+&quot;&apos;,bae034=sysdate 
					WHERE djh in (select min(bae074) from ac95 where aae400 = &apos;&quot;+bkdjh+&quot;&apos;)&quot;;
				var cnt = db.ExcecutSQL(sql);
				if(cnt == 0) {
					db.Rollback();
					return &quot;财务实付审核失败，更新si_djb_tmp表失败&quot;;
				}	
			}			

		}
		else if (STAT == &quot;31&quot;) { //财务主管审核
			if(CHKSTAT == &quot;0&quot;) {//不通过
				mystat = &quot;30&quot;;
			}		
			sql = &quot;update aef3 set STAT=&apos;&quot;+mystat+&quot;&apos;,
					CWSHSTAT2=&apos;&quot;+CHKSTAT+&quot;&apos;,
					CWSHMAN2=&apos;&quot;+CHKMAN+&quot;&apos;,
					CWSHTIME2=sysdate,
					CWSHNOTE2=&apos;&quot;+CWSHNOTE+&quot;&apos;
					WHERE GUID = &apos;&quot;+GUID+&quot;&apos;&quot;;
			var cnt = db.ExcecutSQL(sql);
			if(cnt == 0) {
				db.Rollback();
				return &quot;2、财务实付审核失败，更新aef3表失败&quot;;
			}
			sql = &quot;update aef4 set  STAT=&apos;&quot;+CHKSTAT+&quot;&apos; where formguid=&apos;&quot;+GUID+&quot;&apos;&quot;;
			var cnt = db.ExcecutSQL(sql);
			if(cnt == 0) {
				db.Rollback();
				return &quot;2、财务实付审核失败，更新aef4表失败&quot;;
			}
			//判断不调用业务接口：YWXT_YHDM is null and YWXT_DRDM is null
			var sql = &quot;select count(*) from aa10 where aaa100=&apos;YWLX&apos; and aaa102 = &apos;&quot;+ywlx+&quot;&apos; and YWXT_YHDM is null and YWXT_DRDM is null&quot;;
			var ywbz = db.GetSQL(sql);				
			//手工录入的审批单不需要更新中间表
			if (ywbz == 0) {
				sql = &quot;update ac95 set bae117=&apos;&quot;+CHKSTAT+&quot;&apos;,BAE013=&apos;&quot;+CHKMAN+&quot;&apos;,bae035=sysdate WHERE AAE400 = &apos;&quot;+bkdjh+&quot;&apos;&quot;;	
				var cnt = db.ExcecutSQL(sql);
				if(cnt == 0) {
					db.Rollback();
					return &quot;2、财务实付审核失败，更新ac95表失败&quot;;
				}
				sql = &quot;update v_si_djb_tmp set bae117=&apos;&quot;+CHKSTAT+&quot;&apos;,BAE013=&apos;&quot;+CHKMAN+&quot;&apos;,bae035=sysdate 
					WHERE djh in (select min(bae074) from ac95 where aae400 = &apos;&quot;+bkdjh+&quot;&apos;)&quot;;
				var cnt = db.ExcecutSQL(sql);
				if(cnt == 0) {
					db.Rollback();
					return &quot;2、财务实付审核失败，更新si_djb_tmp表失败&quot;;
				}
			}
		}
		else {
			db.Rollback();
			return &quot;财务实付审核失败,stat不正确&quot;;
		}// end if(stat == 2)	
		db.Commit();
		return &quot;审核成功!!&quot;;	
	}catch(e){
		if(db != null) db.Rollback();
		return e.toString();
	}finally{
		if(db != null) db.Close();
	}
}

