update aef3 set STAT=&apos;[%STAT]&apos;,
	CWSHSTAT1=&apos;[%CHKSTAT]&apos;,
	CWSHMAN1=&apos;[%CHKMAN]&apos;,
	CWSHTIME1=sysdate,
	CWSHNOTE1=&apos;[%CWSHNOTE]&apos;
where guid=&apos;[%GUID]&apos;;
update aef4 set STAT=&apos;[%CHKSTAT]&apos; where formguid=&apos;[%GUID]&apos;;
update ac95 set bae116=&apos;[%CHKSTAT]&apos; WHERE AAE400 = &apos;[%bkdjh]&apos;;

update aef3 set STAT=&apos;[%STAT]&apos;,
	CWSHSTAT2=&apos;[%CHKSTAT]&apos;,
	CWSHMAN2=&apos;[%CHKMAN]&apos;,
	CWSHTIME2=sysdate,
	CWSHNOTE2=&apos;[%CWSHNOTE]&apos;
where guid=&apos;[%GUID]&apos;;
update aef4 set  STAT=&apos;[%CHKSTAT]&apos; where formguid=&apos;[%GUID]&apos;
