//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	loadSvgForm();
	_this.ShowTabBar(1);
	_this.SetSheetName(0,&quot;审批流程&quot;);
	_this.SetSheetName(1,&quot;说明&quot;);
}


function loadSvgForm() 
{
	//window_onresize();

	_this.SetRedraw(0);

	var str = invokeOSFunc(&quot;getSvg&quot;,&quot;syt=&quot;+G_SYTID);
	_this.SetToSVGForm(&quot;_flow&quot;,0,-1,-1,str);  

  	setTimeout(&quot;WF_refresh()&quot;,1000);

	if ( _this.GetReleaseNo()&gt;&quot;5.15&quot;) {
		_this.ShowToolbar(0);
	}

	try{_this.SVGFORM_FixScreenWidth(&quot;_flow&quot;); }catch(e){}

   	_this.SetRedraw(1);

}

