			loc1= xml.indexOf(&quot;&lt;ROW &quot;,loc1+1);
			if ( loc1&lt; 0 ) break;
			var loc2= xml.indexOf(&quot;&gt;&quot;,loc1+3);
			var attr= xml.substring(loc1+4,loc2-1) ;

			var id =getXmlAttribute0(attr,&quot;id&quot;);// _this.XMLDS_GetString(i,&quot;O_ID&quot;);
			var url =getXmlAttribute0(attr,&quot;url&quot;);//_this.XMLDS_GetString(i,&quot;O_URL&quot;);
			if ( url!=&quot;&quot; ){
				var xmlgrdid= getXmlAttribute(url,&quot;grdid&quot;);
				var xmlprocessid= getXmlAttribute(url,&quot;stat&quot;);				
				var processid=xmlprocessid.split(&quot;,&quot;);////支持processid=,
				var zth = G_ACCID.replace(G_ORGID,&quot;&quot;);
				var cnt = invokeOSFunc(&quot;getProcessCnt&quot;,&quot;processid=&quot;+xmlprocessid+&quot;&logdat=&quot;+G_LOGDAT+&quot;&zth=&quot;+zth);;
				//判读是否没有权限
//				var usrrol = invokeOSFunc(&quot;getUsrRol&quot;,&quot;mwid=&quot;+xmlgrdid);
//				if (usrrol == 0) {
//					_this.XMLCTL_SetAttribe(&quot;_flow&quot;,id,&quot;url&quot;,&quot;&quot; );
//					_this.XMLCTL_SetAttribe(&quot;_flow&quot;,id,&quot;fill&quot;,&quot;#EEEEEE&quot; );
//					_this.XMLCTL_SetAttribe(&quot;_flow&quot;,id,&quot;font-color&quot;,&quot;#000000&quot; );
//				}

				if ( cnt &gt;0 ) {
					var x=_this.XMLCTL_GetAttribe(&quot;_flow&quot;,id,&quot;x&quot;);
					var y=_this.XMLCTL_GetAttribe(&quot;_flow&quot;,id,&quot;y&quot;);
					var svg=&quot;&lt;circle id=\&quot;circle_&quot;+id+&quot;\&quot; cx=\&quot;&quot;+x+&quot;\&quot; cy=\&quot;&quot;+y+&quot;\&quot; r=\&quot;9\&quot; stroke=\&quot;#FFFFFF\&quot; fill=\&quot;#FF0000\&quot; fillto=\&quot;\&quot; opacity=\&quot;1\&quot; stroke-dash=\&quot;1\&quot; 属性=\&quot;\&quot; font-color=\&quot;#FFFFFF\&quot; stroke-width=\&quot;1\&quot;&gt;4&lt;/circle&gt;&lt;rect id=\&quot;rect_&quot;+id+&quot;\&quot; x=\&quot;&quot;+(x-11)+&quot;\&quot; y=\&quot;&quot;+(y-11)+&quot;\&quot; width=\&quot;22\&quot; height=\&quot;22\&quot; round=\&quot;\&quot; stroke=\&quot;#FFFFFF\&quot; fill=\&quot;#FFFFFF\&quot; fillto=\&quot;\&quot; opacity=\&quot;0\&quot; stroke-dash=\&quot;1\&quot; font-size=\&quot;14\&quot; font-color=\&quot;#FFFFFF\&quot; stroke-width=\&quot;0\&quot; 属性=\&quot;Arial\&quot;&gt;&quot;+cnt+&quot;&lt;/rect&gt;&quot;;
					_this.SVGFORM_AppendXml(&quot;_flow&quot;,svg,&quot;&quot;);

					_this.XMLCTL_SetValue(&quot;_flow&quot;,id,_this.XMLCTL_GetValue(&quot;_flow&quot;,id));
					_this.XMLCTL_SetAttribe(&quot;_flow&quot;,id,&quot;font-color&quot;,&quot;#EC0000&quot; );
				}

			}

		}	
	}
	catch(e) {
		alert(e.toString());
	}
}


function getXmlAttribute(attribe,attribename)
{	
            var loc = attribe.indexOf( attribename+&quot;=&quot; );
            var loc1 = attribe.indexOf( &quot;&&quot; ,  loc+attribename.length+1);
            var ret=&quot;&quot;;
            if(loc1&lt;0)
            	ret = attribe.substring( loc+attribename.length+1 ) ;
            else 
            	ret = attribe.substring( loc+attribename.length+1,loc1  ) ;
            return ret;
}
// id=&quot;101&quot;
function getXmlAttribute0(attribe,attribename)
{

            var loc = attribe.indexOf( attribename+&quot;=\&quot;&quot; );
            var loc1 = attribe.indexOf( &quot;\&quot;&quot; ,  loc+attribename.length+2);
            var ret = attribe.substring( loc+attribename.length+2,loc1  ) ;
            return ret;

}

