var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);
var xmldb = new JavaPackage(&quot;com.xlsgrid.net.xmldb&quot;);
var web = new JavaPackage(&quot;com.xlsgrid.net.web&quot;);

function getSvg() 
{
	var svg = &quot;&quot;;
	var filename = &quot;SI_PAYFLOW3.flow&quot;;
	var filename0 = filename;

	var usrinfo = web.EASession.GetLoginInfo(request);

	try {
  		filename = pub.EAOption.dynaDataRoot+ pub.EAOption.get(&quot;xmldb.file.grddb&quot;)+&quot;/syt&quot; + syt + &quot;/&quot; + filename0;
  		svg = pub.EAFunc.readFile(filename);
  	}
	catch(Exception eee ){
	  	// 递归，找上级组织节点是否有这个文件
	  	var bExit = true;
	  	var mysyt = syt;
	  	while (bExit ) {
	  		var _sytdb = new xmldb.EASYTXmlDB(mysyt);
	  		mysyt = _sytdb.parentSyt;
	      	if(mysyt.length()==0 ){
	      		break;
	      	}
	      	else {
	      		try{
				  	filename = EAOption.dynaDataRoot+ EAOption.get(&quot;xmldb.file.grddb&quot;)+&quot;/syt&quot; + mysyt + &quot;/&quot; + filename0;
				  	svg =EAFunc.readFile(filename);
				  	bExit=false;

				}
				catch(Exception eeee ){
	      			bExit = true;	

	      		}
	      	}
	      }
  	}

//	svg = pub.EAFunc.Replace(svg,&quot;\&quot;&quot;, &quot;\\\&quot;&quot;);
//	svg = pub.EAFunc.Replace(svg,&quot;\r\n&quot;, &quot;\\r\\n&quot;);
//	svg = pub.EAFunc.Replace(svg,&quot;\r&quot;, &quot;\\r&quot;);
//	svg = pub.EAFunc.Replace(svg,&quot;\n&quot;, &quot;\\n&quot;);
//	svg = web.EASession.GetSysValue(svg ,usrinfo );//替代那些[%SYS_]

	return svg;
}

//获取操作人员对应权限
function getUsrRol()
{
	var usrrol = &quot;&quot;;
	var usrinfo = web.EASession.GetLoginInfo(request);
	var usrid = usrinfo.getUsrid();
//	var sql = &quot;select a.* from rol a,usrrol b where b.usr=(select guid from usr where id=&apos;zjy&apos; and org=&apos;1&apos;) and a.guid=b.rol&quot;;
//	var rolds = pub.EADbTool.QuerySQL(sql);
//	for (var i=0;i&lt;rolds.getRowCount();i++) {
//		if (usrrol == &quot;&quot;) usrrol = rolds.getStringAt(i,&quot;ID&quot;);
//		else usrrol += &quot;,&quot; + rolds.getStringAt(i,&quot;ID&quot;);
//	}

	var sql = &quot;select count(*) cnt from sysmod a,rolmod b
		where a.guid=b.sysmod and b.rol in (
		      select a.guid from rol a,usrrol b 
		  where b.usr=(select guid from usr where id=&apos;&quot;+usrid+&quot;&apos; and org=&apos;1&apos;) and a.guid=b.rol
		) and a.grdid=&apos;&quot;+mwid+&quot;&apos;&quot;;
	usrrol = pub.EADbTool.GetSQL(sql);
	return usrrol;

}

//获取流程操作的提示数显示
function getProcessCnt()
{
	var usrinfo = web.EASession.GetLoginInfo(request);
	var orgid = usrinfo.getOrgid();
	var accid = usrinfo.getAccid();
	var sytid = usrinfo.getSytid();

	var sql = &quot;&quot;;
	var cnt = 0;

	//业务生成
	if (processid == &quot;0&quot;) {
		sql = &quot;select count(*) from (select distinct  BAE415,BAE074,BAE419,AAE140,BAC005
		      from ac95 a where aaa027=(select tcqbm from cw_sbsb where org=&apos;&quot;+orgid+&quot;&apos;) and zth=&apos;&quot;+zth+&quot;&apos; 
		      and aae208=to_char(to_date(&apos;&quot;+logdat+&quot;&apos;,&apos;yyyy-mm-dd&apos;),&apos;yyyymm&apos;) 
		      and aae400 is null and zfbz=&apos;0&apos; and BAE421 is null
		      and bac005 in (select id from v_cw_ywlx where szzt=&apos;2&apos;)
		      and exists (select 1 from cw_dfdsb_yszl b where sbh = &apos;&quot;+orgid+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and b.yszl_yhjgdm is not null and b.yhbm = a.bae419)
		      )&quot;;
		cnt = pub.EADbTool.GetSQL(sql);
		if (cnt &lt;= 0) cnt = 0;
	}
	//待作废审批单
	else if (processid == &quot;-1&quot;) {
		sql = &quot;select count(*) from aef3 where org=&apos;&quot;+orgid+&quot;&apos; and acc=&apos;&quot;+accid+&quot;&apos; and substr(aae401,0,6)=to_char(to_date(&apos;&quot;+logdat+&quot;&apos;,&apos;yyyy-mm-dd&apos;),&apos;yyyymm&apos;) 
		  and AAE338=&apos;22&apos; and nvl(BAE553,&apos; &apos;)&lt;&gt;&apos;1&apos; and (stat=&apos;1&apos; or stat = &apos;20&apos;)&quot;;
		cnt = pub.EADbTool.GetSQL(sql);
		if (cnt &lt;= 0) cnt = 0;
	}
	//打印
	else if (processid == &quot;4&quot;) {
		sql = &quot;select count(*) from aef3 where org=&apos;&quot;+orgid+&quot;&apos; and acc=&apos;&quot;+accid+&quot;&apos; and substr(aae401,0,6)=to_char(to_date(&apos;&quot;+logdat+&quot;&apos;,&apos;yyyy-mm-dd&apos;),&apos;yyyymm&apos;) 
		       and (stat=&apos;31&apos; or stat=&apos;21&apos;) and AAE338=&apos;22&apos; and nvl(BAE553,&apos;0&apos;)=&apos;0&apos; and nvl(BAE552,&apos;0&apos;)=&apos;0&apos;&quot;;
		cnt = pub.EADbTool.GetSQL(sql);
		if (cnt &lt;= 0) cnt = 0;
	}
	//财务审核
	else if (processid == &quot;21&quot;) {
		sql = &quot;select count(*) from aef3 where org=&apos;&quot;+orgid+&quot;&apos; and acc=&apos;&quot;+accid+&quot;&apos; and substr(aae401,0,6)=to_char(to_date(&apos;&quot;+logdat+&quot;&apos;,&apos;yyyy-mm-dd&apos;),&apos;yyyymm&apos;)
		        and (stat=&apos;1&apos; or stat = &apos;30&apos;) and AAE338=&apos;22&apos; and nvl(BAE553,&apos;0&apos;)=&apos;0&apos;&quot;;
		cnt = pub.EADbTool.GetSQL(sql);
		if (cnt &lt;= 0) cnt = 0;
	}
	//财务主管审核
	else if (processid == &quot;31&quot;) {
		sql = &quot;select count(*) from aef3 where org=&apos;&quot;+orgid+&quot;&apos; and acc=&apos;&quot;+accid+&quot;&apos; and substr(aae401,0,6)=to_char(to_date(&apos;&quot;+logdat+&quot;&apos;,&apos;yyyy-mm-dd&apos;),&apos;yyyymm&apos;)
		       and stat=&apos;21&apos; and AAE338=&apos;22&apos; and nvl(BAE553,&apos;0&apos;)=&apos;0&apos;&quot;;
		cnt = pub.EADbTool.GetSQL(sql);
		if (cnt &lt;= 0) cnt = 0;
	}
	//发送银行
	else if (processid == &quot;5&quot;) {
		sql = &quot;select count(*) from aef3 where org=&apos;&quot;+orgid+&quot;&apos; and acc=&apos;&quot;+accid+&quot;&apos; and substr(aae401,0,6)=to_char(to_date(&apos;&quot;+logdat+&quot;&apos;,&apos;yyyy-mm-dd&apos;),&apos;yyyymm&apos;)
		  and nvl(BAE552,&apos;0&apos;)=&apos;1&apos; and stat=&apos;31&apos; and AAE338=&apos;22&apos; and nvl(BAE553,&apos;0&apos;)=&apos;0&apos;&quot;;
		cnt = pub.EADbTool.GetSQL(sql);
		if (cnt &lt;= 0) cnt = 0;
	}
	else if (processid == &quot;6&quot;) {
		sql = &quot;select count(*) from aef3 where org=&apos;&quot;+orgid+&quot;&apos; and acc=&apos;&quot;+accid+&quot;&apos; and substr(aae401,0,8)=to_char(to_date(&apos;&quot;+logdat+&quot;&apos;,&apos;yyyy-mm-dd&apos;),&apos;yyyymmdd&apos;)
		  and nvl(BAE552,&apos;0&apos;)=&apos;1&apos; and stat=&apos;5&apos; and AAE338=&apos;22&apos; and nvl(BAE553,&apos;0&apos;)=&apos;0&apos;&quot;;
		cnt = pub.EADbTool.GetSQL(sql);
		if (cnt &lt;= 0) cnt = 0;
	}

	return cnt;
}
