	_this.SetToComboBox(&quot;&quot;,0,2,6,yhzlList);
	_this.SetCellText(0,2,6,&quot;101&quot;);
	var dwyhList = _this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_YHZL_ZJ&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	//单位银行种类
