	_this.SetCellText(0,3,6,&quot;%&quot;);	
	//成功标志
	var cgbzList = _this.CreateListValue();
	_this.SetListValue(cgbzList,&quot;0&quot;,&quot;成功&quot;);
	_this.SetListValue(cgbzList,&quot;99&quot;,&quot;失败&quot;);
	_this.SetListValue(cgbzList,&quot;%&quot;,&quot;全部&quot;);
	_this.SetToComboBox(&quot;&quot;,0,3,2,cgbzList);
	_this.SetCellText(0,3,2,&quot;%&quot;);

	//险种类型
