}


//批量代扣
function PLDK() {
	//增加权限控制
	var author = invokeOSFunc(&quot;authorPLDK&quot;,&quot;&quot;);
	if (author != 1) return;
	var kpdat = _this.GetCellText(0,1,6);
	var ywlx = _this.GetCellText(0,3,4);
	var ret = _this.invokeOSFunc(&quot;checkYWLX&quot;,&quot;ywlx=&quot;+ywlx);
	if(ret != &quot;pl&quot;) {
		alert(&quot;请选择批量业务类型再进行批量代扣！&quot;);
		return;
	}
	var pch = _this.GetCellText(0,3,8);
	if(pch == &quot;&quot; || pch == &quot;%&quot;) {
		alert(&quot;批次号不能为空！&quot;);
		return;
	}
	if (!confirm(&quot;生成&quot;+kpdat+&quot;的拷盘文件，是否继续？&quot;)) return;

	var cnt = 0;
	for (var r=_this.GetMainCellRangeRow1(2);r&lt;=_this.GetMainCellRangeRow2(2);r++) {
		var flag = _this.GetCellText(2,r,0);
		var kpbz = _this.GetCellText(2,r,1);
		var sfny = _this.GetCellText(2,r,5);
		var hpbz = _this.GetCellText(2,r,2);
		var yhbm = _this.GetCellText(2,r,22);
		var djh  = _this.GetCellText(2,r,8);
		var mny  = _this.GetCellText(2,r,12);
		var dwbh = _this.GetCellText(2,r,10);
		var dwyhzh = _this.GetCellText(2,r,15);
		var dwyhhm = _this.GetCellText(2,r,14);
		var dwyhmc = _this.GetCellText(2,r,16);
		var kpwjm  = _this.GetCellText(2,r,24);
		var hpwjm  = _this.GetCellText(2,r,25);

		if (flag == 1 && sfny != &quot;&quot;) {
			cnt ++;		
//			if (flag == 1 && kpbz == 1) {
//				alert(&quot;该笔记录已生成拷盘，不能重复拷盘！\n银行：&quot;+yhbm +&quot; \n单据号：&quot;+djh+&quot; \n金额：&quot;+mny);
//				_this.SetFocusCell(2,r,5);
//				return;
//			}
			if (dwbh == &quot;&quot; || dwbh == null){
				alert(&quot;单位编号不能为空！\n银行：&quot;+yhbm +&quot; \n单据号：&quot;+djh+&quot; \n金额：&quot;+mny);
				_this.SetFocusCell(2,r,5);
				return;
			}
			if (dwyhzh == &quot;&quot; || dwyhzh == null){
				alert(&quot;单位银行帐号不能为空！\n银行：&quot;+yhbm +&quot; \n单据号：&quot;+djh+&quot; \n金额：&quot;+mny);
				_this.SetFocusCell(2,r,5);
				return;
			}
			if (dwyhhm == &quot;&quot; || dwyhhm == null){
				alert(&quot;单位银行户名不能为空！\n银行：&quot;+yhbm +&quot; \n单据号：&quot;+djh+&quot; \n金额：&quot;+mny);
				_this.SetFocusCell(2,r,5);
				return;
			}
			if (dwyhmc == &quot;&quot; || dwyhmc == null){
				alert(&quot;单位银行名称不能为空！\n银行：&quot;+yhbm +&quot; \n单据号：&quot;+djh+&quot; \n金额：&quot;+mny);
				_this.SetFocusCell(2,r,5);
				return;
			}
			if (yhbm == &quot;&quot; || yhbm == null){
				alert(&quot;银行编码不能为空！\n银行：&quot;+yhbm +&quot; \n单据号：&quot;+djh+&quot; \n金额：&quot;+mny);
				_this.SetFocusCell(2,r,5);
				return;
			}
		}
	}
	if (cnt == 0) {
		alert(&quot;没有选中记录！&quot;);
		return;
	}

	var param = new Object();
	param.kpdat = kpdat;
	param.xml = getSelectXml(2);	
	param.thisorgid = G_ORGID;
	param.thisaccid = G_ACCID;
	param.sbh = G_ORGID;
	param.zth = G_ACCID.replace(G_ORGID,&quot;&quot;);
	param.usrid = G_USRID;
	param.czyxm = G_USRNAM;
	param.bz = &quot;1&quot;;
	param.btkpwjm = kpwjm;
	param.bthpwjm = hpwjm;

	try {
		var ret = invokeOSFuncExt(&quot;PLDK&quot;,param);
//		alert(ret);
//		return;
		if (ret.substring(0,1) == &quot;1&quot;) {
			alert(&quot;发送拷盘文件成功！&quot;);
			loadMainData();
//			for (var r=_this.GetMainCellRangeRow1(2);r&lt;=_this.GetMainCellRangeRow2(2);r++) {
//				var flag = _this.GetCellText(2,r,0);
//				var kpbz = _this.GetCellText(2,r,2);
//				if (flag == 1 && kpbz == 0) {
//					_this.SetCellText(2,r,2,&quot;1&quot;);
//					_this.SetCellText(2,r,5,kpdat.replace(/\-/g,&quot;&quot;));
//					_this.SetCellShowText(2,r,2,&quot;已拷&quot;);
//					_this.SetCellColor(2,r,2,0,0,255);
//				}
//			}
		}
		else {
			alert(&quot;发送拷盘文件不成功：&quot;+ret);
		}
	}
	catch (e) {
		alert(e);
	}

}

//零星代扣（单笔）
function LXDK()
{
	//增加权限控制
	var author = invokeOSFunc(&quot;authorLXDK&quot;,&quot;&quot;);
	if (author != 1) return;
	var ywlx = _this.GetCellText(0,3,4);
	var ret = _this.invokeOSFunc(&quot;checkYWLX&quot;,&quot;ywlx=&quot;+ywlx);
	if(ret != &quot;db&quot;) {
		alert(&quot;请选择单笔业务类型再进行实时单笔代扣！&quot;);
		return;
	}
	if(!window.confirm(&quot;是否要发起单笔实时代扣交易？&quot;)) return false;
	var kpdat = _this.GetCellText(0,1,6);
	//循环选中的每笔数据，发送给银行，失败一笔下一笔继续发
	var cnt = 0;//记录选中行数
	for (var r=_this.GetMainCellRangeRow1(2);r&lt;=_this.GetMainCellRangeRow2(2);r++) {
		var flag = _this.GetCellText(2,r,0);
		var cwlsh = _this.GetCellText(2,r,23);
		var fkzh = _this.GetCellText(2,r,15);
		var fkhm = _this.GetCellText(2,r,14);
		var fkyh = _this.GetCellText(2,r,16);
		if(flag == 1) {
			if(cwlsh != &quot;&quot;) {alert(&quot;财务已入账，不能重复扣款！&quot;);return false;}	
			if(fkzh == &quot;&quot;) {alert(&quot;单位银行账号不能为空&quot;);return false;}	
			if(fkhm == &quot;&quot;) {alert(&quot;单位银行户名不能为空&quot;);return false;}
			cnt++;	
			var param = new Object();
			//原始凭证附件张数
			var fjzs = &quot;&quot;;
			fjzs = &quot;1&quot;;
			param.yszlywlx = &quot;1.6&quot;;
			param.yspz_fjzs = fjzs;			
			param.yszl_fkzh = fkzh;
			param.yszl_fkhm = fkhm;
			param.yszl_fkyh = fkyh;
			param.yszl_skyh = &quot;&quot;;
			param.yszl_skzh = &quot;&quot;;
			param.yszl_skhm = &quot;&quot;;
			param.yszl_kmbh = &quot;&quot;;
			param.yszl_yy = kpdat.split(&quot;-&quot;)[0];	
			param.yszl_mm = kpdat.split(&quot;-&quot;)[1]*1;	
			param.yszl_dd = kpdat.split(&quot;-&quot;)[2]*1;
			param.ffpch = &quot;&quot;;
			param.yszl_yhzl = _this.GetCellText(0,2,6);
			param.ywlx = _this.GetCellText(0,3,4);
			param.scny = _this.GetCellText(0,1,2);
			var sbh = G_ORGID;
			var zth = G_ACCID.replace(sbh,&quot;&quot;);
			param.yszl_xzlx = _this.GetCellText(0,1,8);	
			param.sbh = sbh;
			param.zth = zth;
			param.thisorgid = G_ORGID;
			param.thisaccid = G_ACCID;
			param.djh = _this.GetCellText(2,r,8);
			param.jyje = _this.GetCellText(2,r,12);
			param.jefx = &quot;借&quot;;
			param.lsh = &quot;&quot;;
			param.czyxm = G_USRNAM;
			param.jsfs = &quot;转账&quot;;
			param.rjzlsh = &quot;&quot;;
			param.yhzl = _this.GetCellText(0,2,6);
			param.bkdjh = &quot;&quot;;
			var ret = _this.invokeOSFuncExt(&quot;Save&quot;,param);
			if(ret == 1) {//成功！
				alert(&quot;扣款成功！【业务单据号：&quot;+_this.GetCellText(2,r,8)+&quot;,单位编号:&quot;+_this.GetCellText(2,r,10)+&quot;,单位名称:&quot;+_this.GetCellText(2,r,11)+&quot;】&quot;);
			}
			else {//失败
				alert(&quot;扣款失败！【业务单据号：&quot;+_this.GetCellText(2,r,8)+&quot;,单位编号:&quot;+_this.GetCellText(2,r,10)+&quot;,单位名称:&quot;+_this.GetCellText(2,r,11)+&quot;】\r\n出错原因:&quot;+ret);
			}					
		}
	}
	loadMainData();

}

//判断数组是否存在某个元素
function contains(arr, obj) {  
    var i = arr.length;  
    while (i--) {  
        if (arr[i] === obj) {  
            return true;  
        }  
    }  
    return false;  
}  

