//创建临时表
function createTempTable(db)
{
	var tempTable = db.GetSQL(&quot;select &apos;TMP_YHKP&apos;||to_char(sysdate,&apos;yymmddhh24miss&apos;) from dual&quot;);
	var sql = &quot;create table &quot;+tempTable+&quot; (sfny varchar(20),djh varchar(20),dwbh varchar(20),dwyhhm varchar(200),dwyhzh varchar(30),dwyhmc varchar(200),yhbm varchar(20))&quot;;
	db.ExcecutSQL(sql);	
	return tempTable;
}

//drop临时表
function dropTempTable(db,tempTable)
{
	var sql = &quot;drop table &quot;+tempTable;
	db.ExcecutSQL(sql);
}


//获取险种名称
function GetXzmc(xzlx,lxbh,db){
	var retStr = &quot;&quot;;
	var sql = &quot;select substrb(csmc,1,8) csmc from cw_csb where lxdm = &apos;XZ&apos; and csdm = &apos;&quot;+xzlx+&quot;&apos;&quot;;
	var xzmc = db.GetSQL(sql);
	if(lxbh == &quot;31&quot;){
		return retStr = &quot;医疗统筹&quot;;
	}else if(lxbh == &quot;33&quot;){
		return retStr = &quot;医疗个账&quot;;
	}else if(lxbh == &quot;34&quot;){
		return retStr = &quot;医疗单建&quot;;	
	}else if(lxbh == &quot;37&quot;){
		return retStr = &quot;公务员&quot;;	
	}else if(lxbh == &quot;38&quot;){
		return retStr = &quot;大额医疗&quot;;	
	}else if(lxbh == &quot;39&quot;){
		return retStr = &quot;离休&quot;;	
	}else if(lxbh == &quot;71&quot;){
		return retStr = &quot;居民统筹&quot;;	
	}else if(lxbh == &quot;73&quot;){
		return retStr = &quot;居民个账&quot;;	
	}else{
		return xzmc ;
	}
}

//补全长度空格
function formatStr(str,len,bef,typ)
{
	str = &quot;&quot;+str;
	var slen = getWordCount(str);//str.length();
	if (slen &gt; (len-1)) {
		//return str.substring(0,len);	//该方法有问题
//		return pub.EAFunc.bSubstring(str ,len);
		var retstr = pub.EADbTool.GetSQL(&quot;select substrb(&apos;&quot;+str+&quot;&apos;,0,&quot;+len+&quot;) from dual&quot;);
		return retstr;
	} 
	var trim = &quot;&quot;;
	if(typ == &quot;int&quot; || typ == &quot;INT&quot;) {
		for (var i=0;i&lt;(len-slen);i++) {
			trim += &quot;0&quot;;
		}			
	}
	else {
		for (var i=0;i&lt;(len-slen);i++) {
			trim += &quot; &quot;;
		}	
	}
	if (bef == 1) return trim + str;
	else return str + trim;
}

function getWordCount(s)
{
        var length = 0;
        for(var i = 0; i &lt; s.length(); i++) {
		var ascii = java.lang.Character.codePointAt(s, i);
		if(ascii &gt;= 0 && ascii &lt;=255)
			length++;
		else
			length += 2;
	}
        return length;
}





//下载拷盘文件保存到本地P目录
function downLoadFile()
{
	var path = &quot;/u/dsdf/&quot;+mmdir+&quot;/&quot;;
	var txt = pub.EAFunc.readFile(path+filename);
	return txt;
}
//作为.sp服务时的入口
//预定义变量：request,response
function Response()
{
	var itemclass = request.getParameter(&quot;filename&quot;);
  	response.setHeader(&quot;File&quot;,filename);
        response.setHeader(&quot;Content-Disposition&quot;, &quot;attachment;filename=&quot;+filename);
	var path = &quot;/u/dsdf/&quot;+mmdir;
	var txt = pub.EAFunc.readFile(path+filename);
	txt = pub.EAFunc.Replace(txt,&quot;\n&quot;,&quot;\r\n&quot;);
	pub.EAZip.compressStringToStream(txt,response.getOutputStream());
}

function getSysDate()
{
	var dt = pub.EADbTool.GetSQL(&quot;select to_char(sysdate,&apos;yyyymmddhh24miss&apos;) from dual&quot;);
	return dt;
}


