						skyhhh = db.GetSQL(&quot;select nvl(yszl_khld_yhhh,&apos; &apos;) from aa10 where aaa100 = &apos;AAE008&apos; and aaa102 = &apos;&quot;+yhbm+&quot;&apos;&quot;);
