						db.Rollback();
						return &quot;跨行托收查询银行行号出错！单据号=&quot;+mydjh;
					}					
				}
				else {
					bthbz = 0;
				}								
				var backup1 = formatStr(&quot;&quot;,10,0,&quot;&quot;);
				var backup2 = formatStr(&quot;&quot;,10,0,&quot;&quot;);
				var backup3 = formatStr(&quot;&quot;,20,0,&quot;&quot;);
//				if(AAE013.trim() != &quot;&quot;) {
//					zy = AAE013;
//				}else {
//					zy = BIC006;
//				}
				//查询代扣协议编号
				try{
					sql = &quot;insert into SI_WSFILE_0122001_DTL
						select &apos;&quot;+sbcode+&quot;&apos;,&apos;&quot;+yymmdd+&quot;&apos;,&apos;&quot;+hhmmss+&quot;&apos;,&apos;&quot;+yhlsh+&quot;&apos;,&apos;&quot;+sblsh+&quot;&apos;,&apos;&quot;+mytype+&quot;&apos;,&apos;&quot;+chbz+&quot;&apos;,&apos;&quot;+jsywlx+&quot;&apos;,&apos;&quot;+AAB001+&quot;&apos;,&apos;&quot;+cbdxlx+&quot;&apos; ,
							&apos;&quot;+AAC003+&quot;&apos;,&apos;&quot;+cbdxzjlx+&quot;&apos;,&apos;&quot;+sbcode+&quot;&apos;,&apos;&quot;+dkxybh+&quot;&apos;,&apos;&quot;+AAE010+&quot;&apos;,&apos;&quot;+AAE009+&quot;&apos;,&apos;&quot;+bthbz+&quot;&apos;,&apos;&quot;+skyhhh+&quot;&apos;,&apos;&quot;+skzh+&quot;&apos; ,
							&apos;&quot;+skhm+&quot;&apos;,&apos;&quot;+AIC263+&quot;&apos;,&apos;&quot;+zy+&quot;&apos;,&apos;&quot;+backup1+&quot;&apos;,&apos;&quot;+backup2+&quot;&apos;,&apos;&quot;+backup3+&quot;&apos;,&apos;&quot;+logguid+&quot;&apos;,sys_guid(),&apos;&apos;,&apos;&apos; from dual&quot;;	
					db.ExcecutSQL(sql);
				}catch(e) {				
				}		
				var line = sbcode+&quot;&quot;+yymmdd+&quot;&quot;+hhmmss+&quot;&quot;+yhlsh+&quot;&quot;+sblsh+&quot;&quot;+mytype+&quot;&quot;+chbz+&quot;&quot;+jsywlx+&quot;&quot;+AAB001+&quot;&quot;+cbdxlx+&quot;&quot;+AAC003+&quot;&quot;+cbdxzjlx+&quot;&quot;+sbcode+&quot;&quot;+dkxybh+&quot;&quot;+AAE010+&quot;&quot;+AAE009
						+&quot;&quot;+bthbz+&quot;&quot;+formatStr(skyhhh,12,0,&quot;&quot;)+&quot;&quot;+skzh+&quot;&quot;+skhm+&quot;&quot;+AIC263+&quot;&quot;+zy+&quot;&quot;+backup1+&quot;&quot;+backup2+&quot;&quot;+backup3+&quot;\n&quot;;
				//java.lang.System.out.println(k+&quot;&quot;+line);
				fileStr += line;
			}
//				db.Rollback();
//				return fileStr;		
			sql = &quot;select * from CW_DFDSB_YSZL where acc=&apos;&quot;+thisaccid+&quot;&apos; and org=&apos;&quot;+thisorgid+&quot;&apos; and yhbm=&apos;&quot;+yhbm+&quot;&apos;&quot;;
			var ftpds = db.QuerySQL(sql); //ftp的配置信息
			if (ftpds.getRowCount() &gt; 0) {
				//var wjm = ftpds.getStringAt(0,&quot;WJM&quot;);
				var ftpip = ftpds.getStringAt(0,&quot;FTP_IP&quot;);
				var ftpport = ftpds.getStringAt(0,&quot;FTP_PORT&quot;);
				var ftpuser = ftpds.getStringAt(0,&quot;FTP_GXSI_LOGIN_NAME&quot;);
				var ftppasswd = ftpds.getStringAt(0,&quot;FTP_GXSI_LOGIN_PASSWD&quot;);		
				var ftploginpath = ftpds.getStringAt(0,&quot;FTP_GXSI_LOGIN_PATH&quot;);			
				var ftpuppath = ftpds.getStringAt(0,&quot;FTP_UP_PATH&quot;);
				var upwjm = ftploginpath +&quot;/&quot;+ ftpuppath; 					
				var fileName = &quot;/u/yszl_dsdf/&quot;+ftpuppath+&quot;/&quot;+wjm+&quot;.txt&quot;; ///u/dsdf/sy_NY20151217.txt
				var fileName2 = &quot;/u/yszl_dsdf/&quot;+ftpuppath+&quot;/&quot;+wjm+&quot;.txt.gz&quot;;
				pub.EAFunc.WriteToFileEx(fileName,fileStr,false);  //生成本地文件,覆盖方式
				var sbcwservice = new SBCW_sbcwService();
				sbcwservice.Gzip(fileName,fileName2);
//				var lognote = &quot;生成拷盘文件：&quot;+fileName2;
//				sql = &quot;insert into oplog(grdid,type,action,opdat,opusr,opusrnam,note,acc,syt,org)
//					values(&apos;RJZ_YWKP&apos;,&apos;发放拷盘&apos;,&apos;生成&apos;,sysdate,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;)&quot;.format([usrid,czyxm,lognote,thisaccid,&quot;SBCW&quot;,thisorgid]);
//				db.ExcecutSQL(sql);				
				//上传sftp				
				var jsch = new jschftp.JSch();
				var sftp = null;
				var sshSession = null;
				try{
					sshSession = jsch.getSession(ftpuser,ftpip,&quot;22&quot;);
					sshSession.setPassword(ftppasswd);
					var config = new java.util.Properties();
					config.put(&quot;StrictHostKeyChecking&quot;,&quot;no&quot;);
					sshSession.setConfig(config);
					sshSession.connect();
					var channel = sshSession.openChannel(&quot;sftp&quot;);
					channel.connect();
					sftp = channel;
					//上传一个文件
					sftp.cd(ftploginpath + &quot;/&quot; + ftpuppath);
					var localFile = new java.io.File(fileName2);
					sftp.put(new java.io.FileInputStream(localFile),localFile.getName());
					//return ftploginpath + &quot;/&quot; + ftpuppath + &quot;/&quot;+localFile.getName();
					sftp.ls(ftploginpath + &quot;/&quot; + ftpuppath + &quot;/&quot;+localFile.getName());
					sftp.quit();
					sshSession.disconnect();
					//return localFile.getName();
				}catch(ee){
					if(sftp != null) sftp.quit();
					if(sshSession != null) sshSession.disconnect();
					if(db != null) db.Rollback();
					return &quot;上传文件到ftp失败:&quot;+ee.toString();
				}
				//获取拷盘流水号
				var kplsh = db.GetSQL(&quot;select seq_kplsh.nextval from dual&quot;);								
				//生成文件完调用业务接口通知业务报盘成功
				if(ywjkbz == &quot;DR&quot;){
					//djhall 所有单据号(拷盘所有单据号)
					sql =  &quot;select bae074,nvl(sum(aic263),0) aic263
						  from ac95 
						 where BAE074 in (select djh from &quot;+tempTable+&quot; where sfny=&apos;&quot;+sfny+&quot;&apos; and yhbm=&apos;&quot;+yhbm+&quot;&apos;)
						   and BAE419=&apos;&quot;+yhbm+&quot;&apos;
						   and zth = &apos;&quot;+zth+&quot;&apos;
						 group by bae074,bae421
						 order by bae074&quot;;
					var dj_ds = db.QuerySQL(sql);
					if(dj_ds.getRowCount() &gt; 0) {					
						for(var i = 0; i &lt;= dj_ds.getRowCount() - 1; i++){
							var bp_djh = dj_ds.getStringAt(i,&quot;BAE074&quot;);
							var dj_zje = dj_ds.getStringAt(i,&quot;aic263&quot;) * 1.0;	
							var yh_ywjk = new SBCW_YWJK_YH();					
							var yhjk_msg = yh_ywjk.yhjk_PayInterface(db,bp_djh,&quot;BP&quot;,dj_zje,kpdat,&quot;&quot;,&quot;&quot;,czyxm,tcqbm ,thisorgid ,thisaccid,ywjkbz,kplsh,&quot;报盘成功&quot;,&quot;&quot;);  
							var ret = yhjk_msg.split(&quot;@&quot;)[0];
							var retmes = yhjk_msg.split(&quot;@&quot;)[1];
							if(ret!=&quot;NOERROR&quot;){
								db.Rollback();
								return &quot;调用拷盘业务接口出错！出错原因:&quot;+retmes+&quot;.\n&quot;;
							}
						}//for(var i = 0; i &lt;= dj_ds.getRowCount() - 1; i++)
					}
					else {
						db.Rollback();
						return &quot;拷盘失败，查询ac95出错！&quot;;
					}	
				}//if(ywjkbz == &quot;DR&quot;)				
				//return &quot;上传文件到ftp成功&quot;;
				//上传成功后调用批量文件就绪通知接口
				//构造xml
				var ref = sbcwservice.genRef(sbcode.trim());//报文标识
				var md5 = pub.EAFunc.getFileMD5(fileName2);//md5码
				wjm += &quot;.txt.gz&quot;;
				var sblsh = db.GetSQL(&quot;select fn_get_YSZL_SBJYLSH(&apos;&quot;+sbcode.trim()+&quot;&apos;) from dual&quot;); 
				//更新拷盘标志信息 //20170216增加更新BAE116字段
				sql = &quot;update ac95 set BAE421=&apos;&quot;+kplsh+&quot;&apos;,BAC001=&apos;&quot;+wjm+&quot;&apos;,BAC002=sysdate,AAE036=sysdate,AAE011=&apos;&quot;+czyxm+&quot;&apos;,aae004 = &apos;&quot;+ref+&quot;&apos;,bae116=&apos;1&apos;
					where AAE208=&apos;&quot;+sfny+&quot;&apos;
					  and BAE074 in (select djh from &quot;+tempTable+&quot; where sfny=&apos;&quot;+sfny+&quot;&apos; and yhbm=&apos;&quot;+yhbm+&quot;&apos;)
					  and BAE419=&apos;&quot;+yhbm+&quot;&apos;
					  and aaa027 = &apos;&quot;+tcqbm+&quot;&apos;
					  and zth = &apos;&quot;+zth+&quot;&apos;&quot;;
				 db.ExcecutSQL(sql);
				sql = &quot;update v_si_djb_tmp set bae116 = &apos;1&apos;,bae012=&apos;&quot;+czyxm+&quot;&apos;,bae034=sysdate,cwbz = 1
				         where djh in (select djh from &quot;+tempTable+&quot; where sfny=&apos;&quot;+sfny+&quot;&apos; and yhbm=&apos;&quot;+yhbm+&quot;&apos;) and sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos;&quot;;
				db.ExcecutSQL(sql);				 					       				
			      var wb = new SBCW_WSSI4();
			      //function F0114002(BusCd,SenderId,RecverId,SiSeq,FileName,FileMD5);
			      var retMap = new java.util.HashMap();
			      retMap = wb.F0114002(busycode,sbcode.trim(),yhjgdm.trim(),sblsh.trim(),wjm.trim(),md5,ref);
			      var retcode = retMap.get(&quot;RstCode&quot;);
			      var retinfo = retMap.get(&quot;RstInfo&quot;);
				if (retcode != &quot;0000&quot;) { //返回成功
					db.Rollback();
					return &quot;批量代扣出错,银行返回错误信息：&quot;+retinfo ;				
				}			
				//删除本地生成的临时文件
				//var f = new java.io.File(fileName);
				//if(f.exists()) f.delete();	
			}
			else {
				db.Rollback();
				return &quot;批量代扣出错,查询银行ftp信息出错&quot;;				
			}

		}
		dropTempTable(db,tempTable); //删除临时表		
		db.Commit();
		return &quot;1~批量代扣发盘成功！&quot;;
		return &quot;1~&quot;+tips; //&quot;生成拷盘文件成功！&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return &quot;生成拷盘出错:&quot;+e.toString();
	}
	finally {
		if (db != null) db.Close();
	}

}

//判断银行是否为同一银行，是否是跨行支付
//dfyhbm 社保银行编码 aae008 单位或个人银行编码
function IsSameBank2(db,dfyhbm,aae008)
{
	var sql = &quot;&quot;;
	sql = &quot;select count(*)
			from (select max(yhzl) yhzl
			          from cw_dfdsb_yszl
			         where sbh = &apos;&quot;+sbh+&quot;&apos;
			           and zth = &apos;&quot;+zth+&quot;&apos;
			           and yhbm = &apos;&quot;+dfyhbm+&quot;&apos;) b,
			       （select max(yhzl) yhzl
			  from cw_dfdsb_yszl
			 where sbh = &apos;&quot;+sbh+&quot;&apos;
			   and zth = &apos;&quot;+zth+&quot;&apos;
			   and yhbm = &apos;&quot;+aae008+&quot;&apos;) a
			 where b.yhzl = a.yhzl&quot;;
	var cnt = db.GetSQL(sql);
	if(cnt &gt; 0) {
		return 1;
	}
	else return 0;		 
}


//作废业务数据
function btRollback(){
	var sql = &quot;&quot;;
	var db = null;

	try{
		db = new pub.EADatabase();
		var iuo_ywjk = new SBCW_IYWJK(); //业务接口类
		var ywjkbz = iuo_ywjk.getYWJKBZ(db,thisaccid);


//		sql = &quot;SELECT substrb(&apos;&quot;+bthpwjm+&quot;&apos;,-lengthb(&apos;&quot;+btkpwjm+&quot;&apos;)) bthpwjm FROM dual&quot;;
//		var bthpwjn_sub = db.GetSQL(sql);
//		if(bthpwjn_sub != btkpwjm){//回盘文件名跟拷盘文件名不一致情况，则是在补托状态，不允许作废
//			return &quot;回盘文件名跟拷盘文件名不一致,在补托状态,不允许作废 \n 拷盘文件名:&quot;+btkpwjm+&quot;\n 回盘文件名:&quot;+bthpwjm;
//		}

		var BAZ610 = &quot;&quot;;
		var AAA027 = &quot;&quot;;
		var BAZ901 = &quot;&quot;;
		var BAZ902 = &quot;&quot;;
		var BAC004 = &quot;&quot;;
		var BAE415 = &quot;&quot;;
		var AAE076 = &quot;&quot;;
		var BAE036 = &quot;&quot;;
		var AIC263 = 0.00;
		var BAE419 = &quot;&quot;;
		var BZE034 = &quot;&quot;;
		var BAC003 = &quot;&quot;;
		var AAB034 = &quot;&quot;;
		var BAE421 = &quot;&quot;;
		//获取回款日期
		var sql = &quot;select to_char(sysdate,&apos;yyyy-mm-dd&apos;) from dual&quot;;
		var hkrq = db.GetSQL(sql);

		sql = &quot;select a.BAZ610,a.AAA027,a.AAB034 ,a.BAZ901,a.BAZ902,a.BAC004,a.BAE415,a.AAE076,a.BAE036,sum(a.aic263) AIC263,a.BAC003,a.BAE421
		from ac95 a where AAB001=&apos;&quot;+dwbh+&quot;&apos; and BAE074=&apos;&quot;+djh+&quot;&apos; 
		group by a.BAZ610,a.AAA027,a.AAB034 ,a.BAZ901,a.BAZ902,a.BAC004,a.BAE415,a.AAE076,a.BAE036 ,a.BAC003,a.BAE421&quot;;
		var tmpds = db.QuerySQL(sql);
		if (tmpds.getRowCount() &gt; 0) {
			//调用接口（银海、东软）通知业务 (一笔明细调用一次接口）
			BAZ610 = tmpds.getStringAt(0,&quot;BAZ610&quot;); //业务内部序号
			AAA027 = tmpds.getStringAt(0,&quot;AAA027&quot;); //所属统筹区
			AAB034 = tmpds.getStringAt(0,&quot;AAB034&quot;); //所属统筹区
			BAZ901 = tmpds.getStringAt(0,&quot;BAZ901&quot;); //回盘结果状态码
			BAZ902 = tmpds.getStringAt(0,&quot;BAZ902&quot;); //回盘结果失败原因
			BAC004 = tmpds.getStringAt(0,&quot;BAC004&quot;); //回盘时间
			BAE415 = tmpds.getStringAt(0,&quot;BAE415&quot;); //发放批次
			AAE076 = tmpds.getStringAt(0,&quot;AAE076&quot;); //财务流水号
			BAE036 = tmpds.getStringAt(0,&quot;BAE036&quot;); //回盘时间
			AIC263 = tmpds.getStringAt(0,&quot;AIC263&quot;); //总金额
//			BAE419 = tmpds.getStringAt(0,&quot;BAE419&quot;); //银行种类
			BAC003 = tmpds.getStringAt(0,&quot;BAC003&quot;); //回盘文件名
			BAE421 = tmpds.getStringAt(0,&quot;BAE421&quot;);//报盘流水号
			//调用业务接口
			var yh_ywjk = new SBCW_YWJK_YH();
			var yhjk_msg = yh_ywjk.yhjk_PayInterface(db,djh,&quot;1&quot;,AIC263,hkrq,BAZ901,BAZ902,czyxm,AAB034,thisorgid ,thisaccid,ywjkbz,BAE421,&quot;&quot;,&quot;&quot;);
			var ret = yhjk_msg.split(&quot;@&quot;)[0];
			var retmes = yhjk_msg.split(&quot;@&quot;)[1];
			if(ret!=&quot;NOERROR&quot;){
				db.Rollback();
				return &quot;4、补托调用业务接口prc_p_PayInterface出错！单据号:&quot;+djh+&quot;;出错原因:&quot;+retmes+&quot;.\n&quot;;
			}

			//更新回盘信息
			sql = &quot;update ac95 set bie085=&apos;2&apos;,zfbz=&apos;1&apos; where AAB001=&apos;&quot;+dwbh+&quot;&apos; and BAE074=&apos;&quot;+djh+&quot;&apos;&quot;;			
			var upcnt = db.ExcecutSQL(sql);
			if (upcnt == 0) {
				db.Rollback();
				return &quot;3、更新回盘记录不成功，请检查回盘文件数据格式是否正确！单据号:&quot;+djh+&quot;.\n&quot;;
			}
			db.Commit();
//			db.Rollback();
			return 1;
		}

	}
	catch (e) {
//		notify(jobseqid,100,e.toString()+&quot;  出错行=&quot; + rowcnt,&quot;error&quot;);
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}

}

//判断业务类型是单笔还是批量 单笔：db 批量:pl
function checkYWLX()
{
	var db = null;
	var sql = &quot;&quot;;
	try{
		db = new pub.EADatabase();
		sql = &quot;select nvl(max(yszl_dm),&apos;NULL&apos;) from aa10 where aaa100 = &apos;YWLX&apos; and aaa102= &apos;&quot;+ywlx+&quot;&apos;&quot;;
		var yszl_dm = db.GetSQL(sql);
		if(yszl_dm == &quot;0122001&quot;) {
			return &quot;pl&quot;;
		}
		else {
			return &quot;db&quot;;
		}
	}catch(e) {
		if(db != null) db.Rollback();
		return e.toString();
	}finally {
		if(db != null) db.Close();
	}
}

//单笔代扣保存
//保存日记帐录入
function Save()
{	
	var db = null;
	var sql = &quot;&quot;;
	var invokeRet = &quot;&quot;;
	var retmsg = &quot;&quot;;//返回1表示成功
	try {
		db = new pub.EADatabase();		
		var lsh = rjzlsh;
		var sxh = &quot;&quot;;
		var yspz_fjpch = &quot;&quot;;
		var iuo_ywjk = new SBCW_IYWJK(); //业务接口类
		var ywjkbz = iuo_ywjk.getYWJKBZ(db,thisaccid);//业务接口标志		
		var service = new SBCW_sbcwService();
		var sbcode = db.GetSQL(&quot;select yszl_sbjgdm from cw_ztb where org = &apos;&quot;+thisorgid+&quot;&apos; and acc = &apos;&quot;+thisaccid+&quot;&apos;&quot;);// 银社直连社保经办机构代码
