					sql = &quot;select sum(je1+je2+je3+je4+je5+je6+je7+je8+je9+je10+je11+je12+je13+je14+je15) je,jsfs
						from v_si_djb_tmp where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and djh = &apos;&quot;+djh+&quot;&apos; and zfbz=&apos;0&apos; group by jsfs&quot;;
					var je_ds = db.QuerySQL(sql);
					var djsum   = je_ds.getStringAt(0,&quot;JE&quot;);
					var si_jsfs = je_ds.getStringAt(0,&quot;JSFS&quot;);
					if(si_jsfs != &quot;退暂存款财务不调接口&quot;){
						//调用业务接口	
						var jffs = &quot;&quot;;		//缴费方式
						if(ywjkbz == &quot;YH&quot;){
							if(ywlx == &quot;A120&quot;){
								jffs = &quot;4&quot;;
							}else{
								jffs = &quot;3&quot;;
							}
						}else if(ywjkbz == &quot;DR&quot;){
							jffs = &quot;DEDZ&quot;;	//等额到账
						}								
						if( dwbh != &quot;BMKX99&quot; ){
							var yhjk_msg = yh_ywjk.yhjk_PayInterface(db,djh,jffs,djsum,rq,&quot;&quot;,&quot;&quot;,czyxm,tcqbm ,thisorgid ,thisaccid,ywjkbz,sblsh,zy,yszl_kmbh);  
							var ret = yhjk_msg.split(&quot;@&quot;)[0];
							var retmes = yhjk_msg.split(&quot;@&quot;)[1];
							if(ret!=&quot;NOERROR&quot;){
								db.Rollback();
								return &quot;1、调用征收业务接口出错！单据号:&quot;+djh+&quot;;出错原因:&quot;+retmes+&quot;.\n&quot;;
							}
							sql = &quot;update ac95 set BAZ901 = &apos;0000&apos;,BAC004=to_date(&apos;&quot;+rq+&quot;&apos;,&apos;yyyy-mm-dd&apos;),BAE011=&apos;&quot;+czyxm+&quot;&apos;,BAE036=sysdate,AAE076 =&apos;&quot;+sblsh+&quot;&apos; 
							where aaa027 = &apos;&quot;+tcqbm+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and BAE074 = &apos;&quot;+djh+&quot;&apos;&quot;;
							db.ExcecutSQL(sql);							
						}//if( dwbh != &quot;BMKX99&quot; )
					}//if(si_jsfs != &quot;退暂存款财务不调接口&quot;)
				}else if(szzt == 2){
					//银行发放接口
					//  --财务回写业务数据标志
					//  PROCEDURE prc_updateac82_aae117(prm_aaz220   IN    NUMBER,   --应付计划事件ID (BAZ610	 业务内部序号)
					//                                  prm_aaa027   IN    VARCHAR2, --所属统筹区 ()
					//                                  prm_aae117   IN    VARCHAR2, --支付标志（11:支付成功，12：支付失败）
					//                                  prm_reason   IN    VARCHAR2, --失败原因
					//                                  prm_yac900   IN    VARCHAR2, --记账凭证号
					//                                  prm_yac901   IN    VARCHAR2, --制单人
					//                                  prm_yac902   IN    DATE,     --记账时间
					//                                  prm_yac903   IN    VARCHAR2, --财务汇总批次号
					//                                  prm_yac904   IN    DATE,     --到账时间
					//                                  prm_yac905   IN    DATE,     --对账时间
					//				    prm_yac906   IN    DATE,     --回盘时间
					//                                  prm_xh       OUT   VARCHAR2, --记录流水号
					//                                  prm_appcode  OUT   VARCHAR2,
					//                                  prm_errmsg   OUT   VARCHAR2);
					//function yhkpReturn(db,orgid,accid,ywguid,tcqbm,zfbz,errmsg,lsh,zdr,jzdat,pch,daozdat,duizdat,hprq,ywjkbz)
					if(ywjkbz == &quot;YH&quot;){
						sql = &quot;SELECT distinct BAZ610,BAE415 FROM ac95 WHERE  aaa027 = &apos;&quot;+tcqbm+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and BAE074 = &apos;&quot;+djh+&quot;&apos;&quot;;
						var jkds = db.QuerySQL(sql);
						var BAZ610 = jkds.getStringAt(0,&quot;BAZ610&quot;);
						var BAE415 = jkds.getStringAt(0,&quot;BAE415&quot;);
						sql = &quot;SELECT to_char(to_date(&apos;&quot;+rq+&quot;&apos;,&apos;yyyy-mm-dd&apos;),&apos;yyyy-mm-dd&apos;) FROM dual&quot;;
						var czysj = db.GetSQL(sql);

						var jkmsg = uo_ywjk.yhkpReturn(db,thisorgid,thisaccid,BAZ610 ,tcqbm,&quot;11&quot;,&quot;&quot;,lsh,czyxm,czysj ,BAE415,czysj ,czysj ,czysj ,ywjkbz);
						var ret = jkmsg.split(&quot;@&quot;)[1];
						var msg = jkmsg.split(&quot;@&quot;)[2];
						if(ret != &quot;NOERROR&quot;){
							db.Rollback();
							return &quot;1、调用发放业务接口出错，出错原因:&quot;+jkmsg ;
						}
					}

					sql = &quot;update ac95 set BAZ901 = &apos;0000&apos;,BAC004=to_date(&apos;&quot;+rq+&quot;&apos;,&apos;yyyy-mm-dd&apos;),BAE011=&apos;&quot;+czyxm+&quot;&apos;,BAE036=sysdate,AAE076 =&apos;&quot;+sblsh+&quot;&apos; 
						where aaa027 = &apos;&quot;+tcqbm+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and BAE074 = &apos;&quot;+djh+&quot;&apos;&quot;;
					db.ExcecutSQL(sql);
				}
			}
		}	
		var TransType = &quot;&quot;;
		var TransMode = &quot;&quot;;
		TransType = &quot;003&quot;;
		TransMode = &quot;2&quot;;
		var BankCode = &quot;&quot;;		
		var wssi2 = new SBCW_WSSI2();
		var yhjgdm = db.GetSQL(&quot;select b.yszl_yhjgdm from cw_bkdyhb a,cw_dfdsb_yszl b where a.sbh = &apos;&quot;+sbh+&quot;&apos; and a.zth = &apos;&quot;+zth+&quot;&apos; and a.kmbh = &apos;&quot;+yszl_kmbh+&quot;&apos;and b.sbh = &apos;&quot;+sbh+&quot;&apos; and b.zth = &apos;&quot;+zth+&quot;&apos; and b.yhbm = a.yhzl&quot;);//银行机构代码
		//求单位机构代码
		var dwjgdm = &quot;&quot;;
		try{dwjgdm = db.GetSQL(&quot;SELECT AAB003 FROM AB01 WHERE AAB001 = &apos;&quot;+dwbh+&quot;&apos;&quot;);}catch(e){}					
               //function F0112001(SenderInstId,RecverInstId,SiSeq,CrBkAcctId,CrBkAcctName,DrBkAcctId,DrBkAcctName,SiAcctId,Amt,TransType,TransMode)                    		
		var mny = db.GetSQL(&quot;select to_number(&apos;&quot;+je+&quot;&apos;)*100 from dual&quot;);	
		var retMap = new java.util.HashMap();
		var retMap = wssi2.F0112001(sbcode,yhjgdm,sblsh ,yszl_skzh ,yszl_skhm ,yszl_fkzh ,yszl_fkhm ,dwbh ,mny,TransType,TransMode,BankCode);
//		db.Rollback();
//		return retMap ;
		var busicode = retMap.get(&quot;RstCode&quot;);
		var busiinfo = retMap.get(&quot;RstInfo&quot;);
		//返回成功则继续 保存日记账
		if(busicode == &quot;0000&quot;) {
			sql = &quot;update cw_yhrjzb set yszl_yhrzlsh = &apos;&quot;+retMap.get(&quot;BkSeq&quot;)+&quot;&apos;,yszl_yhrzrq =&quot;+retMap.get(&quot;BusiDate&quot;)+&quot;,yszl_yhrzsj = &quot;+retMap.get(&quot;BusiTime&quot;)+&quot;
					where sbh = &apos;&quot;+sbh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and siseqno = &apos;&quot;+sblsh+&quot;&apos;&quot;;	
			db.ExcecutSQL(sql);		
			try{writeLog(&quot;单笔结算&quot;,czyid,czyxm,thisorgid,thisaccid,&quot;单笔成功，业务类型：&quot;+yszlywlx+&quot;,社保流水号：&quot;+sblsh);}catch(ee){}									
			db.Commit();
			/*失败也要通知业务，接口待定*/			
			retmsg = 1;			
		}else {
			if(db != null) db.Rollback();
			retmsg = &quot;交易出错&quot;+busiinfo;
		}

	}
	catch(e) {
		if (db != null) db.Rollback();
		try{writeLog(&quot;单笔结算&quot;,czyid,czyxm,thisorgid,thisaccid,&quot;单笔结算出错，业务类型：&quot;+ysywlx+&quot;,出错原因：&quot;+e.toString());}catch(ee){}		
		//throw new Exception(e.toString());
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
	return retmsg;
}


//写日志
function writeLog(Action,opusr,opusrnam,org,acc,note)
{
	var GRDID = &quot;YSZL_RJZLR&quot;;
	var type = &quot;银行账单笔录(社保发起)&quot;;
	var SYT= &quot;SBCW&quot;;
	note = note.toString();
	note = pub.EAFunc.Replace(note,&quot;&apos;&quot;,&quot;&apos;&apos;&quot;);
	if(note.length() &gt;= 4000) {
		note = note.substring(0,3999);
	}	
	var sql = &quot;insert into oplog(GRDID,TYPE,ACTION,OPUSR,OPUSRNAM,NOTE,ORG,ACC,SYT) values (&apos;&quot;+GRDID+&quot;&apos;,&apos;&quot;+type+&quot;&apos;,&apos;&quot;+Action+&quot;&apos;,&apos;&quot;+opusr+&quot;&apos;,&apos;&quot;+opusrnam+&quot;&apos;,&apos;&quot;+note+&quot;&apos;,&apos;&quot;+org+&quot;&apos;,&apos;&quot;+acc+&quot;&apos;,&apos;&quot;+SYT+&quot;&apos;)&quot;;
	pub.EADbTool.ExcecutSQL(sql);
}

ROW.DATDSC:
select rownum,a.* from (
select AAC001,AAC003,AAB001,AAE135,AIC263,AAE010,AAE007,BIC006,AAE002Min,AAE002Max,months,
       AAE208,BAE074,AAE076,BAZ901,BAZ902,BAC003
from (
select   AAC001,AAC003,AAB001,AAE135,AIC263,AAE010,AAE007,BIC006,
  min(substr(AAE002,0,6)) AAE002Min,max(substr(AAE002,8,13)) AAE002Max,
  nvl(max(substr(AAE002,8,13)),0)-nvl(min(substr(AAE002,0,6)),0)+1 months,
  AAE208,BAE074,AAE076,BAZ901,
  nvl(a.BAZ902,(select aaa103 from aa10 b where a.BAZ901=b.aaa102 and b.aaa100=&apos;BAZ901&apos; )) BAZ902,
  BAC003,BAE415,BAE419
from ac95 a
where AAE208=&apos;[%SFNY]&apos;
  and BAE074=&apos;[%DJH]&apos;
  and aae140=&apos;[%XZLX]&apos;
  and BAE419=&apos;[%DFYHBM]&apos; 
  --and nvl(BAZ901,&apos;%&apos;) like &apos;[%THM]%&apos;
  [%FILTERSTR] 
group by AAC001,AAC003,AAB001,AAE135,AIC263,AAE010,AAE007,BIC006,
  AAE208,BAE074,AAE076,BAZ901,BAZ902,BAC003,BAE415,BAE419
) 
order by BAE415,BAE419,AAB001,AAC001
) a
