//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.LoadScene(sheet,10);
	_this.AutoFixScreenWidth();
}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if (id == &quot;开始检查&quot;) {
		_this.SetCellText(0,8,1,&quot;&quot;);
		if (!confirm(&quot;检查科目,是否继续?&quot;)) return;

		_this.SetCellText(0,8,1,&quot;正在检查,请稍候...&quot;);
		var ret = invokeOSFunc(&quot;CheckKM&quot;,&quot;sbh=&quot;+G_ORGID+&quot;&zth=&quot;+G_ACCID.replace(G_ORGID,&quot;&quot;));
		_this.SetCellText(0,8,1,ret);
		alert(ret);


	}
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);
var xmldb = new JavaPackage(&quot;com.xlsgrid.net.xmldb&quot;);
var web = new JavaPackage(&quot;com.xlsgrid.net.web&quot;);
var orajdbc = new JavaPackage(&quot;oracle.jdbc&quot;);


//科目检查
function CheckKM()
{
	var db = null;
	var retVal = &quot;&quot;;

	try {
		db = new pub.EADatabase();
		var conn = db.GetConn();

		var errtext = &quot;&quot;;

		var praFunc = conn.prepareCall(&quot;{?=call cw_pack.check_kmbh(?,?,?,?)}&quot;);
		praFunc.registerOutParameter(1,java.sql.Types.VARCHAR);
		praFunc.setString(2,sbh);
		praFunc.setString(3,zth);
		praFunc.setString(4,&quot;&quot;);
		praFunc.registerOutParameter(4,java.sql.Types.VARCHAR);
		praFunc.setInt(5,4000);

		praFunc.executeUpdate();

		retVal = praFunc.getString(1);
		errtext = praFunc.getString(4);

		if (errtext != &quot;&quot; || retVal != &quot;1&quot;) {
			db.Rollback();
		}
		else {
			db.Commit();
		}

		if (errtext == &quot;&quot; || retVal == &quot;1&quot;) errtext = &quot;检查完毕,没有错误的科目!&quot;;

		return errtext;

	}
	catch(e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}
