function listFiles(host,user,userpwd,path)
{
	_this.LoadZXGFile(ZXGFILE0,-1,0);
	_this.SetMainCellRange(0,4,1,15,3);

	var param = new Object();
	param.host = host;
	param.user = user;
	param.userpwd = userpwd;
	param.path = path;
	var ret = invokeOSFuncExt(&quot;ftpListFiles&quot;,param);
	if (ret != &quot;&quot; && ret.substring(0,1) == &quot;[&quot;) {
		ret = ret.substring(1,ret.length-1);
		var files = ret.split(&quot;,&quot;);
		files.sort();
		var r = 4;
		for (var i=0;i&lt;files.length;i++) {
			if (r &gt;= 15) _this.AppendRow(0,r);
			_this.SetCellText(0,r,1,(i+1));
			_this.SetToBoolBox(0,r,2);
			var file = files[i].replace(/(^\s*)|(\s*$)/g, &quot;&quot;); //去除前后空格
			_this.SetCellText(0,r,3,file);
			r ++;
		}
	}
}


//确定
function selok()
{
	var param = new Object();
	param.host = host;
	param.user = user;
	param.userpwd = userpwd;

	param.path = _this.GetCellText(1,11,2);

	var path = _this.GetCellText(1,11,2);
	var filename = &quot;&quot;;
	for (var r=4;r&lt;_this.GetRowCount(0);r++) {
		if (_this.GetCellText(0,r,2) == &quot;1&quot;) {
			filename = _this.GetCellText(0,r,3);
			break;
		}
	}
	if (filename == &quot;&quot;) window.returnValue = &quot;&quot;;
	else window.returnValue = filename+&quot;,&quot;+host+&quot;,&quot;+user+&quot;,&quot;+userpwd+&quot;,&quot;+path;
	window.close();
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (sheet == 1 && row == 3 && col == 2) {
		setFtpInfo(newvalue);
	}
	else if (sheet == 0) {
		selectOneRow(sheet,row);
	}
}

function selectOneRow(sheet,row)
{
	for (var r=4;r&lt;_this.GetRowCount(sheet)-1;r++) {
		if (_this.GetCellText(sheet,r,1) != &quot;&quot;) {
			_this.SetCellText(sheet,r,2,&quot;0&quot;);
		}
	}
	_this.SetCellText(sheet,row,2,&quot;1&quot;);
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if (sheet == 0) {
		var dir = _this.GetCellText(sheet,row,3);
		if (dir != &quot;&quot; && dir.indexOf(&quot;.TXT&quot;) == -1 && dir.indexOf(&quot;.txt&quot;) == -1) {
			var curpath = _this.GetCellText(1,11,2);
			var path = curpath+&quot;/&quot;+dir;
			_this.SetCellText(1,11,2,path)
			listFiles(host,user,userpwd,path);
		}
		else if (dir.indexOf(&quot;.TXT&quot;) &gt; -1 || dir.indexOf(&quot;.txt&quot;) &gt; -1) {
			selectOneRow(sheet,row);
		}
	}
}



//返回
function getback()
{
	host = _this.GetCellText(1,5,2);
	user= _this.GetCellText(1,7,2);
	userpwd= _this.GetCellText(1,9,2);
	var Path = _this.GetCellText(1,11,2);
	var XPath = Path.substring(0,Path.lastIndexOf(&quot;/&quot;));
	_this.SetCellText(1,11,2,XPath);
	listFiles(host,user,userpwd,XPath);
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function ftpListFiles()
{
    try{
	var eaftp = new pub.EAFtpClient2();
	eaftp.connectServer(host,user,userpwd);  //登录FTP
	eaftp.ftpClient.changeWorkingDirectory(path);
	var files = eaftp.listRemoteFiles(&quot;*&quot;);
//	java.lang.System.out.println(&quot;ftp files=&quot;+files);
	eaftp.closeConnect();
	files = pub.EAFunc.Replace(files.toString(),&quot; &quot;,&quot;&quot;);
	return files;
    }catch(e) {return e.toString();}	
}
