ROW.GRDOSDS_VALUE:
var pub = new JavaPackage ( &quot;com.xlsgrid.net.pub&quot; );
var web = new JavaPackage ( &quot;com.xlsgrid.net.web&quot; );

function getAcc(request,response)
{	
	var ret = &quot;&quot;;
	try {
		var orgid = request.getParameter(&quot;orgid&quot;);
		//var sql = &quot;select id,name from v_acc where org=&apos;&quot;+orgid+&quot;&apos;&quot;;
		var sql = &quot;select b.zth,a.* from acc a,cw_ztb b where a.org=b.org and a.id=b.acc and a.syt=&apos;SBCW&apos; and a.org=&apos;&quot;+orgid+&quot;&apos; order by to_number(id)&quot;;
		var ds = pub.EADbTool.QuerySQL(sql);
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var id = ds.getStringAt(i,&quot;ID&quot;);
			var name = ds.getStringAt(i,&quot;NAME&quot;);
			var zth = ds.getStringAt(i,&quot;ZTH&quot;);
			ret += &quot;&lt;option value=\&quot;&quot;+id+&quot;\&quot;&gt;&quot;+zth+&quot;-&quot;+name+&quot;&lt;/option&gt;\n&quot;;
		}
	}
	catch (e) {
		ret = e.toString();
	}
	return ret;
}

function getAccDate(request,response)
{	
	var ret = &quot;&quot;;
	try {
		var accid = request.getParameter(&quot;accid&quot;);
		var sql = &quot;select substr(dqym,0,4)||&apos;-&apos;||substr(dqym,5,6)||&apos;-&apos;||to_char(sysdate,&apos;dd&apos;) logdat from cw_ztb where acc=&apos;&quot;+accid+&quot;&apos;&quot;;
		var ds = pub.EADbTool.QuerySQL(sql);
		if (ds.getRowCount() &gt; 0) {
			return ds.getStringAt(0,&quot;LOGDAT&quot;);
		}
		else {
			ret = pub.EADbTool.GetSQL(&quot;select to_char(sysdate,&apos;yyyy-mm-dd&apos;) from dual&quot;);
		}
	}
	catch (e) {
		ret = e.toString();
	}
	return ret;
}

