			        if(accid == &apos;&apos;) {
			            alert(&apos;请选择帐套信息!&apos;);
			            return false;
			        }
			        if(usrid == &apos;&apos;) {
			            alert(&apos;用户名不能为空!&apos;);
			            return false;
			        }
			        if(password == &apos;&apos;) {
			            alert(&apos;密码不能为空!&apos;);
			            return false;
			        }
