ROW.GRDJSDS_VALUE:
var ZXGFILE2 = &quot;&quot;;
var ZXGFILE3 = &quot;&quot;;

var sheet_zskpwj = 0;
var sheet_kpdata = 2;
var sheet_jfmx = 3;

var xzlxlList;
var hyList;
var _scny = &quot;&quot;; //生成年月

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.ShowTabBar(1);
	_this.SplitSheet(&quot;0,1&quot;,&quot;V&quot;,&quot;15%&quot;);
	_this.SplitSheet(2,&quot;V&quot;,&quot;50%&quot;);

	_this.SetAttribe(&quot;SHEET_2&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );
	_this.SetAttribe(&quot;SHEET_3&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );

	_this.SetToSelectBox(&quot;&quot;,0,1,2,&quot;V_CW_YYYY_MM&quot;,&quot;&quot;);
	try { _scny = SCNY; } catch(e) {}
	if (_scny == &quot;&quot;) {
		_this.SetCellText(0,1,2,G_LOGDAT.substring(0,7));
	}
	else {
		_this.SetCellText(0,1,2,_scny);
	}

	_this.SetToDateBox(&quot;&quot;,0,1,6,G_LOGDAT);

//	var kmList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_ACCITEM&where=mjbz=1 and XJYHBZ&lt;&gt;0 and QLBZ&lt;&gt;1 and nvl(RJZKM_LRBZ,&apos;1&apos;) = &apos;1&apos;&quot;),&quot;ID&quot;,&quot;NAME&quot;);
//	_this.SetToComboBox(&quot;&quot;,0,3,6,kmList);

	//拷盘标志
	var kpbzList = _this.CreateListValue();
	_this.SetListValue(kpbzList,&quot;0&quot;,&quot;未拷盘&quot;);
	_this.SetListValue(kpbzList,&quot;1&quot;,&quot;已拷盘&quot;);
	_this.SetListValue(kpbzList,&quot;2&quot;,&quot;全部&quot;);
	_this.SetToComboBox(&quot;&quot;,0,2,2,kpbzList);
	_this.SetCellText(0,2,2,&quot;2&quot;);

	//作废标志
	var zfbzList = _this.CreateListValue();
	_this.SetListValue(zfbzList,&quot;0&quot;,&quot;未作废&quot;);
	_this.SetListValue(zfbzList,&quot;1&quot;,&quot;已作废&quot;);
	_this.SetListValue(zfbzList,&quot;%&quot;,&quot;全部&quot;);
	_this.SetToComboBox(&quot;&quot;,0,2,4,zfbzList);
	_this.SetCellText(0,2,4,&quot;0&quot;);

	var allList = _this.CreateListValue();
	_this.SetListValue(allList,&quot;%&quot;,&quot;全部&quot;);

	//银行种类
	var yhzlList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_YHZL_ZF&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	_this.SetToComboBox(&quot;&quot;,0,2,6,yhzlList);
	_this.SetCellText(0,2,6,&quot;%&quot;);

	//险种类型
	xzlxlList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_XZLX&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	_this.SetToComboBox(&quot;&quot;,0,1,8,xzlxlList);
	_this.SetCellText(0,1,8,&quot;%&quot;);

	//回盘标志
	var hpbzList = _this.CreateListValue();
	_this.SetListValue(hpbzList,&quot;%&quot;,&quot;全部&quot;);
	_this.SetListValue(hpbzList,&quot;0&quot;,&quot;未回&quot;);
	_this.SetListValue(hpbzList,&quot;1&quot;,&quot;已回&quot;);
	_this.SetToComboBox(&quot;&quot;,0,2,8,hpbzList);
	_this.SetCellText(0,2,8,&quot;%&quot;);

	_this.AddToolbarButton(&quot;udf_GenFile&quot;,&quot;生成拷盘文件&quot;,&quot;生成拷盘文件&quot;,&quot;&quot;,1,3,3,40);
	_this.AddToolbarButton(&quot;udf_downKP&quot;,&quot;下载拷盘文件&quot;,&quot;下载拷盘文件&quot;,&quot;&quot;,1,3,3,40);
	var xmlmenu = &quot;&lt;item id=\&quot;typ_ftp\&quot; name=\&quot;1.FTP导入\&quot;/&gt;&lt;item id=\&quot;typ_seltxt\&quot; name=\&quot;2.择选本地文件导入\&quot;/&gt;&quot;;       
	_this.AddToolbarButton(&quot;btn_yhkp&quot;,&quot;银行回盘&quot;,&quot;银行回盘&quot;,xmlmenu,1,3,3,80);

	_this.AutoFixScreenWidth();

	ZXGFILE2 = _this.SaveTempZXGFile(2); 
	ZXGFILE3 = _this.SaveTempZXGFile(3);

	_this.SetToBoolBox(2,0,0);

	if (_scny != &quot;&quot;) {
		loadMainData();
	}
}

//加载数据
function loadMainData()
{	
	_this.SetToolbarString(&quot;正在加载数据...&quot;);

	_this.LoadZXGFile(ZXGFILE2,-1,2);
	_this.SetFixedRow(2,1);
	_this.SetFixedRow(2,1);

	_this.SetMainCellRange(2,1,1,_this.GetRowCount(2)-2,_this.GetColCount(2)-1);
	_this.SetAttribe(&quot;SHEET_2&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );

	var zth = G_ACCID.replace(G_ORGID,&quot;&quot;);
	var kpbz = _this.GetCellText(0,2,2);
	if (kpbz == 2) kpbz = &quot;&quot;;
	else if (kpbz == 0) kpbz = &quot; and a.bae421 is null&quot;;
	else if (kpbz == 1) kpbz = &quot; and a.bae421 is not null&quot;;
	var zfbz = _this.GetCellText(0,2,4);
	var ssyh = _this.GetCellText(0,2,6); //所属银行
	var hpbz = _this.GetCellText(0,2,8);
	var xzlx = _this.GetCellText(0,1,8); //险种类型
	var scny = (_this.GetCellText(0,1,2)).replace(&quot;-&quot;,&quot;&quot;); 

	var param = &quot;SBH=&quot;+G_ORGID+&quot;&ZTH=&quot;+zth+&quot;&SCNY=&quot;+scny+&quot;&ZFBZ=&quot;+escape(zfbz)+&quot;&XZLX=&quot;+escape(xzlx)+&quot;&KPBZ=&quot;+kpbz+&quot;&HPBZ=&quot;+escape(hpbz)
	var xml = _sql.query(&quot;main_zs&quot;,param);
//	_this.SetXmlDS(sheet_kpdata ,1,0,_this.GetRowCount(sheet_kpdata )-2,_this.GetColCount(sheet_kpdata )-1,xml);	
	_this.SetXmlDS(2,1,1,_this.GetRowCount(2)-2,_this.GetColCount(2)-1,xml);

	_this.SetToBoolBox(2,0,0);
	for (var r=_this.GetMainCellRangeRow1(2);r&lt;=_this.GetMainCellRangeRow2(2);r++) {
		if (_this.GetCellText(2,r,6) == &quot;&quot;) break;
		_this.SetToBoolBox(2,r,0);
		var kpbz = _this.GetCellText(2,r,1);
		if (kpbz == &quot;1&quot;) {
			_this.SetCellShowText(2,r,1,&quot;已拷&quot;);
			_this.SetCellColor(2,r,1,0,0,255);
		}
		else if (kpbz == &quot;0&quot;) {
			_this.SetCellShowText(2,r,1,&quot;未拷&quot;);
			_this.SetCellColor(2,r,1,255,0,0);
		}
		var hpbz = _this.GetCellText(2,r,2);
		if (hpbz == &quot;1&quot;) {
			_this.SetCellShowText(2,r,2,&quot;已回&quot;);
			_this.SetCellColor(2,r,2,0,0,255);
		}
		else if (hpbz == &quot;0&quot;) {
			_this.SetCellShowText(2,r,2,&quot;未回&quot;);
			_this.SetCellColor(2,r,3,255,0,0);
		}
	}
	_this.RefreshFormula();
	_this.SetToolbarString(&quot;&quot;);

}

//加载明细数据
function loadMainDetail(sheet,row)
{
	_this.LoadZXGFile(ZXGFILE3,-1,sheet_jfmx);
	_this.SetFixedRow(sheet_jfmx,1);
	_this.SetFixedRow(sheet_jfmx,1);

	_this.SetMainCellRange(sheet_jfmx,1,0,_this.GetRowCount(sheet_jfmx)-2,_this.GetColCount(sheet_jfmx)-1);
	_this.SetAttribe(&quot;SHEET_3&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );

	var djh = _this.GetCellText(2,row,8);
	var dwbh = _this.GetCellText(2,row,10)	

	var param = &quot;DJH=&quot;+djh+&quot;&DWBH=&quot;+dwbh;
	var xml = _sql.query(&quot;jfmx&quot;,param);
	_this.SetXmlDS(3,1,0,_this.GetRowCount(3)-2,_this.GetColCount(3)-1,xml);

	_this.RefreshFormula();		
}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if (id == &quot;查询&quot;) {
		loadMainData();
	}
}

//鼠标左键点击
function _thisOnMouseLClick(sheet,row,col)
{
	if (sheet == 2) {
		if (row &gt;= _this.GetMainCellRangeRow1(sheet) && row &lt;= _this.GetMainCellRangeRow2(sheet) && col != 0) {
			loadMainDetail(sheet,row);
		}
	}
	else if (sheet == 0) {

	}
}

function showKM()
{
	var obj = new Object();
        obj.filter = &quot;xjyhbz &lt;&gt; 0&quot;;
        obj.mjbz = &quot;1&quot;;
        obj.bz = &quot;&quot;;
        obj.jb = 0;
        obj.kmbh = &quot;&quot;;
	var retVal = window.showModalDialog(&quot;show.sp?grdid=CW_KMSEL&quot;,obj,&quot;dialogWidth=600px;dialogHeight=500px&quot;);
	cur_kmbh = retVal;
	if (cur_kmbh != &quot;&quot; && cur_kmbh != &quot;undefined&quot; && cur_kmbh != undefined) {
		_this.SetCellText(0,3,6,cur_kmbh);
		_thisOnCellModify(0,3,6,&quot;&quot;,cur_kmbh);
	}

}

function getSelectXml(sheet)
{
	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;&lt;ROWSET&gt;\n&quot;;
	var num = 0;
	for (var r=_this.GetMainCellRangeRow1(sheet);r&lt;=_this.GetMainCellRangeRow2(sheet);r++) {
		var flag = _this.GetCellText(sheet,r,0);
		var sfny = _this.GetCellText(sheet,r,6);
		var lsh = _this.GetCellText(2,r,22);
		if (flag == &quot;1&quot; && sfny != &quot;&quot;) {
			num ++;
			xml += &quot;&lt;ROW num=\&quot;&quot;+num+&quot;\&quot;&gt;\n&quot;;
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,5)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,5) +&quot;&lt;/&quot;+_this.GetColName(sheet,5)+&quot;&gt;\n&quot;;		//实付年月
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,6)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,6) +&quot;&lt;/&quot;+_this.GetColName(sheet,6)+&quot;&gt;\n&quot;;		//所属年月 开始
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,7)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,7) +&quot;&lt;/&quot;+_this.GetColName(sheet,7)+&quot;&gt;\n&quot;;		//所属年月 截止
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,8)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,8) +&quot;&lt;/&quot;+_this.GetColName(sheet,8)+&quot;&gt;\n&quot;;		//单据号
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,10)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,10) +&quot;&lt;/&quot;+_this.GetColName(sheet,10)+&quot;&gt;\n&quot;;	//单位编号
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,14)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,14) +&quot;&lt;/&quot;+_this.GetColName(sheet,14)+&quot;&gt;\n&quot;;	//单位银行户名
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,15)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,15) +&quot;&lt;/&quot;+_this.GetColName(sheet,15)+&quot;&gt;\n&quot;;	//单位银行账户
			xml += &quot;&lt;&quot;+_this.GetColName(sheet,16)+&quot;&gt;&quot; + _this.GetCellText(sheet,r,16) +&quot;&lt;/&quot;+_this.GetColName(sheet,16)+&quot;&gt;\n&quot;;	//单位银行名称
			xml += &quot;&lt;/ROW&gt;\n&quot;;		
		}
	}
	xml += &quot;&lt;/ROWSET&gt;&quot;;
	return xml;
}

//生成文件
function GenFile()
{
	var kpdat = _this.GetCellText(0,1,6);

	if (!confirm(&quot;生成&quot;+kpdat+&quot;的拷盘文件，是否继续？&quot;)) return;

	var cnt = 0;
	for (var r=_this.GetMainCellRangeRow1(2);r&lt;=_this.GetMainCellRangeRow2(2);r++) {
		var flag = _this.GetCellText(2,r,0);
		var kpbz = _this.GetCellText(2,r,1);
		var sfny = _this.GetCellText(2,r,5);
		var hpbz = _this.GetCellText(2,r,2);
		if (flag == 1 && sfny != &quot;&quot;) {
			cnt ++;
		}
		if (flag == 1 && kpbz == 1) {
			alert(&quot;该笔记录已生成拷盘，不能重复拷盘！\n银行：&quot;+dfyh+&quot; \n险种：&quot;+xzlx+&quot; \n单据号：&quot;+djh+&quot; \n批次号：&quot;+pch+&quot; \n金额：&quot;+mny);
			_this.SetFocusCell(2,r,5);
			return;
		}
	}
	if (cnt == 0) {
		alert(&quot;没有选中记录！&quot;);
		return;
	}

	var param = new Object();
	param.kpdat = kpdat;
	param.xml = getSelectXml(2);	
	param.thisorgid = G_ORGID;
	param.thisaccid = G_ACCID;
	param.usrid = G_USRID;
	param.czyxm = G_USRNAM;

	try {
		var ret = invokeOSFuncExt(&quot;GenKPFile&quot;,param);
		if (ret.substring(0,1) == &quot;1&quot;) {
			alert(&quot;生成[&quot;+kpdat+&quot;]拷盘文件成功！&quot;+ret.substring(2));
			loadMainData();
//			for (var r=_this.GetMainCellRangeRow1(2);r&lt;=_this.GetMainCellRangeRow2(2);r++) {
//				var flag = _this.GetCellText(2,r,0);
//				var kpbz = _this.GetCellText(2,r,2);
//				if (flag == 1 && kpbz == 0) {
//					_this.SetCellText(2,r,2,&quot;1&quot;);
//					_this.SetCellText(2,r,5,kpdat.replace(/\-/g,&quot;&quot;));
//					_this.SetCellShowText(2,r,2,&quot;已拷&quot;);
//					_this.SetCellColor(2,r,2,0,0,255);
//				}
//			}
		}
		else {
			alert(&quot;生成拷盘文件不成功：&quot;+ret);
		}
	}
	catch (e) {
		alert(e);
	}
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (sheet == 2) {
		if (row == 0 && col == 0) {
			for (var r=_this.GetMainCellRangeRow1(sheet);r&lt;=_this.GetMainCellRangeRow2(sheet);r++) {
				_this.SetCellText(sheet,r,0,newvalue);
			}
		}	
		else if (col == 1) {
			if (newvalue == 1) {
				if (!confirm(&quot;作废该笔记录，是否继续？&quot;)) return;
			}
			else if (newvalue == 0) {
				if (!confirm(&quot;取消作废该笔记录，是否继续？&quot;)) return;
			}
			var param = new Object();
			param.zfbz = newvalue;
			param.thisorgid = G_ORGID;
			param.thisaccid = G_ACCID;
			param.czyxm = G_USRNAM;
			param.djh = _this.GetCellText(sheet,row,9);
			param.xzlx = _this.GetCellText(sheet,row,11);
			param.pch = _this.GetCellText(sheet,row,18);
			param.yhbm = _this.GetCellText(sheet,row,21);
			param.lsh = _this.GetCellText(sheet,row,22);
			var ret = invokeOSFuncExt(&quot;zfSave&quot;,param);
			alert(ret);
		}	
	}
	else if (sheet == 0) {
		if (row == 1 && col == 2) {
			var ym1 = _this.GetCellText(sheet,1,2);
			if (ym1.indexOf(&quot;-&quot;) == -1) {
				var ym11 = ym1.substring(0,4)+&quot;-&quot;+ym1.substring(4,6);
				_this.SetCellText(sheet,1,2,ym11);
			}
			ym1 = ym1.replace(&quot;-&quot;,&quot;&quot;);
			if (ym1.length &lt; 6) {
				alert(&quot;输入的期限不正确，请检查！格式：YYYYMM/YYYY-MM 201501/2015-01&quot;);
				_this.SetCellText(sheet,row,col,oldvalue);
			}

		}	
		loadMainData();
	}
}

function selectOneRow(sheet,row)
{
	var flg = _this.GetCellText(sheet,row,0);
	if (flg == 1) {
		_this.SetCellText(sheet,row,0,0);
		setOneRowBkColor(sheet,row,255,255,255);		
	}
	else {
		_this.SetCellText(sheet,row,0,1);
		setOneRowBkColor(sheet,row,255,236,139);
	}
}

function setOneRowBkColor(sheet,row,r,g,b)
{
	for (var c=0;c&lt;_this.GetColCount(sheet);c++) {
		_this.SetCellBkColor(sheet,row,c,r,g,b);
	}
	_this.SetCellFocus(sheet,row,0);
	_this.Redraw();
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if (sheet == 2 && row &gt;= _this.GetMainCellRangeRow1(2) && row &lt;= _this.GetMainCellRangeRow2(2)) {
		selectOneRow(sheet,row);
	}
}

//下载拷盘文件
function downKP()
{
	var ret = &quot;&quot;;
	var cnt = 0;
	var dat = _this.GetCellText(0,1,6);
	var mmdir = dat.substring(0,4) + dat.substring(5,7);
	var fso = new ActiveXObject(&quot;Scripting.FileSystemObject&quot;); 
	for (var r=_this.GetMainCellRangeRow1(2);r&lt;=_this.GetMainCellRangeRow2(2);r++) {
		var flag = _this.GetCellText(2,r,0);
		var kpbz = _this.GetCellText(2,r,2);
		var kprq = _this.GetCellText(2,r,5);
		var yhbm = _this.GetCellText(2,r,21);
		var kpwjm = _this.GetCellText(2,r,23);
		var kpwjmSum = kpwjm.substring(0,kpwjm.lastIndexOf(&quot;.&quot;))+&quot;_SUM&quot;+kpwjm.substring(kpwjm.lastIndexOf(&quot;.&quot;));
		if (flag == 1 && kpbz == &quot;1&quot;) {
			cnt ++;
			_sql.querytods(&quot;YHFTPINFO&quot;,&quot;YHBM=&quot;+yhbm);
			var wjml = _this.XMLDS_GetString(0,&quot;WJML&quot;);
			wjml += &quot;\\&quot; + mmdir;
			if (!fso.FolderExists(wjml)) {
				fso.CreateFolder(wjml); //创建文件夹目录
			}
			var txt = invokeOSFunc(&quot;downLoadFile&quot;,&quot;filename=&quot;+kpwjm+&quot;&mmdir=&quot;+mmdir);
			var filename = wjml+&quot;\\&quot;+kpwjm;
			if (fso.FileExists(filename)) {
				if (!confirm(&quot;拷盘文件&quot;+filename+&quot;已存在，是否覆盖？&quot;)) continue;
			}			
			ret += _this.SaveTextFile(filename,txt,&quot;*.txt&quot;) + &quot;\n&quot; ;		
			txt = invokeOSFunc(&quot;downLoadFile&quot;,&quot;filename=&quot;+kpwjmSum+&quot;&mmdir=&quot;+mmdir);
			ret += _this.SaveTextFile(wjml+&quot;\\&quot;+kpwjmSum,txt,&quot;*.txt&quot;) + &quot;\n&quot; ;		
		}
	}

	if (cnt == 0) {
		alert(&quot;没有拷盘数据！&quot;);
	}

	if (ret != &quot;&quot; && cnt &gt; 0) alert(&quot;下载成功！\n&quot;+ret);


}

//银行回盘
function YHCome()
{
	var hpdat = _this.GetCellText(0,1,6); //回盘日期
	//var retVal = window.showModalDialog(&quot;show.sp?grdid=RJZ_YHHP&quot;,null,&quot;dialogWidth=650px;dialogHeight=500px&quot;);
	var retVal = window.showModalDialog(&quot;show.sp?grdid=FTPUTIL&szbz=2&bank=3&quot;,null,&quot;dialogWidth=1000px;dialogHeight=500px&quot;);
	//alert(retVal);
	if (retVal != &quot;&quot; && retVal != &quot;undefined&quot; && retVal != undefined) {
		if (retVal.split(&quot;,&quot;).length != 5) return;

		var param = new Object();
		param.typ = 1;
		param.hpdat = hpdat;
		param.thisorgid = G_ORGID;
		param.thisaccid = G_ACCID;
		param.czyxm = G_USRNAM;
		param.hpfilename = &quot;&quot;;
		param.text = &quot;&quot;;
		param.ftpinfo = retVal;

		var jobid = invokeOSFunc(&quot;getSysDate&quot;,&quot;&quot;);
		param.jobid = jobid;
		param.jobnam = &quot;银行回盘导入进程&quot;;

//		var ret = invokeOSFunc(&quot;yhhpImport&quot;,&quot;typ=1&ftpinfo=&quot;+retVal+&quot;&hpdat=&quot;+hpdat+&quot;&thisorgid=&quot;+G_ORGID+&quot;&thisaccid=&quot;+G_ACCID+&quot;&czyxm=&quot;+G_USRNAM+&quot;&hpfilename=&text=&jobid=&quot;+jobid+&quot;&jobseqid=&quot;+jobseqid+&quot;&jobnam=&quot;+jobnam);
//		alert(ret);

		_sql.querytods(&quot;QueryRunOSTimer&quot;,&quot;jobid=YHHP&quot;+jobid);
		if( 1*_this.XMLDS_GetStringAt(0,0)&gt;0 ){
			//有一个作业正在运行，打开进度框
			window.showModalDialog(&quot;show.sp?grdid=RunOSTimer&jobseqid=YHHP&quot;+jobid,&quot;&quot;,&quot;dialogwidth:600pt;dialogheight:500pt;help:yes;resizable:yes;&quot; );
		}
		else {
			_sql.querytods(&quot;QueryRunOSTimerExist&quot;,&quot;jobid=YHHP&quot;+jobid);
			if ( 1*_this.XMLDS_GetStringAt(0,0) &gt; 0) {
				if (confirm( &quot;该进程已经执行过，是否重新执行？&quot;) == 1) {
					_sql.run(&quot;DeleteQueryRunOSTimer&quot;,&quot;jobid=YHHP&quot;+jobid);
				}
				else {
					window.showModalDialog(&quot;show.sp?grdid=RunOSTimer&jobseqid=YHHP&quot;+jobid,&quot;&quot;,&quot;dialogwidth:600pt;dialogheight:500pt;help:yes;resizable:yes;&quot; );
					return ;
				}
			}

			var ret = invokeOSFuncExt(&quot;genbatch&quot;,param) ;    
			if ( ret != &quot;&quot; ) {
				window.showModalDialog(&quot;show.sp?grdid=RunOSTimer&jobseqid=&quot;+ret ,&quot;&quot;,&quot;dialogwidth:600pt;dialogheight:500pt;help:yes;resizable:yes;&quot; );
			}
		}
	}

}

//点击菜单项
function _thisOnMenuItemSelect(sheet,row,col,menuitemid)
{
	if (menuitemid == &quot;typ_ftp&quot;) {
		YHCome();
	}
	else if (menuitemid == &quot;typ_seltxt&quot;) {
		var inputObj = document.createElement(&apos;input&apos;)
	        inputObj.setAttribute(&apos;id&apos;,&apos;_ef&apos;);
	        inputObj.setAttribute(&apos;type&apos;,&apos;file&apos;);
	        inputObj.setAttribute(&quot;style&quot;,&apos;visibility:hidden&apos;);
	        document.body.appendChild(inputObj);
	        inputObj.click();
	        var filename = inputObj.value ;
	        if (filename == &quot;&quot; || filename == undefined) {
	        	return;
	        }

	        var txt = _this.LoadTextFile(filename);

		var hpdat = _this.GetCellText(0,1,6); //回盘日期
		_this.SetToolbarString(&quot;正在导入银行回盘文件,请稍候...&quot;);
		var param = new Object();
		param.typ = 2;
		param.hpdat = hpdat;
		param.thisorgid = G_ORGID;
		param.thisaccid = G_ACCID;
		param.usrid = G_USRID;
		param.czyxm = G_USRNAM;
		param.hpfilename = filename;
		param.text = txt;
		param.ftpinfo = &quot;&quot;;
		//_this.SetCellText(0,3,6,txt);
		//&quot;typ=1&ftpinfo=&quot;+retVal+&quot;&hpdat=&quot;+hpdat+&quot;&thisorgid=&quot;+G_ORGID+&quot;&thisaccid=&quot;+G_ACCID+&quot;&czyxm=&quot;+G_USRNAM
//		var ret = invokeOSFuncExt(&quot;yhhpImport&quot;,param);
//		alert(ret);

		var jobid = invokeOSFunc(&quot;getSysDate&quot;,&quot;&quot;);
		param.jobid = jobid;
		param.jobnam = &quot;银行回盘导入进程&quot;;

		_sql.querytods(&quot;QueryRunOSTimer&quot;,&quot;jobid=YHHP&quot;+jobid);
		if( 1*_this.XMLDS_GetStringAt(0,0)&gt;0 ){
			//有一个作业正在运行，打开进度框
			window.showModalDialog(&quot;show.sp?grdid=RunOSTimer&jobseqid=YHHP&quot;+jobid,&quot;&quot;,&quot;dialogwidth:600pt;dialogheight:500pt;help:yes;resizable:yes;&quot; );
		}
		else {
			_sql.querytods(&quot;QueryRunOSTimerExist&quot;,&quot;jobid=YHHP&quot;+jobid);
			if ( 1*_this.XMLDS_GetStringAt(0,0) &gt; 0) {
				if (confirm( &quot;该进程已经执行过，是否重新执行？&quot;) == 1) {
					_sql.run(&quot;DeleteQueryRunOSTimer&quot;,&quot;jobid=YHHP&quot;+jobid);
				}
				else {
					window.showModalDialog(&quot;show.sp?grdid=RunOSTimer&jobseqid=YHHP&quot;+jobid,&quot;&quot;,&quot;dialogwidth:600pt;dialogheight:500pt;help:yes;resizable:yes;&quot; );
					return ;
				}
			}

			var ret = invokeOSFuncExt(&quot;genbatch&quot;,param) ;    
			if ( ret != &quot;&quot; ) {
				window.showModalDialog(&quot;show.sp?grdid=RunOSTimer&jobseqid=&quot;+ret ,&quot;&quot;,&quot;dialogwidth:600pt;dialogheight:500pt;help:yes;resizable:yes;&quot; );
			}
		}

		_this.SetToolbarString(&quot;&quot;);

	}

}

