//创建临时表
function createTempTable(db)
{
	var tempTable = db.GetSQL(&quot;select &apos;TMP_YHKP&apos;||to_char(sysdate,&apos;yymmddhh24miss&apos;) from dual&quot;);
	var sql = &quot;create table &quot;+tempTable+&quot; (zfbz varchar(20),sfny varchar(20),djh varchar(20),xzlx varchar(20),dfyhbm varchar(20),dfyh varchar(50),ffpc varchar(20))&quot;;
	db.ExcecutSQL(sql);	
	return tempTable;
}

//drop临时表
function dropTempTable(db,tempTable)
{
	var sql = &quot;drop table &quot;+tempTable;
	db.ExcecutSQL(sql);
}

