				fileStrSum += upwjm + &quot;    &quot; + sumHS + &quot;    &quot; + sumMny + &quot;    \n&quot;;
				fileStrSum += &quot;    合计    &quot;+ sumHS + &quot;    &quot; + sumMny + &quot;    \n&quot;;
