				//var fileName2 = storeRoot+&quot;/&quot;+upwjmSum;
				pub.EAFunc.WriteToFileEx(fileName2,fileStrSum,false);  //生成本地文件,覆盖方式

				var lognote = &quot;生成拷盘文件：&quot;+fileName2;
				sql = &quot;insert into oplog(grdid,type,action,opdat,opusr,opusrnam,note,acc,syt,org)
					values(&apos;RJZ_YWKP&apos;,&apos;发放拷盘&apos;,&apos;生成&apos;,sysdate,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;)&quot;.format([usrid,czyxm,lognote,thisaccid,&quot;SBCW&quot;,thisorgid]);
				db.ExcecutSQL(sql);

//				//暂时不上传FTP
				//上传FTP
//				var eaftp = new pub.EAFtpClient2();
//				eaftp.ftpClient = new ftppack.FTPClient();
//				eaftp.ftpClient.setControlEncoding(&quot;UTF-8&quot;); //处理文件名是中文乱码问题
//				eaftp.ftpClient.connect(ftpip);
//				eaftp.ftpClient.login(ftpuser, ftppasswd);
				//eaftp.connectServer(ftpip,ftpuser,ftppasswd);  //登录FTP//不用，采用上面几行代码登录代替

//				var localFile = new java.io.File(fileName);
//				eaftp.ftpClient.changeWorkingDirectory(ftploginpath + &quot;/&quot; + ftpuppath);
//				var inputStream = new java.io.FileInputStream(localFile);
//				var b = eaftp.ftpClient.storeFile(upwjm,inputStream);
//				if (b == true) { //上传成功
					//更新拷盘标志信息
					sql = &quot;update ac95 set BAE421=to_char(sysdate,&apos;yyyymmddhh24miss&apos;),BAC001=&apos;&quot;+upwjm+&quot;&apos;,BAC002=sysdate,AAE036=sysdate,AAE011=&apos;&quot;+czyxm+&quot;&apos; 
						where AAE208=&apos;&quot;+sfny+&quot;&apos;
						  and BAE074 in (&quot;+pub.EAFunc.SQLIN(djh)+&quot;)
						  and aae140=&apos;&quot;+xzlx+&quot;&apos;
						  and BAE419=&apos;&quot;+dfyhbm+&quot;&apos;
						  and BAE415=&apos;&quot;+ffpc+&quot;&apos;&quot;;
					 db.ExcecutSQL(sql);	

					//上传合计文件
//					var localFile2 = new java.io.File(fileName2);
//					var inputStream2 = new java.io.FileInputStream(localFile2);
//					eaftp.ftpClient.storeFile(upwjmSum2,inputStream2);
					//删除本地生成的临时文件
					//var f = new java.io.File(fileName2);
					//if(f.exists()) f.delete();
//				}
//				eaftp.closeConnect();

				//删除本地生成的临时文件
				//var f = new java.io.File(fileName);
