			}

		}

		db.Commit();

		return &quot;1~&quot;+tips; //&quot;生成拷盘文件成功！&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return &quot;生成拷盘出错:&quot;+e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

//补全长度空格
function formatStr(str,len,bef,db)
{	
	var sql = &quot;&quot;;
	if(bef == &quot;0&quot;){
		str = &quot;&quot;+str;
		var slen = getWordCount(str);
		if (slen &gt; len) return str.substring(0,len);
		var trim = &quot;&quot;;
		for (var i=0;i&lt;(len-slen);i++) {
			trim += &quot; &quot;;
		}
		return str + trim;
	}else if(bef == &quot;1&quot;){
		sql = &quot;select LPAD(trim(to_char(&quot;+str+&quot;,&apos;999999999990.00&apos;)),&quot;+len+&quot;) from dual&quot;;
		str = db.GetSQL(sql);
		return str ;
	}

}

function getWordCount(s)
{
        var length = 0;
        for(var i = 0; i &lt; s.length(); i++) {
		var ascii = java.lang.Character.codePointAt(s, i);
		if(ascii &gt;= 0 && ascii &lt;=255)
			length++;
		else
			length += 2;
	}
        return length;
}

//文件名
function getKPFileName(xzlx,filename)
{
	var retFileName = &quot;&quot;;
	if (xzlx == &quot;21&quot; && filename.indexOf(&quot;sy_&quot;) == -1) retFileName = &quot;sy_&quot; + filename;
	else if (xzlx == &quot;12&quot; && filename.indexOf(&quot;JB_&quot;) == -1) retFileName = &quot;JB_&quot; + filename;
	else if (xzlx == &quot;13&quot; && filename.indexOf(&quot;JB_&quot;) == -1) retFileName = &quot;JB_&quot; + filename;
	else if (xzlx == &quot;41&quot; && filename.indexOf(&quot;gs_&quot;) == -1) retFileName = &quot;gs_&quot; + filename;
	else if (xzlx == &quot;51&quot; && filename.indexOf(&quot;syu_&quot;) == -1) retFileName = &quot;syu_&quot; + filename;
	else if (xzlx == &quot;991&quot; && filename.indexOf(&quot;js_&quot;) == -1) retFileName = &quot;js_&quot; + filename;
	else if (xzlx == &quot;992&quot; && filename.indexOf(&quot;jzg_&quot;) == -1) retFileName = &quot;jzg_&quot; + filename;
	else if (xzlx == &quot;993&quot; && filename.indexOf(&quot;jsc_&quot;) == -1) retFileName = &quot;jsc_&quot; + filename;
	else if (xzlx == &quot;994&quot; && filename.indexOf(&quot;lxjt_&quot;) == -1) retFileName = &quot;lxjt_&quot; + filename;
	else if (xzlx == &quot;995&quot; && filename.indexOf(&quot;lgmf_&quot;) == -1) retFileName = &quot;lgmf_&quot; + filename;
	else if (xzlx == &quot;996&quot; && filename.indexOf(&quot;jlg_&quot;) == -1) retFileName = &quot;jlg_&quot; + filename;
	else if (xzlx == &quot;997&quot; && filename.indexOf(&quot;hn_&quot;) == -1) retFileName = &quot;hn_&quot; + filename;
	else if (xzlx == &quot;998&quot; && filename.indexOf(&quot;zydg_&quot;) == -1) retFileName = &quot;zydg_&quot; + filename;

	return retFileName;
}


//银行回盘处理
function yhhpImport()
{
	var ftpcfg = &quot;&quot;;
	var filename = &quot;&quot;;
	var host = &quot;&quot;;
	var user = &quot;&quot;;
	var userpwd = &quot;&quot;;
	var path = &quot;&quot;;

	var db = null;
	var sql = &quot;&quot;;
	var line = &quot;&quot;;
	var jkret = &quot;&quot;;
	var rowcnt = 0;
	var failecnt = 0;
	var process = 0;

	try {
		db = new pub.EADatabase();

		var iuo_ywjk = new SBCW_IYWJK(); //业务接口类
		var ywjkbz = iuo_ywjk.getYWJKBZ(db,thisaccid);

		notify(jobseqid,10,&quot;准备导入银行回盘文件...&quot;,&quot;run&quot;);//名称为空表示只要更新进度
		process = 10;

		var uo_ywjk = new SBCW_IYWJK(); //业务接口

		var txt = &quot;&quot;;

		if (typ == 1) {
			ftpcfg = ftpinfo.split(&quot;,&quot;);
			filename = ftpcfg[0];
			host = ftpcfg[1];
			user = ftpcfg[2];
			userpwd = ftpcfg[3];
			path = ftpcfg[4];

			var eaftp = new pub.EAFtpClient2();
			eaftp.connectServer(host,user,userpwd);  //登录FTP
			eaftp.ftpClient.changeWorkingDirectory(path);
			var destFile = &quot;/u/dsdf/&quot;+filename;  //下载保存
			var b = eaftp.download(path+&quot;/&quot;+filename,destFile);
			eaftp.closeConnect();
			if (b == true) {
				txt = pub.EAFunc.readFile(destFile);
			}
		}
		else if (typ == 2) {
			txt = text;
			filename = hpfilename;
		}
		if (txt == &quot;&quot;) {
			if (notify(jobseqid,100,&quot;导入文件为空！&quot;,&quot;end&quot;)==&quot;ERROR&quot;) throw new Exception ( &quot;进程已经中断&quot; );
			return &quot;导入文件为空！&quot;;
		}

		var sr = new java.io.StringReader(txt);
		var br = new java.io.BufferedReader(sr);

		notify(jobseqid,20,&quot;正在导入银行回盘文件...&quot;,&quot;run&quot;);//名称为空表示只要更新进度
		process = 20;

		while( (line=br.readLine()) != null) { //读取文本行记录
			if (line == &quot;&quot;) continue;
			rowcnt ++;
			var grbh = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,0,16),&quot; &quot;,&quot;&quot;);		/*个人编号 16*/
			var xm = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,16,50),&quot; &quot;,&quot;&quot;);		/*姓名 50*/
			var dwbh = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,66,16),&quot; &quot;,&quot;&quot;);	/*单位编号 16*/
			var sfzh = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,82,18),&quot; &quot;,&quot;&quot;);	/*身份证 18*/
			var dfje = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,100,16),&quot; &quot;,&quot;&quot;);	/*代发金额 16*/
			var dfzh = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,118,40),&quot; &quot;,&quot;&quot;);	/*代发账号 40*/
			var zcode = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,158,6),&quot; &quot;,&quot;&quot;);	/*邮政编码 6*/
			var addr = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,164,100),&quot; &quot;,&quot;&quot;);	/*通信地址 单据号||批次号 100*/
			var djh = pub.EAFunc.Replace(pub.EAFunc.bSubstring(addr,0,addr.indexOf(&quot;||&quot;)),&quot; &quot;,&quot;&quot;);
			var pch = pub.EAFunc.Replace(pub.EAFunc.bSubstring(addr,addr.indexOf(&quot;||&quot;)+2,addr.length()),&quot; &quot;,&quot;&quot;);
			var cgbz = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,264,1),&quot; &quot;,&quot;&quot;);	/*成功发放标志 1*/
			var sbbz = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,265,80),&quot; &quot;,&quot;&quot;);	/*不成功发放备注 80*/
			var zqzh = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,345,40),&quot; &quot;,&quot;&quot;);	/*返回正确帐号 40*/
			var zqyhm = pub.EAFunc.Replace(pub.EAFunc.bSubstring(line,385,50),&quot; &quot;,&quot;&quot;);	/*返回正确户名 50*/

			if (rowcnt % 500 == 0) {
				notify(jobseqid,60,&quot;导入银行回盘文件，行号：&quot;+rowcnt+&quot;...&quot;,&quot;run&quot;);//名称为空表示只要更新进度
				process = 60;
			}

			sql = &quot;select * from ac95 where  AAC001=&apos;&quot;+grbh+&quot;&apos; and AAB001=&apos;&quot;+dwbh+&quot;&apos; /*and AAE135=&apos;&quot;+sfzh+&quot;&apos;*/ and BAE074=&apos;&quot;+djh+&quot;&apos; and BAE415=&apos;&quot;+pch+&quot;&apos; 
				and BAZ901 is not null and BAE036 is not null&quot;;
			if (sfzh != &quot;&quot;) sql += &quot; and AAE135=&apos;&quot;+sfzh+&quot;&apos;&quot;;
			var hpds = db.QuerySQL(sql);
			if (hpds.getRowCount() &gt; 0) { //已回过盘不允许重复
				db.Rollback();
				notify(jobseqid,100,&quot;已经回盘，不允许重复回盘！出错行号:&quot;+rowcnt+&quot;.&quot;+sql,&quot;end&quot;);//名称为空表示只要更新进度
			}


			//更新回盘信息
			sql = &quot;update ac95 set BAZ901=&apos;&quot;+cgbz+&quot;&apos;,BAZ902=&apos;&quot;+sbbz+&quot;&apos;,BAC004=to_date(&apos;&quot;+hpdat+&quot;&apos;,&apos;yyyy-mm-dd&apos;),BAC003=&apos;&quot;+filename+&quot;&apos;,BAE011=&apos;&quot;+czyxm+&quot;&apos;,BAE036=sysdate 
				where  AAC001=&apos;&quot;+grbh+&quot;&apos; and AAB001=&apos;&quot;+dwbh+&quot;&apos; /*and AAE135=&apos;&quot;+sfzh+&quot;&apos;*/
				  and BAE074=&apos;&quot;+djh+&quot;&apos; and BAE415=&apos;&quot;+pch+&quot;&apos;&quot;;
			if (sfzh != &quot;&quot;) sql += &quot; and AAE135=&apos;&quot;+sfzh+&quot;&apos;&quot;;

			var upcnt = db.ExcecutSQL(sql);
			if (upcnt == 0) {
				db.Rollback();
				notify(jobseqid,100,&quot;更新回盘记录不成功，请检查回盘文件数据格式是否正确！出错行号:&quot;+rowcnt+&quot;.&quot;+sql,&quot;end&quot;);//名称为空表示只要更新进度
				return &quot;更新回盘记录不成功，请检查回盘文件数据格式是否正确！出错行号:&quot;+rowcnt+&quot;.\n&quot;;
			}

			//sql = &quot;select * from ac95 where  AAC001=&apos;&quot;+grbh+&quot;&apos; and AAB001=&apos;&quot;+dwbh+&quot;&apos; and AAE135=&apos;&quot;+sfzh+&quot;&apos; and BAE074=&apos;&quot;+djh+&quot;&apos; and BAE415=&apos;&quot;+pch+&quot;&apos;&quot;;
			sql = &quot;select a.BAZ610,a.AAA027,a.BAZ901,nvl(a.BAZ902,(select aaa103 from aa10 b where a.BAZ901=b.aaa102 and b.aaa100=&apos;BAZ901&apos; )) BAZ902,a.BAC004,a.BAE415,a.AAE076,a.BAE036
				from ac95 a where  AAC001=&apos;&quot;+grbh+&quot;&apos; and AAB001=&apos;&quot;+dwbh+&quot;&apos; /*and AAE135=&apos;&quot;+sfzh+&quot;&apos;*/ and BAE074=&apos;&quot;+djh+&quot;&apos; and BAE415=&apos;&quot;+pch+&quot;&apos;&quot;;
			if (sfzh != &quot;&quot;) sql += &quot; and AAE135=&apos;&quot;+sfzh+&quot;&apos;&quot;;

			var tmpds = db.QuerySQL(sql);
			if (tmpds.getRowCount() &gt; 0) {
				//调用接口（银海、东软）通知业务 (一笔明细调用一次接口）
				var BAZ610 = tmpds.getStringAt(0,&quot;BAZ610&quot;); //业务内部序号
				//var AAE117 = tmpds.getStringAt(0,&quot;AAE117&quot;); //支付状态
				var AAA027 = tmpds.getStringAt(0,&quot;AAA027&quot;); //所属统筹区
				var BAZ901 = tmpds.getStringAt(0,&quot;BAZ901&quot;); //回盘结果状态码
				var BAZ902 = tmpds.getStringAt(0,&quot;BAZ902&quot;); //回盘结果失败原因
				var BAC004 = tmpds.getStringAt(0,&quot;BAC004&quot;); //回盘时间
				var BAE415 = tmpds.getStringAt(0,&quot;BAE415&quot;); //发放批次
				var AAE076 = tmpds.getStringAt(0,&quot;AAE076&quot;); //财务流水号
				var BAE036 = tmpds.getStringAt(0,&quot;BAE036&quot;); //回盘时间

				if (AAE076 == &quot;&quot;) {  //还没有存入日记帐不能做回盘操作
					db.Rollback();
					notify(jobseqid,100,&quot;该拷盘数据财务尚未记日记帐，请先到批量录模块存入日记帐后再进行回盘操作！&quot;,&quot;end&quot;);//名称为空表示只要更新进度
					return &quot;该拷盘数据财务尚未记日记帐，请先到批量录模块存入日记帐后再进行回盘操作！&quot;;
				}

				sql = &quot;select czyxm,to_char(czysj,&apos;yyyy-mm-dd&apos;) czysj from cw_rjzb where acc=&apos;&quot;+thisaccid+&quot;&apos; and lsh=&apos;&quot;+AAE076+&quot;&apos;&quot;;
				var rjzds = db.QuerySQL(sql);
				var zdr = czyxm;
				var czysj = db.GetSQL(&quot;select to_char(sysdate,&apos;yyyy-mm-dd&apos;) from dual&quot;);
				if (rjzds.getRowCount() &gt; 0) {
					zdr = rjzds.getStringAt(0,&quot;CZYXM&quot;);
					czysj = rjzds.getStringAt(0,&quot;CZYSJ&quot;);
				}
				var yhbz = (cgbz == &quot;0&quot; ? &quot;11&quot; : &quot;12&quot;); //支付标志（11:支付成功，12：支付失败）
				//notify(jobseqid,30,&quot;正在导入银行回盘文件...&quot;,&quot;run&quot;);//名称为空表示只要更新进度
				var jkmsg = uo_ywjk.yhkpReturn(db,thisorgid,thisaccid,BAZ610,AAA027,yhbz,BAZ902,AAE076,zdr,czysj,BAE415,BAC004,BAC004,BAE036,ywjkbz);
				var retmsg = jkmsg.split(&quot;@&quot;);
				//notify(jobseqid,33,&quot;正在导入银行回盘文件...&quot;+retmsg ,&quot;run&quot;);//名称为空表示只要更新进度

				//回盘不成功的记录，写入日记帐对冲原来的入帐日记 （一笔出不成功就记一笔日记帐）
				if (cgbz != &quot;0&quot;) {
					failecnt ++;

					if (retmsg.length() != 3) { // && retmsg[0].length() != 20
						db.Rollback();
						notify(jobseqid,100,&quot;调用接口(uo_ywjk.yhkpReturn)出错！接口返回：&quot;+jkmsg,&quot;end&quot;);//名称为空表示只要更新进度
						//throw new Exception(&quot;调用接口出错！接口返回：&quot;+jkmsg);
						return &quot;调用接口(uo_ywjk.yhkpReturn)出错！接口返回：&quot;+jkmsg;
					}

					notify(jobseqid,process,&quot;回盘不成功的记录...BAZ610=&quot;+BAZ610+&quot; 接口返回:&quot;+retmsg ,&quot;run&quot;);//名称为空表示只要更新进度
					if (retmsg[0].indexOf(&quot;null&quot;) &gt; -1) {
						notify(jobseqid,100,&quot;回盘失败！调用接口出错！业务内部序号:&quot;+BAZ610,&quot;error&quot;);//名称为空表示只要更新进度
						throw new Exception(&quot;回盘失败！调用接口出错！业务内部序号:&quot;+BAZ610);
					}

					sql = &quot;select * from v_si_djb_tmp where acc=&apos;&quot;+thisaccid+&quot;&apos; and xh=&apos;&quot;+retmsg[0]+&quot;&apos;&quot;;
					var sids = db.QuerySQL(sql);
					if (sids.getRowCount() &gt; 0) {
						var rq = sids.getStringAt(0,&quot;RQ&quot;);
						var yy = rq.substring(0,4); //20151224
						var mm = rq.substring(4,6);
						var dd = rq.substring(6,8);
						var kmbh = sids.getStringAt(0,&quot;KMBH&quot;);
						var sbh = sids.getStringAt(0,&quot;SBH&quot;);	
						var zth = sids.getStringAt(0,&quot;ZTH&quot;);	
						var jefx = &quot;借&quot;;

						//判断是否已存过日记帐
						var djblsh = sids.getStringAt(0,&quot;LSH&quot;);	
						sql = &quot;select * from cw_rjzb where acc=&apos;&quot;+thisaccid+&quot;&apos; and lsh=&apos;&quot;+djblsh+&quot;&apos;&quot;;
						var cnt = db.GetSQLRowCount(sql);
						if (cnt == 0) {							
							//保存进日记帐表cw_rjzb			
							var lsh = db.GetSQL(&quot;select seq_rjz_lsh.nextval from dual&quot;); //流水号
							sql = &quot;select nvl(max(sxh),-1)+1 from cw_rjzb where acc=&apos;&quot;+thisaccid+&quot;&apos; and yy=&apos;&quot;+yy+&quot;&apos; and mm=&apos;&quot;+mm+&quot;&apos; and kmbh=&apos;&quot;+kmbh+&quot;&apos;&quot;;
							var sxh = db.GetSQL(sql); //顺序号
							var jsfs = &quot;转帐&quot;;
							sql = &quot;insert into cw_rjzb(sbh,zth,org,acc,lsh,yy,mm,dd,sxh,czyxm,old_pch,zy,kmbh,lxbh,dwbh,dwmc,je,jefx,jsfs,yspz_fjzs,yspz_fjpch,yspz_lrrq,czysj)
								select sbh,zth,org,acc,&apos;&quot;+lsh+&quot;&apos; lsh,&apos;&quot;+yy+&quot;&apos; yy,&apos;&quot;+mm+&quot;&apos; mm,&apos;&quot;+dd+&quot;&apos; dd,&apos;&quot;+sxh+&quot;&apos; sxh,zbr,pch,zy,kmbh,lxbh,dwbh,dwmc,
								(0-(je1+je2+je3+je4+je5+je6+je7+je8+je9+je10+je11+je12+je13+je14+je15)) je,&apos;&quot;+jefx+&quot;&apos; jefx,&apos;转帐&apos; jsfs,&apos;&apos; yspz_fjzs,&apos;&apos; yspz_fjpch,null yspz_lrrq,sysdate
								from v_si_djb_tmp where acc=&apos;&quot;+thisaccid+&quot;&apos; and xh=&apos;&quot;+retmsg[0]+&quot;&apos;&quot;;
							db.ExcecutSQL(sql);	

							//保存进日记帐明细表cw_rjzmxb
							var mxxh = 0;
							for (var k=1;k&lt;=15;k++) {
								var lxbh = sids.getStringAt(0,&quot;LXBH&quot;);	
								var je = sids.getStringAt(0,&quot;JE&quot;+k);	
								var lxxh = k;				
								if (je != &quot;0&quot; && je != &quot;0.00&quot; && je != &quot;&quot;) {
									mxxh ++;
									var ym1 = sids.getStringAt(0,&quot;YM1&quot;);
									var ym2 = sids.getStringAt(0,&quot;YM2&quot;);
									sql = &quot;insert into cw_rjzmxb(sbh,zth,org,acc,lsh,mxxh,kmbh,lxbh,lxxh,ym1,ym2,je,jefx)values(&apos;&quot;+sbh+&quot;&apos;,&apos;&quot;+zth+&quot;&apos;,&apos;&quot;+thisorgid+&quot;&apos;,&apos;&quot;+thisaccid+&quot;&apos;,&apos;&quot;+lsh+&quot;&apos;,
											&apos;&quot;+mxxh+&quot;&apos;,&apos;&apos;,&apos;&quot;+lxbh+&quot;&apos;,&apos;&quot;+lxxh+&quot;&apos;,&apos;&quot;+ym1+&quot;&apos;,&apos;&quot;+ym2+&quot;&apos;,0 - &quot;+je+&quot;,&apos;&quot;+jefx+&quot;&apos;)&quot;;
									db.ExcecutSQL(sql);
								}
							}

							//更新SI_DJB_TMP
							sql = &quot;update v_si_djb_tmp set lsh=&apos;&quot;+lsh+&quot;&apos;,RZBZ=&apos;1&apos;,RZR=&apos;&quot;+czyxm+&quot;&apos;,RZSJ=sysdate 
								where acc=&apos;&quot;+thisaccid+&quot;&apos; and xh=&apos;&quot;+retmsg[0]+&quot;&apos;&quot;;
							db.ExcecutSQL(sql);

							sql = &quot;update ac95 set prc_out_xh=&apos;&quot;+retmsg[0]+&quot;&apos;,prc_out_code=&apos;&quot;+retmsg[1]+&quot;&apos;,prc_out_text=&apos;&quot;+retmsg[2]+&quot;&apos;,err_cwlsh=&apos;&quot;+lsh+&quot;&apos; where BAZ610=&apos;&quot;+BAZ610+&quot;&apos;&quot;;
							db.ExcecutSQL(sql);

						}
						else {
							db.Rollback();
							notify(jobseqid,100,&quot;没有找到业务冲帐记录！（序号:&quot;+retmsg[0]+&quot;）&quot;,&quot;end&quot;);//名称为空表示只要更新进度
							return &quot;没有找到业务冲帐记录！（序号:&quot;+retmsg[0]+&quot;）&quot;;
						}
					}
				}//END回盘不成功的记录
				else {
//					sql = &quot;update ac95 set prc_out_xh=&apos;&quot;+retmsg[0]+&quot;&apos;,prc_out_code=&apos;&quot;+retmsg[1]+&quot;&apos; where BAZ610=&apos;&quot;+BAZ610+&quot;&apos;&quot;;
//					db.ExcecutSQL(sql);
				}

			} //end tmpds.getRowCount()

		} //end while

		var lognote = &quot;进程ID：&quot;+jobseqid+&quot; 回盘文件：&quot;+filename+&quot; 总笔数：&quot;+rowcnt+&quot; 失败笔数：&quot;+failecnt+&quot;。&quot;;
		sql = &quot;insert into oplog(grdid,type,action,opdat,opusr,opusrnam,note,acc,syt,org)
			values(&apos;RJZ_YWKP&apos;,&apos;发放回盘&apos;,&apos;导入&apos;,sysdate,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;)&quot;.format([usrid,czyxm,lognote,thisaccid,&quot;SBCW&quot;,thisorgid]);
		db.ExcecutSQL(sql);

		db.Commit();

		//return jkret;
		if (notify(jobseqid,100,&quot;银行回盘导入成功！\n回盘文件：&quot;+filename+&quot;\n总笔数：&quot;+rowcnt+&quot;\n失败笔数：&quot;+failecnt+&quot;。&quot;,&quot;end&quot;)==&quot;ERROR&quot;) throw new Exception ( &quot;进程已经中断&quot; );
		if (notify(jobseqid,100,&quot;操作完成&quot;,&quot;end&quot;)==&quot;ERROR&quot;) throw new Exception ( &quot;进程已经中断&quot; );

		return &quot;银行回盘导入成功！\n回盘文件：&quot;+filename+&quot;\n总笔数：&quot;+rowcnt+&quot;\n失败笔数：&quot;+failecnt+&quot;。&quot;;
	}
	catch (e) {
		notify(jobseqid,100,e.toString()+&quot;  出错行=&quot; + rowcnt,&quot;error&quot;);
		if (db != null) db.Rollback();
		return e.toString() + &quot;  出错行=&quot; + rowcnt;
	}
	finally {
		if (db != null) db.Close();
	}
}

//下载拷盘文件保存到本地P目录
function downLoadFile()
{
	var path = &quot;/u/dsdf/&quot;+mmdir+&quot;/&quot;;
	var txt = pub.EAFunc.readFile(path+filename);
	return txt;
}
//作为.sp服务时的入口
//预定义变量：request,response
function Response()
{
	var itemclass = request.getParameter(&quot;filename&quot;);
  	response.setHeader(&quot;File&quot;,filename);
        response.setHeader(&quot;Content-Disposition&quot;, &quot;attachment;filename=&quot;+filename);
	var path = &quot;/u/dsdf/&quot;+mmdir;
	var txt = pub.EAFunc.readFile(path+filename);
	txt = pub.EAFunc.Replace(txt,&quot;\n&quot;,&quot;\r\n&quot;);
	pub.EAZip.compressStringToStream(txt,response.getOutputStream());
}

function getSysDate()
{
	var dt = pub.EADbTool.GetSQL(&quot;select to_char(sysdate,&apos;yyyymmddhh24miss&apos;) from dual&quot;);
	return dt;
}


//回盘加入进度条提示实现 
function genbatch()
{
	var db = null;
	var jobseqid  = &quot;&quot;;
	try {
		db = new pub.EADatabase();	// 如果连接到其他数据库, new pubpack.EADatabase(“dbname”)
		jobseqid = &quot;YHHP&quot;+jobid;

		var tim = new timepack.EARunOSTimer(); 
		tim.init(jobseqid,jobnam,&quot;SBCW&quot;,&quot;RJZ_YWKP&quot;,&quot;yhhpImport&quot;,&quot;jobid=&quot;+jobid+&quot;&thisorgid=&quot;+thisorgid+&quot;&jobseqid=&quot;+jobseqid 
			+&quot;&thisaccid=&quot;+thisaccid+&quot;&typ=&quot;+typ+&quot;&ftpinfo=&quot;+ftpinfo+&quot;&hpdat=&quot;+hpdat
			+&quot;&usrid=&quot;+usrid+&quot;&czyxm=&quot;+czyxm+&quot;&hpfilename=&quot;+hpfilename+&quot;&text=&quot;+text);
	}
	catch ( ee ) {
		throw new pub.EAException ( ee.toString() );
	}
	finally {
		if (db!=null) db.Close();
	}
	return jobseqid  ;// 返回后台分配的作业编号

}
// 通知外部当前的运行情况
function notify(jobseqid,percent,note,stat)
{
	var db = null;
	if ( percent &lt; 0 ) return &quot;&quot;;
	try{
		db = new pub.EADatabase();
		note = pub.EAFunc.Replace( note, &quot;&apos;&quot;,&quot;‘&quot; );
		if(note==&quot;&quot;){
			db.ExcecutSQL(&quot;update RunOSTimer set percent=&quot;+(percent) +&quot;,stat=&apos;&quot;+stat+&quot;&apos; where id=&apos;&quot;+jobseqid+&quot;&apos;&quot;);
		}
		else {
			db.ExcecutSQL(&quot;update RunOSTimer set percent=&quot;+(percent) +&quot;,percentnote=&apos;&quot;+note+&quot;&apos;,stat=&apos;&quot;+stat+&quot;&apos; where id=&apos;&quot;+jobseqid+&quot;&apos;&quot;);
			db.ExcecutSQL(&quot;insert into RunOSTimerDTL(id,name ) values(&apos;&quot;+jobseqid+&quot;&apos;,&apos;&quot;+note+&quot;&apos;)&quot; );
		}
		db.Commit();
	}
	catch ( e ) {
		//pubpack.EAFunc.Log( e.toString() );
		db.Rollback();
		return &quot;ERROR&quot; ;
	}
	finally {
		if (db!=null) db.Close();
	}
	return &quot;OK&quot;;
}

ROW.DATDSC:
select rownum,a.* from (
select AAC001,AAC003,AAB001,AAE135,AIC263,AAE010,AAE007,BIC006,AAE002Min,AAE002Max,months,
       AAE208,BAE074,AAE076,BAZ901,BAZ902,BAC003
from (
select   AAC001,AAC003,AAB001,AAE135,AIC263,AAE010,AAE007,BIC006,
  min(substr(AAE002,0,6)) AAE002Min,max(substr(AAE002,8,13)) AAE002Max,
  nvl(max(substr(AAE002,8,13)),0)-nvl(min(substr(AAE002,0,6)),0)+1 months,
  AAE208,BAE074,AAE076,BAZ901,
  nvl(a.BAZ902,(select aaa103 from aa10 b where a.BAZ901=b.aaa102 and b.aaa100=&apos;BAZ901&apos; )) BAZ902,
  BAC003,BAE415,BAE419
from ac95 a
where AAE208=&apos;[%SFNY]&apos;
  and BAE074=&apos;[%DJH]&apos;
  and aae140=&apos;[%XZLX]&apos;
  and BAE419=&apos;[%DFYHBM]&apos; 
  --and nvl(BAZ901,&apos;%&apos;) like &apos;[%THM]%&apos;
  [%FILTERSTR] 
group by AAC001,AAC003,AAB001,AAE135,AIC263,AAE010,AAE007,BIC006,
  AAE208,BAE074,AAE076,BAZ901,BAZ902,BAC003,BAE415,BAE419
) 
order by BAE415,BAE419,AAB001,AAC001
) a
